INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('이중 하나는 거짓말', '한국문학의 결정적 순간을 만들어온 젊은 거장 김애란의 장편소설. 몇 년 전 진행된 한 인터뷰에서 차기작에 대해 묻는 질문에 작가가 “빛과 거짓말 그리고 그림에 관한 이야기”라고 설명한 것 외에는 모든 것이 베일에 가려져 있던 바로 그 작품이 오랜 기다림 끝에 마침내 공개된다.', '김애란 (지은이)', '문학동네', 'https://image.aladin.co.kr/product/34474/76/cover200/k292932634_3.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이후 한국소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('괴수 8호 13 (트리플 특장판) - 초판한정 일러스트 카드 + 한국한정 양면 일러스트 커버 + 한정 아크릴 포토 카드', '타치카와에 발생한 괴수 9호의  수중에 떨어진 아시로 미나. 미나를 구출하러 가려는 카프카를 도쿄 곳곳에서 나타난 대괴수들이 가로막는다.', '마츠모토 나오야 (지은이), 유유리 (옮긴이)', '서울미디어코믹스(서울문화사)', 'https://image.aladin.co.kr/product/34573/89/cover200/k382933360_1.jpg', '국내도서>만화>소년만화>액션', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('영원한 천국', '출간된 작품마다 독자들을 ‘정유정 월드’로 이끌어온 소설가 정유정의 신작이 출간된다. 《28》에서는 전염병을, 《진이, 지니》에서는 호미노이드를 다루며, 세계의 변화를 선험적으로 감지하여 그 안에서 가장 인간다운 것이 무엇인지 탐구해온 작가의 이번 신작은 《완전한 행복》에 이은 욕망 3부작의 두 번째 책, 《영원한 천국》이다.', '정유정 (지은이)', '은행나무', 'https://image.aladin.co.kr/product/34499/81/cover200/k712932751_1.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이후 한국소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('더 좋은 문장을 쓰고 싶은 당신을 위한 필사책', '《요즘 어른을 위한 최소한의 문해력》, 《요즘 어른을 위한 최소한의 맞춤법》 등 10만 독자가 선택한 작가 이주윤이 그만의 필사 글쓰기 노하우를 담은 《더 좋은 문장을 쓰고 싶은 당신을 위한 필사책》을 출간했다.', '이주윤 (지은이)', '빅피시', 'https://image.aladin.co.kr/product/34390/31/cover200/k112932504_1.jpg', '국내도서>인문학>책읽기/글쓰기>글쓰기', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('만화로 보는 3분 철학 1 : 서양 고대 철학편', '지금까지 이런 철학 만화는 없었다! 철학의 기초 개념을 잡는 가장 쉬운 책이다. 만화로 3분이면 완벽하게 개념 잡는 서양철학사를 담았다.', '김재훈, 서정욱 (지은이)', '카시오페아', 'https://image.aladin.co.kr/product/27434/96/cover200/k092733374_1.jpg', '국내도서>인문학>철학 일반>교양 철학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('푸른 들판을 걷다', '초역작 『맡겨진 소녀』와 대표작 『이처럼 사소한 것들』로 독서가들의 열렬한 사랑을 받은 아일랜드 소설가 클레어 키건의 신간이 다산책방에서 출간되었다. 『푸른 들판을 걷다』는 국내에 세 번째로 소개하는 작가의 작품이자, 처음으로 선보이는 소설집이다.', '클레어 키건 (지은이), 허진 (옮긴이)', '다산책방', 'https://image.aladin.co.kr/product/34499/68/cover200/k502932751_1.jpg', '국내도서>소설/시/희곡>세계의 소설>아일랜드소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('찬란한 멸종 - 거꾸로 읽는 유쾌한 지구의 역사', 'JTBC &lt;차이나는 클라스&gt;, tvN &lt;어쩌다 어른&gt; 등의 방송과 유튜브 채널에서 ‘지구의 역사’와 ‘인류 대멸종’을 주제로 강의해 누적 조회 수 1100만 회 이상을 기록하며 화제를 모았던 이정모 저자가 『찬란한 멸종』을 통해 멸종으로 보는 46억 년 지구의 역사를 본격적으로 스토리텔링한다.
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('데뷔 못 하면 죽는 병 걸림 3부 초판 한정 굿즈박스 세트 - 전4권', '4년차 공시생, 낯선 몸에 빙의해 3년 전으로 돌아왔다. 그리고 그의 눈앞에 나타난 갑작스러운 상태창의 협박. 돌연사 위협 때문에 팔자에도 없던 아이돌에 도전하게 된 주인공의 대환장 일지.', '백덕수 (지은이)', '위시북스(KW북스)', 'https://image.aladin.co.kr/product/34553/35/cover200/k272933168_1.jpg', '국내도서>소설/시/희곡>판타지/환상문학>한국판타지/환상소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('김켈리의 신비마트 1', '신비마트의 시작은 아이들의 고민이었다. 이성 친구, 공부, 용돈 등 평범한 학교생활을 하는 아이들도 고민이 참 많다. 이런 아이들의 평범한 고민을 마법 같은 일로 한 번에 해결할 수는 없을까? 그런 고민을 담아낸 이야기가 바로 『김켈리의 신비마트』다.', '이세경 (지은이), 김켈리 (원작), 박상빈 (그림), 박제련 (채색)', '베가북스', 'https://image.aladin.co.kr/product/34472/2/cover200/k362932639_1.jpg', '국내도서>어린이>초등 전학년>학습만화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('고전이 답했다 마땅히 살아야 할 삶에 대하여', '매일 아침 7만 명이 유튜브 강의를 찾아 듣고, 한 달에 20여 차례 전국의 강연장을 찾아가 독자들을 만나는 이 시대 최고의 강연자. 30만 독자가 기다린 베스트셀러 저자 고명환이 지난 10여 년간의 인생 내공이 응축된 책, 『고전이 답했다 마땅히 살아야 할 삶에 대하여』에서 고전(古典)에서 길어 올린 흔들림 없는 삶의 내공을 전한다.', '고명환 (지은이)', '라곰', 'https://image.aladin.co.kr/product/34526/60/cover200/k622932853_2.jpg', '국내도서>자기계발>성공>성공학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('몽골제국 연대기', '중앙유라시아 역사 분야에서 독보적인 업적을 인정받는 서울대학교 김호동 교수가 세계 최초의 세계사 『집사』의 세계 최초 축약본인 『몽골제국 연대기』를 완성했다. 13세기에 몽골 기마군단이 말을 달리기 시작하자 동서양은 다시는 이전으로 돌아갈 수 없을 만큼 가까워졌다. 흔히 ‘최초의 세계사’라고 일컫는 『집사』는 그 결합과 연결의 결과물이다.', '라시드 앗 딘 (지은이), 김호동 (옮긴이)', '사계절', 'https://image.aladin.co.kr/product/34331/51/cover200/k402932687_2.jpg', '국내도서>역사>아시아사>중앙아시아사', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('너에게 들려주는 단단한 말', '청소년을 위한 인생철학 에세이다. ‘나’라는 존재와 친구와의 관계, 공부와 성적, 꿈과 진로 등에 관한 고민이 커지는 청소년기는 인생이란 여정에서 어둡고 막막한 터널을 처음으로 마주하는 시기다. 어떤 생각을 키우고, 어떤 마음을 갖느냐에 따라 앞으로 펼쳐질 삶의 모양이 달라질 수도 있기에 저자는 단어 하나, 문장 하나에도 온 마음을 담았다.', '김종원 (지은이)', '퍼스트펭귄', 'https://image.aladin.co.kr/product/34233/47/cover200/k212931234_2.jpg', '국내도서>청소년>청소년 인문/사회', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('사피엔스 : 그래픽 히스토리 Vol.1 - 인류의 탄생', '《사피엔스: 그래픽 히스토리》 시리즈는 교양 논픽션인 원작의 핵심이 기발한 각색과 세련된 그림을 통해 흡인력 강한 스토리텔링으로 재탄생한 그래픽노블의 걸작이다. 그중 첫 권은 원작의 ‘1부 인지혁명’을 다룬다.', '유발 하라리 (원작), 다니엘 카사나브 (그림), 김명주 (옮긴이), 다비드 반데르묄렝 (각색)', '김영사', 'https://image.aladin.co.kr/product/25686/5/cover200/8934991321_1.jpg', '국내도서>인문학>교양 인문학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('불안 세대 - 디지털 세계는 우리 아이들을 어떻게 병들게 하는가', '세계적인 사회심리학자 조너선 하이트의 신작 『불안 세대』가 전 세계를 뜨겁게 달구고 있다. 베스트셀러 『바른 마음』으로 ‘영미권에서 가장 논쟁적인 학자’으로 부상한 그는 스마트폰과 소셜 미디어, 인터넷이 청소년 정신 건강 위기를 초래하고 있다는 충격적인 주장을 내놓는다.', '조너선 하이트 (지은이), 이충호 (옮긴이)', '웅진지식하우스', 'https://image.aladin.co.kr/product/34372/90/cover200/8901285894_1.jpg', '국내도서>인문학>교양 인문학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('카구라바치 1 (더블특전판) - 초판한정 일러스트 시네마 티켓 + 선착순 더블 특전 한국 오리지널 작가 한글 자필 코멘터리 카드, 해야만 하는 일', '도장(刀匠)인 아버지를 둔 소년 치히로는, 아버지 밑에서 도장이 되기 위한 수련에 여념이 없는 매일을 보내고 있었다. 실없는 아버지와 과묵한 아들. 웃음이 끊이지 않는 일상이 언제까지고 계속될 줄 알았지만… 어느 날 비극이 들이닥친다…', '호카조노 타케루 (지은이)', '대원씨아이(만화)', 'https://image.aladin.co.kr/product/34526/35/cover200/k122932853_1.jpg', '국내도서>만화>소년만화>액션', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('하루 한 장 나의 어휘력을 위한 필사 노트 - 할 말은 많지만 쓸 만한 말이 없는 어른들을 위한 숨은 어휘력 찾기', '《어른의 어휘력》, 《감정 어휘》 어휘력 도서 최다 판매 유선경 작가의 첫 필사책. “오늘부터 하루 한 장, 나의 어휘력을 위한 필사를 시작합니다”', '유선경 (지은이)', '위즈덤하우스', 'https://image.aladin.co.kr/product/33648/90/cover200/k172939414_1.jpg', '국내도서>인문학>교양 인문학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('푸른 사자 와니니 7 - 인간의 길에서', '‘푸른 사자 와니니’ 시리즈는 아프리카 초원을 배경으로 암사자 와니니가 친구들과 위기를 극복하고 무리를 이루는 과정을 흥미진진하게 그려 내며 후속권이 출간될 때마다 독자들의 열렬한 응원을 받고 있다.', '이현 (지은이), 오윤화 (그림)', '창비', 'https://image.aladin.co.kr/product/34502/44/cover200/8936443364_1.jpg', '국내도서>어린이>동화/명작/고전>국내창작동화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('ETS 토익 정기시험 기출문제집 1000 Vol. 4 Reading (리딩) - 토익 기출문제 독점출간/ ALL NEW 최신기출 10회 수록/ 문제집+해설집+무료 동영상 강의+기출어휘 단어장(PDF)+APP 모바일 학습', '정기시험 최신 기출문제 10세트가 수록되어 있다. 시험에 나온 최신 기출문제로 실전 감각을 키워 시험에 확실하게 대비할 수 있다. 교재 내 QR코드는 해당 회차의 무료 동영상 강의로 연결된다. 오답률 높은 고난도 문항을 친절한 해설 강의와 함께 수록하였다.', 'ETS (엮은이)', '(주)YBM(와이비엠)', 'https://image.aladin.co.kr/product/33010/94/cover200/8917239501_1.jpg', '국내도서>외국어>토익>Reading', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('헬로 뷰티풀', '가족을 둘러싼 30년 간의 사랑과 슬픔, 관용과 화해를 그린 이 소설은 사랑이 우리를 구원할 수 있는지, 또한 온전한 자신으로 산다는 것은 무엇인지를 묻는다. 앤 나폴리타노는 끈끈한 관계에 깃든 고통과 기쁨, 그리고 삶의 어찌할 수 없는 비애를 부드럽게 직시하면서 인물들 하나하나의 운명에 깊이 공감하게 만든다.', '앤 나폴리타노 (지은이), 허진 (옮긴이)', '복복서가', 'https://image.aladin.co.kr/product/34499/86/cover200/k872932751_3.jpg', '국내도서>소설/시/희곡>영미소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('당신이 누군가를 죽였다', '일본 최고의 베스트셀러 작가 히가시노 게이고의 장편소설『당신이 누군가를 죽였다』가 출간되었다. 작품은 장르문학계의 거장인 작가가 101번째 작품을 맞아 추리소설의 원점으로 돌아가 ‘황금시대 미스터리’의 매력을 유감없이 발휘한 걸작으로 평단과 독자의 호평을 받고 있다.', '히가시노 게이고 (지은이), 최고은 (옮긴이)', '북다', 'https://image.aladin.co.kr/product/34292/25/cover200/k692932177_1.jpg', '국내도서>소설/시/희곡>추리/미스터리소설>일본 추리/미스터리소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('저속노화 식사법 - 노년내과 의사가 알려주는 기적의 식단 혁명', '일명 ‘저속노화 선생님’, 서울아산병원 노년내과 의사 정희원의 『저속노화 식사법』이 테이스트북스에서 출간되었다. 만성질환을 예방하는 지중해식 식단과 고혈압을 예방하는 대시(DASH) 식단 요소의 장점만을 합친 MIND 식사법을 저속노화 식사로 제안한다.', '정희원 (지은이)', '테이스트북스', 'https://image.aladin.co.kr/product/34331/64/cover200/k332932798_1.jpg', '국내도서>건강/취미>건강정보>음식과 건강', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('ETS 토익 정기시험 기출문제집 1000 Vol. 4 Listening (리스닝) - 토익 기출문제 독점출간/ ALL NEW 최신기출 10회 수록/ 문제집+해설집+무료 정기시험 성우 MP3+무료 동영상 강의+기출어휘 단어장(PDF)+APP 모바일 학습', '정기시험 최신 기출문제 10세트가 수록되어 있다. 시험에 나온 기출문제로 실전 감각을 키워 시험에 확실하게 대비하자! 수록된 10세트의 LC 음원은 모두 정기시험 성우의 음원이다. 시험장에서 듣게 될 음성으로 공부하면 까다로운 영국식, 호주식 발음도 걱정 없다.', 'ETS (엮은이)', '(주)YBM(와이비엠)', 'https://image.aladin.co.kr/product/33010/92/cover200/8917239498_1.jpg', '국내도서>외국어>토익>Listening', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('김재우의 구동사 100 - 100일간의 구동사 마스터 클래스', '국내파이지만 원어민도 놀라는 실력자인 김재우 선생님이 20년 영어 교육 노하우를 바탕으로 혼신의 힘을 다해 만든 책이다. 특히 ‘원어민은 입에 달고 살지만 한국인의 입에서는 나오지 않는’ 구동사를 일목요연하게 정리하면서도 총망라했다.', '김재우 (지은이)', '상상스퀘어', 'https://image.aladin.co.kr/product/34526/22/cover200/k162932852_1.jpg', '국내도서>외국어>영어회화>생활영어', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('모순 - 개정판', '작가 양귀자가 1998년 펴낸 세 번째 장편소설로, 책이 나온 지 한 달 만에 무서운 속도로 베스트셀러 1위에 진입, 출판계를 놀라게 하고 그해 최고의 베스트셀러로 자리 잡으면서 ‘양귀자 소설의 힘’을 다시 한 번 유감없이 보여준 소설이다.', '양귀자 (지은이)', '쓰다', 'https://image.aladin.co.kr/product/2584/37/cover200/8998441012_3.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이전 한국소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('정확한 사랑의 실험 (10주년 기념 특별판, 양장)', '섬세한 해석과 정교한 글쓰기로 신뢰받고 있는 문학평론가 신형철의 『정확한 사랑의 실험』이 출간 10주년을 맞이했다. ‘사랑의 논리’ ‘욕망의 병리’ ‘윤리와 사회’ ‘성장과 의미’라는, 비평가로서의 주요 관심사 아래 총 27편의 영화에 관해 쓴 글 22편을 엮은 책은 10년간 꾸준히 읽히며 사랑받아왔다. 10주년 기념 특별판 표지는 김마리(퍼머넌트 잉크) 디자이너가 함께했다.', '신형철 (지은이)', '마음산책', 'https://image.aladin.co.kr/product/34468/92/cover200/8960908932_1.jpg', '국내도서>에세이>명사에세이>기타 명사에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('가연물', '요네자와 호노부의 최신작 《가연물》이 드디어 국내에 소개된다. 2023년 7월에 발간된 《가연물》은 주요 미스터리 랭킹에서 《야경》, 《왕과 서커스》, 《흑뢰성》에 이어 작가 통산 네 번째 3관왕을 달성했다.', '요네자와 호노부 (지은이), 김선영 (옮긴이)', '리드비', 'https://image.aladin.co.kr/product/34545/14/cover200/k492933965_1.jpg', '국내도서>소설/시/희곡>추리/미스터리소설>일본 추리/미스터리소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('백설공주에게 죽음을 (특별판)', '전 세계에 독일 미스터리를 각인시킨 작가 넬레 노이하우스의 대표작이자 유럽 최고 인기 시리즈인 ''타우누스 시리즈''의 네 번째 작품이다. 이 책은 2010년 출간 사흘 만에 베스트셀러 순위에 올라 무려 32주 동안 1위를 지키는 기염을 토했다.', '넬레 노이하우스 (지은이), 김진아 (옮긴이)', '북로드', 'https://image.aladin.co.kr/product/33976/65/cover200/k622930548_2.jpg', '국내도서>소설/시/희곡>추리/미스터리소설>기타국가 추리/미스터리소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('별의 커비 푸푸푸 히어로 5 - 잘 있어! 할캔드라!', '마버로아와 함께 할캔드라에 도착한 커비와 친구들. 랜디아의 기습 공격이 이어지자, 커비와 친구들이 힘을 합쳐 랜디아를 무찔렀다. 그런데, 랜디아가 쓰고 있던 마스터 크라운을 거머쥔 마버로아가 지금껏 숨겨왔던 본색을 드러내는데….', '아오키 케이, 미카마루 (지은이), 김지영 (옮긴이)', '다산어린이', 'https://image.aladin.co.kr/product/34526/21/cover200/k902932852_2.jpg', '국내도서>어린이>게임 만화/캐릭터도감', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('불변의 법칙 - 절대 변하지 않는 것들에 대한 23가지 이야기', '《돈의 심리학》의 저자 모건 하우절이 3년 만에 세상에 내놓은 신작.‘절대 변하지 않는 것들에 대한 23가지 이야기’를 전한다. 이번 책은 돈과 투자 영역은 물론이고, 인간의 본성과 세상의 이치에 관한 이야기를 두루 다루어 한층 더 다층적이고 복합적인 메시지를 담아냈다는 평가를 받는다.', '모건 하우절 (지은이), 이수경 (옮긴이)', '서삼독', 'https://image.aladin.co.kr/product/33406/14/cover200/k272938139_1.jpg', '국내도서>경제경영>경제학/경제일반>경제이야기', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('디자인 구구단 - 디자인이 필요한 순간, ''툭'' 튀어나오는 디자인 공식', '총 9개의 이론(비율, 공간, 균형, 색의 속성, 질감, 형과 형태, 움직임, 율동, 서체)을 통해 디자인이 필요한 모든 분이 디자인에 대한 이해를 높이고 감각을 키울 수 있도록 안내한다. 12년간 실무에서 감각을 키우고 3년간 직접 교육을 하면서 누구나 쉽게 감각을 키울 수 있는 방법을 연구해 만든 트레이닝 북이다.', '에이핫 (지은이)', '길벗', 'https://image.aladin.co.kr/product/34360/88/cover200/k182932901_1.jpg', '국내도서>컴퓨터/모바일>그래픽/멀티미디어>그래픽 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('김대중 육성 회고록 (양장) - 김대중은 오늘 우리에게 무엇을 말하는가', '김대중 대통령의 탄생 100주년, 서거 15주기를 맞아 그의 육성으로 작성된 마지막 자서전 『김대중 육성 회고록』을 출간한다. 1924년 신안 하의도에서 출생했을 때부터 2009년 서거하기 전까지 김대중 대통령의 생애와 사상을 생생한 육성으로 들을 수 있다.', '연세대학교 김대중도서관 (지은이)', '한길사', 'https://image.aladin.co.kr/product/34447/79/cover200/8935678805_1.jpg', '국내도서>사회과학>정치학/외교학/행정학>정치인', 1, 1);
