INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('하늘과 땅 식료품점', '아프리카계 흑인 아버지와 유대인 백인 어머니 사이에 태어난 제임스 맥브라이드가 자기 뿌리와 경험을 바탕으로 집필한 소설로, 실존하는 펜실베이니아 포츠타운에 ‘치킨힐’이라는 가상의 마을을 세워 우리를 이렇게 또 다른 세계로 안내하고자 한다.', '제임스 맥브라이드 (지은이), 박지민 (옮긴이)', '미래지향', 'https://image.aladin.co.kr/product/34455/51/cover200/k712932432_2.jpg', '국내도서>소설/시/희곡>영미소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('죽이고 싶은 아이 2', '한국 청소년 문학의 역사를 새로 쓴 이꽃님의 『죽이고 싶은 아이』, 그 두 번째 이야기가 지금 우리에게 도착했다. 압도적인 몰입감과 휘몰아치는 전개로 수십만 독자를 단숨에 사로잡은 『죽이고 싶은 아이』의 명성을 고스란히 잇는, 기대 그 이상의 완벽한 속편의 모습으로.', '이꽃님 (지은이)', '우리학교', 'https://image.aladin.co.kr/product/34179/34/cover200/k122931213_1.jpg', '국내도서>청소년>청소년 문학>청소년 소설', 1, 1);
