INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('흐릿한 나를 견디는 법 - 도망치는 건 새로운 세계를 발견하는 일일 테니', '《무명의 감정들》로 깊은 위로를 선사했던 쑥의 두 번째 에세이. 전작에서는 무명의 시간들을 담담히 그려내며 위로를 던졌다면, 이번 책에서는 좀 더 유쾌하게 나를 만들어가는 무명을 만날 수 있다.', '쑥 (지은이)', '빅피시', 'https://image.aladin.co.kr/product/34530/97/cover200/k662932953_1.jpg', '국내도서>에세이>사진/그림 에세이', 1, 1);
