INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('고요한 읽기', '작가 인생 43년, 소설쓰기로 인생에 복무하는 작가 이승우. 그의 작품세계를 대표하는 두 개의 기둥인 ‘종교적 실존’과 ‘문학적 실존’ 위에 지은 집 같은 산문집을 펴낸다.', '이승우 (지은이)', '문학동네', 'https://image.aladin.co.kr/product/34555/2/cover200/k042933162_2.jpg', '국내도서>에세이>한국에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('히스토리에 Historie 12', '에우리디케를 살해하려 했다는 사실이 발각되어 귀양길에 오른 마케도니아의 왕비, 올림피아스. 몰로시아로 향하던 도중 그녀는 왕, 필리포스가 보낸 자객의 습격을 맞닥뜨린다. 얄궂게도 그녀를 노린 자객 중에는 알렉산드로스를 닮은 파우사니아스라는 남자가 있었다.', '이와아키 히토시 (지은이), 오경화 (옮긴이)', '서울미디어코믹스(서울문화사)', 'https://image.aladin.co.kr/product/34548/27/cover200/k612933061_1.jpg', '국내도서>만화>대하/서사만화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('설민석의 한국사 대모험 30 - 공민왕 편 : 세상에서 가장 슬픈 이야기', '공민왕과 노국대장공주의 사랑 이야기를 재미있는 만화로 담아냈다. 고려 왕자였던 공민왕과 원나라 공주였던 노국대장공주가 어떻게 결혼하게 됐는지, 두 사람이 서로를 얼마나 아끼고 응원했는지 설쌤과 함께 확인해 보자.', '설민석, 스토리박스 (지은이), 정현희 (그림), 강석화 (감수)', '단꿈아이', 'https://image.aladin.co.kr/product/34536/48/cover200/k402932059_1.jpg', '국내도서>어린이>초등 한국사', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('초역 부처의 말 - 2500년 동안 사랑받은', '2500년 동안 사람들에게 사랑받으며 회자되어 온 부처의 말을 코이케 류노스케 스님이 현대어로 재해석해 책으로 출간했다. 간결하게 축약된 핵심만을 담은 부처의 메시지는, 마음이 약해지고 어려움에 처한 사람들에게 힘과 용기를 불어넣어 준다.', '코이케 류노스케 (지은이), 박재현 (옮긴이)', '포레스트북스', 'https://image.aladin.co.kr/product/33973/70/cover200/k682930444_1.jpg', '국내도서>인문학>교양 인문학', 1, 1);
