INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('흔한남매 흔한 게임 1', '몰입감 넘치는 스토리와 두뇌를 깨우는 다양한 활동으로 구성된 ‘두뇌 액티비티 코믹 북’ 시리즈이다. 흔한남매와 함께 게임 속으로 빨려 들어가 여러 가지 미션을 해결하다 보면 손에 땀을 쥐는 재미는 물론, ‘판단력’과 ‘집중력’, ‘사고력’, ‘관찰력’ 등을 기를 수 있다.', '흔한남매 (원작), 권나혜 (글), 도니패밀리 (그림), 흔한컴퍼니 (감수)', '미래엔아이세움', 'https://image.aladin.co.kr/product/34539/7/cover200/k562932458_1.jpg', '국내도서>어린이>문화/예술/인물>음악/미술/예체능', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('국어 잘하는 아이가 이깁니다 - ‘갓민애’ 교수의 초등 국어 달인 만들기', '부모들의 국어 공부 고민을 친절하고 명쾌하게 해결해준다. 입시를 이기는 독서 전략, 서울대학교 학생들의 국어 공부 방법, 학령별 가이드까지 한 권에 꾹꾹 눌러담았다. 우리 아이를 더 즐겁고 쉽게 국어 달인으로 만들어보자.', '나민애 (지은이)', '김영사', 'https://image.aladin.co.kr/product/33600/46/cover200/8934965401_1.jpg', '국내도서>좋은부모>교육/학습>독서/작문 교육', 1, 1);
