INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2024 큰별쌤 최태성의 별★별한국사 한국사능력검정시험 심화(1, 2, 3급) 상 - 한국사능력검정시험 심화(1, 2, 3급)에 대비한 맞춤 기본서', '오랜 연구와 검증으로 한국사에 기초가 전혀 없는 사람도 단기간에 한능검에 합격할 수 있는 최적의 구성을 완성하였다. 판서의 장인 큰별쌤 최태성이 핵심만을 모아 만든 아트 판서를 수록하고, 시험에 자주 등장하는 사료, 사진, 지도 등을 모아 해설하여 합격을 위한 필수 개념을 익히기 쉽게 하였다.', '최태성 (지은이)', '이투스북', 'https://image.aladin.co.kr/product/32974/50/cover200/k422936456_1.jpg', '국내도서>수험서/자격증>한국사능력검정시험>심화(1,2,3급)', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('침묵을 배우는 시간 - 말이 넘쳐나는 세상 속, 더욱 빛을 발하는 침묵의 품격', '침묵으로 세상과 거리를 두면 역설적이게도 더 세상에 다가갈 수 있다. 이 책에서 제시하는 51가지의 침묵 도구를 적절하게 사용한다면 당신의 말의 무게는 지금과는 확연히 달라질 것이다.', '코르넬리아 토프 (지은이), 장혜경 (옮긴이)', '서교책방', 'https://image.aladin.co.kr/product/34296/75/cover200/k112932276_1.jpg', '국내도서>자기계발>협상/설득/화술>화술', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('돈의 심리학 (30만 부 기념 스페셜 에디션) - 당신은 왜 부자가 되지 못했는가', '월스트리트저널에서 10년 넘게 금융과 투자에 대한 글을 써온 칼럼니스트이자 콜라보레이티브 펀드 파트너로 활동중인 모건 하우절의 첫 책이다. 출간 즉시 아마존 투자 분야 1위를 차지했고 개인 투자자부터 전문 컨설턴트까지 극찬 세례를 받으며 명실상부 ‘2020 아마존 최고의 금융도서’로 평가받는다.', '모건 하우절 (지은이), 이지연 (옮긴이)', '인플루엔셜(주)', 'https://image.aladin.co.kr/product/32852/7/cover200/k132936910_2.jpg', '국내도서>경제경영>재테크/투자>재테크/투자 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('옆집 천사님 때문에 어느샌가 인간적으로 타락한 사연 9 (특별한정판) - Novel Engine', '생일을 맞이한 아마네는 마히루 덕분에 수많은 변화가 나타났음을 실감하고, 낯간지러운 행복을 곱씹었다. 그리하여 마히루가 태어난 날에도 축복을 선물하고자 결심하고, 바쁘게 준비하는 나날을 보내는 아마네. 그러던 어느 날, 진로를 고민하는 시기에 접어든 두 사람은 함께 사는 미래를 머릿속에 그리며, 수험 공부를 극복하기로 약속했다.', '사에키상 (지은이), 하네코토 (그림), JYH (옮긴이)', '영상출판미디어(주)', 'https://image.aladin.co.kr/product/34538/42/cover200/k222932054_1.jpg', '국내도서>소설/시/희곡>라이트 노벨>노블엔진(Novel Engine)', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('THE MONEY BOOK 더 머니북 - 잘 살아갈 우리를 위한 금융생활 안내서', '돈이 행복의 필수 조건이라고 여기면서도, 막상 돈을 잘 모르는 사람들. 《더 머니북》은 이 사람들을 위해 만들어졌다. 사용자들에게 자주 받아온 “금융교육은 어디서 받나요?”라는 질문에 토스가 내놓는 답이자, ‘누구나 편리하고 평등하게 금융하는 세상 만들기’라는 진심을 말하는 시작점이다.', '토스 (지은이)', '비바리퍼블리카', 'https://image.aladin.co.kr/product/34009/78/cover200/k772931867_1.jpg', '국내도서>경제경영>경제학/경제일반>화폐/금융/재정', 1, 1);
