INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('세이노의 가르침 (100만 부 한정판)', '2000년부터 발표된 그의 주옥같은 글들. 독자들이 자발적으로 만든 제본서는 물론, 전자책과 앱까지 나왔던 《세이노의 가르침》이 드디어 전국 서점에서 독자들을 마주한다. 여러 판본을 모으고 저자의 확인을 거쳐 최근 생각을 추가로 수록하였다.', '세이노(SayNo) (지은이)', '데이원', 'https://image.aladin.co.kr/product/34366/29/cover200/k792932002_1.jpg', '국내도서>자기계발>성공>성공학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('에그박사 13 - 자연 생물 관찰 만화', '인기 키즈 크리에이터 에그박사의 원작 영상을 생생한 관찰 스토리와 귀여운 생물 도감으로 재구성한 자연 생물 관찰 만화이다. 파브르의《곤충기》처럼 에그박사의 눈으로 바라본 신비롭고 놀라운 자연 생물의 이야기가 가득하다.', '에그박사 (원작), 박송이 (글), 홍종현 (그림), 이승현 (감수)', '미래엔아이세움', 'https://image.aladin.co.kr/product/34425/96/cover200/k422932026_2.jpg', '국내도서>어린이>과학/수학/컴퓨터>생물과 생명', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('빨모쌤의 라이브 영어회화 (특별 부록 한정판) - 맥락과 뉘앙스가 살아나는 진짜 영어 말하기 수업', '구독자 150만 명의 ‘인생 영어 선생님’으로 꼽히는 ‘빨모쌤(빨간모자쌤)’의 첫 책 『빨모쌤의 라이브 영어회화』가 드디어 출간되었다. 바람직한 영어 공부 마인드부터 빨모쌤이 엄선한 핵심 영어 표현 75개를 한 권에 총망라했다.', '빨간모자쌤 신용하 (지은이)', '웅진지식하우스', 'https://image.aladin.co.kr/product/33799/49/cover200/s122932101_1.jpg', '국내도서>외국어>영어회화>생활영어', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('해커스 토익 기출 VOCA (보카) - 주제별 연상암기로 토익 영단어 30일 완성! RC/LC 필수 토익 보카 [최신 기출 어휘+출제포인트 수록ㅣ단어암기 어플 제공]', '토익 기초 단어부터 고난도 어휘까지 모든 난이도의 단어를 수록했다. 토익 빈출?주제 30개에 맞춰?단어를 수록하여 주제별로 묶어서 쉽게 암기가 가능하다. 단어 MP3를 바로 들을 수 있는 QR코드를 각 Day 시작 페이지에 삽입하여 편리하게 학습이 가능하다.', 'David Cho (지은이)', '해커스어학연구소(Hackers)', 'https://image.aladin.co.kr/product/17602/13/cover200/s172937955_1.jpg', '국내도서>외국어>토익>Vocabulary', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('반딧불이의 혼례 3', '살날이 얼마 남지 않은 화족 영애 사토코는 어느 날 납치되어 ‘유녀도’에 갇혀버린다. 그곳에서 살인 청부업자 신페이의 도움을 받아 목숨을 건진 뒤로 이질적일 정도로 사랑받게 되는데――. 유곽에서 위기를 벗어난 사토코와 신페이는, 비밀리에 수배해 둔 배를 타고 섬 탈출을 시도한다.', '타치바나 오레코 (지은이), 유유리 (옮긴이)', '서울미디어코믹스(서울문화사)', 'https://image.aladin.co.kr/product/34574/7/cover200/k552933360_1.jpg', '국내도서>만화>본격장르만화>판타지>드라마틱 판타지', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('내 언어의 한계는 내 세계의 한계이다', '총 30권으로 구성될 인문학자 김종원이 지난 16년 동안 같은 텍스트를 접하면서도, 사람들의 변화가 제각각인 이유에 관해 사색한 답을 풀어놓은 《김종원의 세계철학전집》의 두 번째 책의 주인공은 ‘루트비히 비트겐슈타인’이다.', '김종원 (지은이)', '마인드셋', 'https://image.aladin.co.kr/product/33996/43/cover200/k412930758_2.jpg', '국내도서>인문학>서양철학>현대철학>러셀/비트겐슈타인', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('일생에 한번은 헌법을 읽어라 - 흔들릴 때마다 삶의 중심을 잡아주는 기준에 관하여', '서울대 법학전문대학원 이효원 교수는 헌법이야말로 인간 삶의 투명한 거울이라고 말하며 살면서 누구나 한번쯤은 헌법을 읽을 것을 강력히 권한다. 대한민국이 어떠한 나라가 되어야 한다고 축약해놓은 규범이자, 다양한 사고방식과 생활양식을 지닌 사람들이 더불어 살아가기 위해 만든 기반인 헌법을 공부함으로써 내가 어떤 사람이 되어야 하는지, 어떻게 살아야 하는지를 성찰할 수 있기 때문이다.', '이효원 (지은이)', '현대지성', 'https://image.aladin.co.kr/product/34361/12/cover200/k922932902_1.jpg', '국내도서>인문학>인문 에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('데뷔 못 하면 죽는 병 걸림 3부 초판 양장본 세트 - 전4권', '4년차 공시생, 낯선 몸에 빙의해 3년 전으로 돌아왔다. 그리고 그의 눈앞에 나타난 갑작스러운 상태창의 협박. 돌연사 위협 때문에 팔자에도 없던 아이돌에 도전하게 된 주인공의 대환장 일지.', '백덕수 (지은이)', '위시북스(KW북스)', 'https://image.aladin.co.kr/product/34553/38/cover200/k232933168_1.jpg', '국내도서>소설/시/희곡>판타지/환상문학>한국판타지/환상소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('타키 포오의 코믹 어드벤처 6 - 캐릭온TV 오리지널 코믹스', '“오늘부터 내 이름은 미노타우로스다!” 돌아갈 수 없는 모습으로 변해 버린 소, 미노타우로스. 복수를 위해 사는 미노타우로스를 쓰러트리기 위해 타키와 포오는 미로 깊은 곳으로 향한다. 미노타우로스의 첫 번째 친구와 함께.', '캐릭온TV (원작), 안도감 (글), 김규태 (그림)', '대원키즈', 'https://image.aladin.co.kr/product/34499/61/cover200/k452932751_1.jpg', '국내도서>어린이>TV/만화/영화>만화 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('사랑과 결함', '데뷔 3년 만에 이효석문학상·문지문학상·황금드래곤문학상을 석권하며 한국문학의 기대주로 자리매김한 예소연의 첫 소설집. 한 작가의 시작을 알리듯 뜨거운 박력이 느껴지는 이번 소설집에는 2023년 문지문학상 수상작 「사랑과 결함」, 문학과지성사 ‘이 계절의 소설’ 선정작 「우리는 계절마다」 「그 개와 혁명」을 비롯해 총 열 편의 작품이 수록되었다.', '예소연 (지은이)', '문학동네', 'https://image.aladin.co.kr/product/34366/96/cover200/k972932005_1.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이후 한국소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('흔한남매 과학 탐험대 11 : 여러 가지 힘', '《흔한남매 과학 탐험대》 시리즈는 과학을 처음 접하는 아이들이 과학의 주요 내용을 미리 맛볼 수 있는 초등 과학 입문서이다. 흔한남매가 최고의 과학 전문가들과 함께 우주, 지구와 달, 우리 몸 등을 여행하며 쉽고 재미있게 과학 지식을 배운다.', '흔한남매 (원작), 김언정, 송은경 (글), 김덕영 (그림), 흔한컴퍼니 (감수), 정현철, 김희목, 권경아, 최진수 (기획)', '주니어김영사', 'https://image.aladin.co.kr/product/34553/89/cover200/8934935197_2.jpg', '국내도서>어린이>과학/수학/컴퓨터>지구와 우주', 1, 1);
