INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('그의 운명에 대한 아주 개인적인 생각', '우리가 묻고 싶었던 것, 그리고 유시민의 답. 우리가 겪어낸 지난 2년을 정리하고 다시 해체해 냉철하게 원인과 결과를 분석하며 개인과 사회가 겪어야 했던 변화들을 일목요연하게 보여준다.', '유시민 (지은이)', '생각의길', 'https://image.aladin.co.kr/product/34121/16/cover200/s812931190_1.jpg', '국내도서>사회과학>비평/칼럼>정치비평/칼럼', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('사피엔스 - 유인원에서 사이보그까지, 인간 역사의 대담하고 위대한 질문', '2011년 원서 출간 이후 10년을 돌아보고 위기 상황을 맞은 인류에게 건네는 제언이 특별 서문으로 수록되었다. 현재 인류는 그 어느 때보다 어려운 상황을 맞고 있다. 난국을 헤쳐나가기 위해 무엇을 해야 할까? 저자는 더 나은 세상을 위한 키워드로 ‘인간 이해’를 강조한다.', '유발 하라리 (지은이), 조현욱 (옮긴이), 이태수 (감수)', '김영사', 'https://image.aladin.co.kr/product/31424/4/cover200/k482832219_1.jpg', '국내도서>인문학>인류학/고고학>인류학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('부모의 어휘력 - 정확히 말할수록 아이의 세상이 커지는 필수 어휘 126', '대한민국 50만 학부모가 선택한 인문 교육 전문가 김종원 작가가 고르고 고른, 부모라면 꼭 알아야 할 필수 어휘 126가지를 한 권으로 정리한 어휘 공부 책이다.', '김종원 (지은이)', '카시오페아', 'https://image.aladin.co.kr/product/34112/12/cover200/k662931384_1.jpg', '국내도서>좋은부모>교육/학습>언어/외국어 교육', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('스토리 설계자 - 고객의 욕망을 꿰뚫는 31가지 카피라이팅 과학', '“상품이 이토록 훌륭한데 왜 이렇게 안 팔릴까?” 사업을 하는 사람이 가장 불안해지는 순간은 상품에 대한 확신이 매출로 이어지지 않을 때다. 문제의 근본은 품질도 가격도 아닌 바로 ‘세일즈 카피라이팅!’ 이를 단숨에 해결해줄 책이 나왔다. 『마케팅 설계자』를 쓴 러셀 브런슨과 함께 사업가 수천 명을 도우며 상품, 코칭, 소프트웨어를 판매하여 수천만 달러 매출을 올린 짐 에드워즈의 신간 『스토리 설계자』다.', '짐 에드워즈 (지은이), 신솔잎 (옮긴이)', '윌북', 'https://image.aladin.co.kr/product/34444/85/cover200/k232932235_3.jpg', '국내도서>경제경영>마케팅/세일즈>광고/홍보/PR', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('내가 네번째로 사랑하는 계절 - 한정원의 8월', '하루 한 편, 한 달 한 권, 1년 365일의 읽을거리를 쌓아가는 ‘시의적절’ 시리즈, 한정원 시인의 8월을 만난다. 마냥 사랑할 수만은 없는 무더운 여름, 어쩐지 미심쩍고도 미진한 이 마음을 두고 『내가 네번째로 사랑하는 계절』이라 말하는 시인.', '한정원 (지은이)', '난다', 'https://image.aladin.co.kr/product/34403/76/cover200/k912932614_1.jpg', '국내도서>에세이>한국에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('나는 메트로폴리탄 미술관의 경비원입니다 - 경이로운 세계 속으로 숨어버린 한 남자의 이야기', '뉴욕 메트로폴리탄 미술관에서 경비원으로 근무했던 패트릭 브링리의 독특하면서도 지적인 회고를 담은 에세이다. 가족의 죽음으로 고통 속에 웅크리고 있던 한 남자가 미술관에서 10년이라는 시간을 보내며 상실감을 극복하고 마침내 세상으로 나아갈 힘을 얻는 여정을 섬세하게 그려냈다.', '패트릭 브링리 (지은이), 김희정, 조현주 (옮긴이)', '웅진지식하우스', 'https://image.aladin.co.kr/product/32892/38/cover200/s402930162_1.jpg', '국내도서>에세이>외국에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('내 몸 혁명 - 뱃살과 질병 없이 살려면 숫자보다 몸을 바꿔라', '박용우 박사의 건강 라이프코칭이 한 권의 책으로 묶였다. 대사이상체중을 건강체중으로 되돌리는 방법을 비롯해 망가진 몸을 근본적으로 변화시키는 4주간의 프로그램, 살찌지 않는 건강한 몸을 위한 건강관리 노하우 등이 담겨 있다.', '박용우 (지은이)', '루미너스', 'https://image.aladin.co.kr/product/33077/57/cover200/k312937406_1.jpg', '국내도서>건강/취미>다이어트', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('괴수 8호 side B 1', '화제의 괴수 액션 판타지 스핀오프 코믹스 등장!! 방위대원 각각의 최전선이 여기에!! 공격의 주류가 총기로 옮겨가는 흐름 속에서  칼과 함께 살아온 호시나 소우시로는 칼을 놓지도, 방위대를 떠나지도 못하고 있었다. 그러던 와중, 제3부대 대장 아시로 미나를 만나게 되는데――?!', '히다노 켄타로 (지은이), 마츠모토 나오야, Keiji Ando (원작), 유유리 (옮긴이)', '서울미디어코믹스(서울문화사)', 'https://image.aladin.co.kr/product/34361/47/cover200/k462932903_1.jpg', '국내도서>만화>소년만화>액션', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('영어로 문장 만들기 훈련 1차 임계점', '문장 만들기 훈련이 지속되기 힘든 이유를 파악해 본책의 모든 한국어 문장을 영어 문장 구조로 시각화하여 학습자가 ‘주어 + 서술어 뼈대’를 체득해 머릿속에 떠오른 한국어 문장을 가장 단시간 내에 영어 문장으로 만들 수 있게 한다.', '유은하 (지은이)', '사람in', 'https://image.aladin.co.kr/product/34292/9/cover200/k682932176_1.jpg', '국내도서>외국어>영작문', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('정류장에 두고 온 뉴욕치즈케이크', '대전에 있는 독립서점 &lt;구구절절&gt;이 기획하고 &lt;월간 토마토&gt;가 발간을 맡은 &lt;독립서점시인선&gt; 첫 번째 책으로, 정덕재 시인의 시집 『정류장에 두고 온 뉴욕치즈케이크』가 발행됐다. 그림이 있는 시집으로 정덕재 시인의 시와 권현칠 화백의 그림이 함께 실려 있다.', '정덕재 (지은이), 권현칠 (그림)', '월간토마토', 'https://image.aladin.co.kr/product/34392/47/cover200/k542932609_2.jpg', '국내도서>소설/시/희곡>시>한국시', 1, 1);
