INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('눈과 보이지 않는 - 2024 뉴베리 대상 수상작', '2024 뉴베리 대상 수상작. 도시의 공원에 사는 개 요하네스가 공원 안 우리에 갇혀 살아가는 들소들을 탈출시키는 과정을 그린 모험담이다. 불가능해 보이는 들소 탈출 작전을 펼치면서 요하네스는 자신의 내면을 깊이 들여다보고 스스로에게 질문을 던지는 시간을 갖게 된다.', '데이브 에거스 (지은이), 숀 해리스 (그림), 송섬별 (옮긴이)', '위즈덤하우스', 'https://image.aladin.co.kr/product/34433/93/cover200/k762932124_1.jpg', '국내도서>어린이>동화/명작/고전>외국창작동화', 1, 1);
