INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('음악소설집 音樂小說集', '음악 전문 출판사 ‘프란츠’가 선보이는 첫 소설 앤솔러지 『음악소설집音樂小說集』. 한국문학을 대표하는 다섯 소설가인 김애란, 김연수, 윤성희, 은희경, 편혜영의 음악을 테마로 한 신작 단편소설이 실렸다. 일상에서 마주치는 다채로운 음악의 장면들을 섬세하게 포착한 다섯 개의 이야기를 통해, 삶의 어떤 순간이라도 음악이 될 수 있다는 것을 깨닫게 한다.', '김애란, 김연수, 윤성희, 은희경, 편혜영 (지은이)', '프란츠', 'https://image.aladin.co.kr/product/34130/64/cover200/k222931917_2.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이후 한국소설', 1, 1);
