INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('어떻게 헤어질 결심을', '영화 &lt;헤어질 결심&gt;은 박해일과 탕웨이를 비롯한 배우들의 열연은 물론, 인상적인 대사와 꼼꼼한 디테일로 관객들을 사로잡았다. 각본에 이어 스토리보드북과 포토북까지 여러 권의 공식 도서가 출간된 이후, 드디어 가장 사적인 이야기가 공개된다.', '박찬욱 (지은이)', '을유문화사', 'https://image.aladin.co.kr/product/34572/67/cover200/8932475059_1.jpg', '국내도서>예술/대중문화>사진>사진집', 1, 1);
