INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('언젠가 우리가 같은 별을 바라본다면', '고국을 떠나 70년 만에 필리핀의 한 작은 섬에서 발견된 쑤니 할머니의 젊은 시절을 담은 이야기이다. 작가는 우리나라가 일본에 주권을 빼앗긴 채 가난하고 핍박받던 시절을 맨몸으로 버텨 낸 우리 어머니의 어머니, 아버지의 아버지들의 이야기를 남기고자 집필을 시작했다.', '차인표 (지은이), 제딧 (그림)', '해결책', 'https://image.aladin.co.kr/product/28513/21/cover200/k882835835_1.jpg', '국내도서>청소년>청소년 문학>청소년 소설', 1, 1);
