INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('아주 작은 대화의 기술 - 힘들이지 않고 사람의 마음을 얻는 80가지 인간관계의 기술', '인간관계 부자들의 소통 비법을 낱낱이 파헤친다. 호감을 사는 첫인사 요령부터 까다로운 대화에 대처하는 방법, 갈등 해소 전략까지 담겨 있다. 여기에 더해 상대방의 감정을 읽고 공감하는 기술 등 이 책에 소개된 80가지 기술은 당신을 대화의 고수로 만들어줄 것이다.', '레일 라운즈 (지은이), 정지현 (옮긴이)', '현대지성', 'https://image.aladin.co.kr/product/33953/77/cover200/k332930438_1.jpg', '국내도서>자기계발>인간관계>인간관계', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('혼자일 수 없다면 나아갈 수 없다 - 프리드리히 니체 아포리즘', '니체는 평생 열네 권의 책을 썼고, 바그너를 비롯한 여러 사람들과 편지를 주고받았다. 『혼자일 수 없다면 나아갈 수 없다』는 그가 남긴 책들과 사후 발견된 편지, 일기, 메모, 미완성 유고 등에서 통찰과 조언을 담은 힘 있고 아름다운 문장들을 모아 엮은 책이다.', '프리드리히 니체 (지은이), 김욱 (편역)', '포레스트북스', 'https://image.aladin.co.kr/product/33556/67/cover200/k472939677_1.jpg', '국내도서>인문학>인문 에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('5개 원소로 읽는 결정적 세계사 - 세상 가장 작은 단위로 단숨에 읽는 6000년의 시간', '원소의 눈으로 읽으면 역사가 새롭고 빠르게 읽힌다. 발상의 전환이 돋보이는 《5개 원소로 읽는 결정적 세계사》는 더 이상 쪼갤 수 없는 단위의 집합인 원소에 새겨진 역사의 결정적인 24가지 순간을 기록한다.', '쑨야페이 (지은이), 이신혜 (옮긴이), 김봉중 (감수)', '더퀘스트', 'https://image.aladin.co.kr/product/34472/0/cover200/k212932639_1.jpg', '국내도서>역사>세계사 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('마흔에 읽는 쇼펜하우어 (리커버, 200쇄 기념 확장판) - 마음의 위기를 다스리는 철학 수업', '인생의 의미를 끊임없이 고민한 철학자, 아르투어 쇼펜하우어의 깨달음과 철학적 사유 중 현시대 우리가 고통을 해소하고 마음의 위기를 극복하는 데 도움이 될 내용을 30가지로 정리했다.', '강용수 (지은이)', '유노북스', 'https://image.aladin.co.kr/product/34196/48/cover200/k222931412_2.jpg', '국내도서>인문학>서양철학>근대철학>쇼펜하우어', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('죽이고 싶은 아이 - 2021 아르코 문학나눔 선정', '&lt;세계를 건너 너에게 갈게&gt; 이꽃님 작가가 결말을 예측할 수 없는, 놀랍도록 흡인력 있는 작품으로 돌아왔다. &lt;죽이고 싶은 아이&gt;는 한 여고생의 죽음이라는, 결코 평범하지 않은 이야기를 통해 독자들에게 진실과 믿음에 관한 이야기를 건넨다.', '이꽃님 (지은이)', '우리학교', 'https://image.aladin.co.kr/product/27211/83/cover200/k422732197_2.jpg', '국내도서>청소년>청소년 문학>청소년 소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('일본어 무작정 따라하기 완전판', '18년 동안 일본어 베스트셀러&스테디셀러로 38만 독자들에게 전폭적인 사랑을 받아온 &lt;일본어 무작정 따라하기&gt;가 &lt;일본어 무작정 따라하기 심화편&gt;과 합쳐져 이번에 완전체의 형태를 선보인다.', '후지이 아사리 (지은이)', '길벗이지톡', 'https://image.aladin.co.kr/product/29877/32/cover200/k712838735_1.jpg', '국내도서>외국어>일본어>일본어 첫걸음', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('지도로 보아야 보인다 - 지도를 펼치지 않고는 지금의 세상을, 다가올 세계를 제대로 볼 수 없다', '아시아, 유럽, 아메리카, 중동, 아프리카 등 5대륙 28개국의 21세기 지정학적 현황을 120개의 생생하고 스펙터클한 지도와 함께 설명하는 책으로, 저자는 프랑스에서 7년째 매주 토요일 저녁 아르테(Arte) TV에서 방영되는 지정학 프로그램인 「Le Dessous des cartes(지도의 이면)」의 진행과 총괄 책임을 맡고 있는 저널리스트다.', '에밀리 오브리, 프랭크 테타르, 토마 앙사르 (지은이), 이수진 (옮긴이)', '사이', 'https://image.aladin.co.kr/product/34336/48/cover200/8993178305_1.jpg', '국내도서>사회과학>지리학/지정학>지정학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('비 그친 오후의 헌책방', '소설은 평화롭던 날을 보내던 다카코가 1년 동안 사내연애를 해온 남자친구에게서 다른 여자와 결혼하게 됐다는 갑작스러운 이별 통보를 받는 장면으로 시작된다. 이별의 충격으로 회사마저 그만두고 폐인이 되어 집에 틀어박혔는데, 어느 날 왕래가 뜸했던 외삼촌에게 전화가 걸려온다.', '야기사와 사토시 (지은이), 서혜영 (옮긴이)', '다산책방', 'https://image.aladin.co.kr/product/34286/10/cover200/k302932076_2.jpg', '국내도서>소설/시/희곡>일본소설>1950년대 이후 일본소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('대한경제부흥회 - 우리는 왜 돈을 못 버는가', '나는 감히 따라 할 수 없을 것 같은 경제 조언은 다 버려라. 우리에게 전혀 도움이 되지 않는 잘못된 경제 정책은 다 바꾸자. 실전형, 생계형 경제 초고수들이 말하는 전 국민 인생 살리기 프로젝트가 여기에 있다. 
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('우리에게 남은 시간 46일', '시한부인 해인의 곁에서 슬퍼하며, 끝까지 사랑과 지지를 아끼지 않는 우현의 슬픈 사랑이야기를 담고 있다. 마지막까지 절대 포기하지 않는 그들의 사랑은 독자의 마음을 짙게 울릴 것이다. 이 책은 우리에게 삶의 소중함과 사랑의 힘을 되새기게 해주는 인상적인 작품이다.', '이설 (지은이)', '하이스트', 'https://image.aladin.co.kr/product/33408/27/cover200/k762938238_2.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이후 한국소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('이처럼 사소한 것들', '2023년 4월 국내에 처음 소개된 『맡겨진 소녀』로 국내 문인들과 문학 독자들의 열렬한 환호를 받은 클레어 키건의 대표작. 자신이 속한 사회 공동체의 은밀한 공모를 발견하고 자칫 모든 걸 잃을 수 있는 선택 앞에서 고뇌하는 한 남자의 내면을 그린 작품이다.', '클레어 키건 (지은이), 홍한별 (옮긴이)', '다산책방', 'https://image.aladin.co.kr/product/32938/68/cover200/k472936042_1.jpg', '국내도서>소설/시/희곡>세계의 소설>아일랜드소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('일이어도, 일이 아니어도 - 만화와 요시나가 후미: 요시나가 후미 인터뷰집', '30년이 넘는 세월 동안 유수의 명작들을 그려온 요시나가 후미는 작품마다 삶의 아이러니, 인간관계의 역설을 날카롭게 그려왔다. 인터뷰집 『일이어도, 일이 아니어도 : 만화와 요시나가 후미』는 그런 그가 만화를 처음 만난 어린 시절부터 데뷔작을 그리고 대표작을 완결하며, 현재 연재중인 최신작에 이르기까지의 만화 일생을 담고 있다.', '요시나가 후미 (지은이), 김솜이 (옮긴이), 야마모토 후미코 (인터뷰이)', '문학동네', 'https://image.aladin.co.kr/product/34572/89/cover200/k932933368_1.jpg', '국내도서>만화>논픽션/휴먼만화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('Go Go 카카오프렌즈 33 : 필리핀 - 세계 역사 문화 체험 학습만화', '카카오프렌즈가 시간여행의 주인공이 되어 펼쳐지는 이야기로 독자들은 카카오프렌즈의 재기발랄한 여행을 통해 세계 곳곳의 다양한 역사와 문화를 경험한다. 깜찍한 캐릭터의 매력에 재미있는 스토리, 알찬 정보까지 겸비한 책이다.', '김미영 (지은이), 김정한 (그림)', '아울북', 'https://image.aladin.co.kr/product/34454/93/cover200/k952932431_2.jpg', '국내도서>어린이>사회/역사/철학>세계사', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('EBS 다큐프라임 자본주의', '지금 자본주의는 기로에 놓여 있다. 이대로 갈 것인가, 새로운 길을 모색할 것인가. 약 250년에 걸쳐 우리 사회를 지배했으며 현재 위기를 겪고 있는 ‘자본주의’를 쉽게 풀어낸 방송, [EBS 다큐프라임 &lt;자본주의&gt; 5부작]이 책으로 출간됐다.', 'EBS 자본주의 제작팀 (지은이)', '가나출판사', 'https://image.aladin.co.kr/product/3164/26/cover200/8957365796_1.jpg', '국내도서>경제경영>경제학/경제일반>경제이론/경제사상', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('절망과 열정의 시대 - 일제강점기 장르 단편선', '독자들을 대한 독립의 뜨거운 열정과 장르 소설의 흥미로운 세계가 함께하는 시대로 안내할 특별한 책 『절망과 열정의 시대』가 출간된다. 다섯 명의 소설가가 일제강점기를 배경으로 각기 다른 장르의 단편소설을 집필하여 완성한 작품으로 스릴러, 호러, 로맨스, 판타지 등 다양한 장르 단편이 모여 독자들에게 새로운 재미를 전한다.', '곽재식, 최희라, 배명은, 이작, 홍지운 (지은이)', '구픽', 'https://image.aladin.co.kr/product/34324/0/cover200/k322932482_3.jpg', '국내도서>소설/시/희곡>액션/스릴러소설>한국 액션/스릴러소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('전설의 프로 트레이더 빅 - 18년간 단 한 해도 손실 없이 연 수익률 72% 기록한', '지난 2011년 번역서가 나왔다가 절판된 이후 중고책이 정가의 20배 넘게 거래되며 기술적(차트) 분석, 기본적(펀더멘털) 분석 투자자 모두에게 좋은 반응을 얻었다. 책에는 추세전환 포착, 매수·매도 포지션 잡기, 감정 훈련법 등 바로 실전에 적용할 수 있는 알짜 노하우도 풍부하다.', '빅터 스페란데오, 설리번 브라운 (지은이), 이건 (옮긴이)', '액티브', 'https://image.aladin.co.kr/product/34502/2/cover200/k102932756_1.jpg', '국내도서>경제경영>재테크/투자>주식/펀드', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('배당투자, 나는 50에 은퇴했다 - 평생 월 1,000만 원씩 받아내는 ‘배당주’ 입장권', '저자의 투자 이유를 듣고 5분 만에 끝내는 종목분석법, 금액·상황별 포트폴리오, 리밸런싱과 매매전략, 세금에서 탈출하는 절세전략만 따라오면, 3,000만 원으로 15억 원을 만든 쭈압처럼 파이어의 고지에 오를 수 있다. 이제는 당신의 차례다.', '쭈압(정영주) (지은이)', '체인지업', 'https://image.aladin.co.kr/product/34329/91/cover200/k982932683_1.jpg', '국내도서>경제경영>재테크/투자>재테크/투자 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('이은경쌤의 중등어휘일력 365 (스프링) - 10대가 반드시 알아야 할 국어 문학·비문학 필수 어휘', '『이은경쌤의 초등어휘일력 365』『이은경쌤의 사자성어 속담 일력 365』 등으로 학습 일력 붐을 일으킨 이은경쌤이 이번에는 10대를 위한 『중등어휘일력 365』를 긴 시간 공들여 준비했다. 유튜브 ‘슬기로운초등생활’ 30만 학부모의 멘토이자 실제 중등, 고등 자녀를 두고 있어 그런 문제를 누구보다 잘 아는 저자가 교과서 속 현대·고전 문학, 비문학 작품 속 주요 어휘들을 모아 일력에 담았다.', '이은경 (지은이), 배혜림 (감수)', '포레스트북스', 'https://image.aladin.co.kr/product/34332/45/cover200/s372932511_1.jpg', '국내도서>좋은부모>교육/학습>독서/작문 교육', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('철도원 삼대', '세계적인 거장 황석영이 장편소설 &lt;철도원 삼대&gt;로 한반도 백년의 역사를 꿰뚫는다. 구상부터 집필까지 30년의 세월이 걸린 이 역작은 철도원 가족을 둘러싼 방대한 서사를 통해 일제시대부터 현재까지 이어지는 노동자의 삶을 실감나게 다루며 대한민국 근현대사를 문학적으로 탁월하게 구현해냈다.', '황석영 (지은이)', '창비', 'https://image.aladin.co.kr/product/24058/18/cover200/8936434438_3.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이후 한국소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('여자 둘이 살고 있습니다 - 개정증보판', '일파만파 ‘여둘’ 열풍을 불러일으킨 김하나, 황선우 작가를 세상에 알린 베스트셀러 『여자 둘이 살고 있습니다』 2024년 개정증보판이 두 작가의 새 에세이들과 미공개 사진들을 더해 이야기장수에서 출간된다.', '김하나, 황선우 (지은이)', '이야기장수', 'https://image.aladin.co.kr/product/34476/64/cover200/k042932748_2.jpg', '국내도서>에세이>한국에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('다시, 역사의 쓸모 - 합리적이고 품위 있는 선택을 위한 20가지 지혜', '5년 연속 역사 베스트셀러, 국립어린이청소년도서관 ‘청소년추천도서’, 경남·청주·양주 등 전국 지자체 ‘올해의 책’ 등 대한민국에 쓸모 열풍을 불러일으켰던 《역사의 쓸모》가 새로운 이야기로 돌아왔다.', '최태성 (지은이)', '프런트페이지', 'https://image.aladin.co.kr/product/34323/80/cover200/k242932480_1.jpg', '국내도서>역사>역사학>역사학 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('홍학의 자리', '10년 가까이 스릴러 장르에 매진하며 장편 단편 할 것 없이 독자들의 눈길을 사로잡는 작품을 발표한 정해연 작가의 신작. 프롤로그를 시작으로 총 21개의 챕터로 구성된 이 작품은 매 챕터마다 놀라운 전개와 탁월한 스토리텔링을 보여준다.', '정해연 (지은이)', '엘릭시르', 'https://image.aladin.co.kr/product/27587/94/cover200/8954681158_1.jpg', '국내도서>소설/시/희곡>추리/미스터리소설>한국 추리/미스터리소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('리틀 라이프 2', '영미권을 대표하는 문학상 맨부커상과 전미도서상 최종후보에 나란히 오르고, 「뉴욕타임스」, 「워싱턴포스트」, 「가디언」, 「월스트리트저널」을 비롯한 25개 언론사에서 ''올해의 책''으로 꼽은 화제작.', '한야 야나기하라 (지은이), 권진아 (옮긴이)', '시공사', 'https://image.aladin.co.kr/product/8588/78/cover200/8952776380_3.jpg', '국내도서>소설/시/희곡>영미소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('타키 포오 얼렁뚱땅 상식스쿨 4 - 초등 상식 학습 스토리북', '유튜브 241만 구독자에게 사랑 받는 최고의 인기 크리에이터 ''캐릭온 TV''가 초등 상식 학습 스토리북으로 출간되었다. 엉뚱 발랄 타키 포오와 함께 재미있는 스토리 속으로 풍덩 들어가보자. 읽다 보면 자연스럽게 초등 상식이 쑥쑥 올라간다.', '캐릭온TV (원작), 김언정 (글), 조수현 (그림), 정효해 (감수)', '대원키즈', 'https://image.aladin.co.kr/product/34546/9/cover200/k152933068_2.jpg', '국내도서>어린이>초등3~4학년>상식/교양', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('불편한 한국사 - 진실을 쫓는 역사 독립군 배기성의', '고대사, 고려사, 조선사까지 폭넓은 우리 역사의 실제 기록을 토대로, 대중적으로 많이 알려진 혹은 (그렇지 않더라도) 역사적 의미가 큰 사건과 인물을 엄선해 《불편한 한국사》에서 그 숨은 진실을 추적한다.', '배기성 (지은이)', '블랙피쉬', 'https://image.aladin.co.kr/product/34527/99/cover200/8968334757_1.jpg', '국내도서>역사>한국사 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('마음먹은 고양이', '실컷 잠을 자고 이제 막 깨어나 기지개를 펴는 고양이가 있다. 쭈우욱 기지개를 펴고, 뒹굴뒹굴하는 모습이 여느 고양이와 다를 바가 없지만 남다른 고양이다. 바로 ‘마음먹은 고양이’다. 마음먹은 고양이가 맨 먼저 마음먹은 건 무엇일까?', '강경호 (지은이), 다나 (그림)', '나무말미', 'https://image.aladin.co.kr/product/34311/68/cover200/k942932377_2.jpg', '국내도서>유아>그림책>나라별 그림책>한국 그림책', 1, 1);
