INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('공부머리 키우는 기적의 독서 습관 - 단 10일이면 저절로 되는 내 아이 독서 습관 기르기', '우리 아이들이 책 읽기에 푹 빠지게 하는 구체적인 방법이 담겨 있다. 아이가 책을 읽어야 하는 이유를 아이의 발달단계에 맞춰 자세히 설명한다. 학교 공부와 독서의 관련성 그리고 그 중요성에 대해 다각도로 분석했다. 가정에서 필요한 독서 기본원칙과 책 읽기 활동 시 꼭 지켜야 할 방법, 집에서 실천할 수 있는 책 읽기 10일 프로젝트 등을 구체적으로 제시하여 누구나 쉽게 따라 할 수 있다.', '김기용 (지은이)', '미디어숲', 'https://image.aladin.co.kr/product/34500/49/cover200/k482932753_2.jpg', '국내도서>좋은부모>교육/학습>독서/작문 교육', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('매직 스플릿 - 자산과 현금흐름이 동시에 불어나는 새로운 투자 시스템', '100억대 자산가이자 베스트셀러 작가 박성현의 신간 『매직 스플릿』이 출간되었다. 매직 스플릿은 자산과 현금흐름이 동시에 불어나는 새로운 투자 시스템으로, 가치투자와 단기투자의 단점을 보완하고 장점을 강화해 수익을 극대화한 투자법이다.', '박성현 (지은이)', '경이로움', 'https://image.aladin.co.kr/product/34434/20/cover200/k592932125_3.jpg', '국내도서>경제경영>재테크/투자>주식/펀드', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('즐거운 어른', '이옥선 작가는 독보적인 말하기와 글쓰기로 요즘 여성 독자들을 사로잡고 있는 김하나 작가의 어머니이다. 집안에 가훈처럼 내려오는 지령이 ''만다꼬''(뭐한다고)일 정도로, 세간의 집들과는 사뭇 다른 분위기로 가정을 경영해온 이옥선 작가가 첫 단독에세이를 펴냈다. 책 제목은 ''즐거운 어른''.', '이옥선 (지은이)', '이야기장수', 'https://image.aladin.co.kr/product/34560/43/cover200/k762933261_1.jpg', '국내도서>에세이>한국에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('이 지랄맞음이 쌓여 축제가 되겠지', '2023년 샘터 문예공모전 생활수필 부문 대상을 받은 시각장애인 에세이스트 조승리의 첫번째 단행본이 출간되었다. 저자는 앞이 보이지 않는 캄캄한 현실 속에서도 자신만의 불꽃을 여실히 지켜냈음을 보여준다.', '조승리 (지은이)', '달', 'https://image.aladin.co.kr/product/33696/25/cover200/k962939047_1.jpg', '국내도서>에세이>한국에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('홀리', '반세기 동안 거의 매년 새로운 작품을 발표하며 독자들에게 기쁨을 안겨 준 거장 스티븐 킹이 『홀리』로 야심 차게 돌아왔다. 빌 호지스 3부작에서 조역으로 등장해 장편 『아웃사이더』와 중편 「피가 흐르는 곳에」에서 탐정으로서 활약을 펼친 홀리 기브니가 연쇄 실종 사건에 뛰어든다.', '스티븐 킹 (지은이), 이은선 (옮긴이)', '황금가지', 'https://image.aladin.co.kr/product/34534/72/cover200/k942932956_1.jpg', '국내도서>소설/시/희곡>호러.공포소설>외국 호러.공포소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('총 균 쇠 - 인간 사회의 운명을 바꾼 힘', '학문의 경계를 넘나드는 세계적 석학 재레드 다이아몬드. 인류 문명에 대한 예리한 통찰을 전해온 그의 대표작이자 1998년 퓰리처상 수상작 《총, 균, 쇠》를 새 번역, 새 편집으로 만난다. 출간 25년 기념 뉴에디션에는 2023년 저자 특별서문과 서울대 인류학과 박한선 교수의 해제, 새 서문과 후기를 수록해 풍성한 읽을거리를 더했다.', '재레드 다이아몬드 (지은이), 강주헌 (옮긴이)', '김영사', 'https://image.aladin.co.kr/product/31629/43/cover200/8934942460_1.jpg', '국내도서>인문학>교양 인문학', 1, 1);
