INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('이기적 유전자 - 40주년 기념판', '과학을 넘어선 우리 시대의 고전, 『이기적 유전자』 40주년 기념판. 진화론의 새로운 패러다임을 제시한 이 책은 다윈의 ‘적자생존과 자연선택’이라는 개념을 유전자 단위로 끌어내려 진화를 설명한다.', '리처드 도킨스 (지은이), 홍영남, 이상임 (옮긴이)', '을유문화사', 'https://image.aladin.co.kr/product/17048/25/cover200/8932473900_1.jpg', '국내도서>과학>생명과학>생명과학', 1, 1);
