INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('A4 한 장을 쓰는 힘 - 글쓰기 근력을 길러줄 최소한의 글쓰기 수업', '학생들에게 논술과 자기소개서 쓰기, 철학적 글쓰기와 서평 쓰기를 가르쳐온 저자는 많은 학생이 글쓰기를 두려워하고 어려워한다는 사실을 알게 되었다. 비단 학생들뿐만 아니라 오지선다형, 암기식 수업을 받고 자란 대부분의 사람이 A4 한 장조차 채우기 어려워하고 있었다. 어떻게 하면 이들이 글쓰기를 차근차근 배울 수 있게 도울 수 있을까 고민한 결과가 이 책이다.', '안광복 (지은이)', '어크로스', 'https://image.aladin.co.kr/product/34454/39/cover200/k872932438_1.jpg', '국내도서>인문학>책읽기/글쓰기>글쓰기', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('물고기는 존재하지 않는다 - 상실, 사랑 그리고 숨어 있는 삶의 질서에 관한 이야기', '집착에 가까울 만큼 자연계에 질서를 부여하려 했던 19세기 어느 과학자의 삶을 흥미롭게 좇아가는 이 책은 어느 순간 독자들을 혼돈의 한복판으로 데려가서 우리가 믿고 있던 삶의 질서에 관해 한 가지 의문을 제기한다. “물고기가 존재하지 않는다는 것은 엄연한 하나의 사실이다.', '룰루 밀러 (지은이), 정지인 (옮긴이)', '곰출판', 'https://image.aladin.co.kr/product/28465/73/cover200/k092835920_2.jpg', '국내도서>과학>기초과학/교양과학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('나를 소모하지 않는 현명한 태도에 관하여', '모든 것이 과하게 요구되고, 요란하게 소비되는 시대에 ‘더 현명한 삶의 방식’은 무엇인지를 통찰한 책이다. 독일의 언론인이자 베스트셀러 작가이며 문학과 커뮤니케이션 과학, 심리학 등을 두루 공부한 마티아스 뇔케 박사는 특유의 재치와 명쾌함이 돋이는 글쓰기로 ‘보여주기 위해 극대화하는 삶이 아닌 조용히 나를 지키는 삶’으로 독자들을 안내한다.', '마티아스 뇔케 (지은이), 이미옥 (옮긴이)', '퍼스트펭귄', 'https://image.aladin.co.kr/product/33562/77/cover200/k882939888_1.jpg', '국내도서>자기계발>인간관계>인간관계', 1, 1);
