INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('사라진 서점', '더블린의 신비한 서점을 둘러싼 기묘한 이야기가 과거와 현대를 오가며 펼쳐진다. 2023년 영미권 최고의 화제작 《사라진 서점》이 드디어 한국의 독자들을 찾아왔다. 1920년대 파리와 더블린을 배경으로 역사적 사실과 소설적 상상을 우아하게 섞은 소설이다.', '이비 우즈 (지은이), 이영아 (옮긴이)', '인플루엔셜(주)', 'https://image.aladin.co.kr/product/34358/83/cover200/k152932909_1.jpg', '국내도서>소설/시/희곡>세계의 소설>아일랜드소설', 1, 1);
