INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('괴수 8호 12', '식별 클래스 괴수의 습격을 받은 동방 사단 에어리어. 나루미와 키코루는 각개 격파에 성공하지만, 호시나의 대전 상대는 시작품 10호의 완성품인 11호. 게다가 호시나가 장착하는 식별 괴수 병기는 해방 전력이 83% 이상 올라가지 않아 고전을 면치 못하는데?!', '마츠모토 나오야 (지은이), 유유리 (옮긴이)', '서울미디어코믹스(서울문화사)', 'https://image.aladin.co.kr/product/34041/86/cover200/k562931076_1.jpg', '국내도서>만화>소년만화>액션', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('나는 다정한 관찰자가 되기로 했다', '‘교육전문가는 아이를 어떻게 키울까?’ 일명 이은경쌤은 누적조회수 3,000만 뷰를 기록한 교육전문가면서 수많은 자녀교육서와 학습서를 출간한 베스트셀러 작가이다. 이 책은 저자가 또래 엄마들과 온, 오프에서 만나 고민을 나누면서 관심이 높았던 주제를 모아 에세이 형식으로 쓴 책이다.', '이은경 (지은이)', '서교책방', 'https://image.aladin.co.kr/product/33949/55/cover200/k702930338_1.jpg', '국내도서>좋은부모>육아/교육 에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('슬픔에 이름 붙이기 - 마음의 혼란을 언어의 질서로 꿰매는 감정 사전', '애매모호하더라도 우리 내면에 분명하게 존재하는 섬세한 느낌들에 이름을 붙여온 ‘슬픔에 이름 붙이기’ 프로젝트. 그 프로젝트로 십여 년 동안 모아온 ‘감정 신조어’를 집대성한 『슬픔에 이름 붙이기』가 사전 형식의 책으로 나왔다.', '존 케닉 (지은이), 황유원 (옮긴이)', '윌북', 'https://image.aladin.co.kr/product/33929/13/cover200/k652930139_1.jpg', '국내도서>에세이>외국에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('사피엔스 : 그래픽 히스토리 Vol.3 - 역사의 배후', '역사를 이해하는 새로운 관점을 소개하는 3탄 ‘역사의 배후’ 편이 출간되었다. 원작의 3부 ‘인류의 통합’을 다루는 이번 신간은 역사에는 방향성이 있는지, 있다면 그 경로로 우리를 이끄는 역사의 원리나 법칙은 무엇인지 살펴본다. 역사를 지배하는 배후세력의 정체는 무엇일까?', '유발 하라리 (원작), 다니엘 카사나브 (그림), 김명주 (옮긴이), 다비드 반데르묄렝 (각색)', '김영사', 'https://image.aladin.co.kr/product/33804/6/cover200/8934935731_1.jpg', '국내도서>인문학>교양 인문학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('구의 증명', '사랑하는 연인의 갑작스러운 죽음 이후 겪게 되는 상실과 애도의 과정을 통해 삶의 의미 혹은 죽음의 의미를 되묻는 소설이다. 이 작품에서 최진영은 퇴색하지 않는 사랑의 가치를 전면에 내세우고 아름다운 문장과 감성적이며 애절한 감수성을 통해 젊고 아름다운 남녀의 열정적인 사랑과 냉정한 죽음에 대해 세련된 감성과 탁월한 문체로 담아내고 있다.', '최진영 (지은이)', '은행나무', 'https://image.aladin.co.kr/product/31561/46/cover200/k782832854_1.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이후 한국소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('유희왕 애장판 10', '스고로쿠의 가게 맞은편에 오픈한 최신 게임 가게. 그곳에 오리지널 게임《D·D·D》를 사러 간 유우기는 절도범으로 몰리게 된다. 그리고 가게 안쪽에서 기다리고 있던《D·D·D》의 개발자, 오토기 류지와 천년 퍼즐을 건 대결을 강요받게 되는데...', '다카하시 카즈키 (지은이)', '대원씨아이(만화)', 'https://image.aladin.co.kr/product/34481/74/cover200/k292932547_1.jpg', '국내도서>만화>본격장르만화>판타지>액션 판타지', 1, 1);
