INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('찌그러져도 동그라미입니다 - 김창완 에세이', '한국 대중문화에 가장 독보적인 자취를 남긴 뮤지션 김창완의 에세이가 웅진지식하우스에서 출간된다. 김창완이 라디오 청취자들에게 답한 편지와 오프닝을 엮었다. 손으로 그린 47개의 동그라미 중 두어 개만 그럴듯한 것처럼, 회사생활도 47일 중 이틀이 동그라면 동그란 것이라고 위로한 편지는 SNS와 블로그에 오랫동안 화제가 되었다.', '김창완 (지은이)', '웅진지식하우스', 'https://image.aladin.co.kr/product/33600/15/cover200/890128068x_2.jpg', '국내도서>에세이>명사에세이>방송연예인에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('ETS 토익기출 파트별 VOCA 보카 - 최신개정판 / 교재+단어 암기 어플+단어 시험 출제마법사+MP3 / 최신 기출예문, 기출문제 독점수록', '토익 기본 어휘와 토익 만점 어휘 + LEVEP UP 어휘까지 짱짱한 구성으로 전 레벨 수험자가 시험 대비 필독서로 활용할 수 있게 구성하였다. 전 예문을 실제 토익 시험에 나왔던 문장들로 독점 수록하여 최적의 소스로 실전에 대비할 수 있게 하였다.', 'ETS (지은이)', '(주)YBM(와이비엠)', 'https://image.aladin.co.kr/product/30746/36/cover200/8917237487_1.jpg', '국내도서>외국어>토익>Vocabulary', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('돈의 속성 (300쇄 리커버 에디션, 양장) - 최상위 부자가 말하는 돈에 대한 모든 것', '초판 발행 후, 경제경영 필도서로 자리매김한 『돈의 속성』  200쇄 기념 개정증보판이다. 200쇄에 맞춰 코로나19로 바뀐 경제상황과 돈에 관한 김승호 회장의 추가 메시지를 담았다.', '김승호 (지은이)', '스노우폭스북스', 'https://image.aladin.co.kr/product/28884/59/cover200/s522832546_1.jpg', '국내도서>경제경영>재테크/투자>재테크/투자 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('데미안 (오리지널 초판본 표지디자인) - 최신 원전 완역본', '헤르만 헤세의 ‘영혼의 전기’로 소개되는 《데미안》은 깊이 있는 정신분석과 자기 탐구로 가시밭 같은 자아 성찰의 길을 섬세하게 그려낸 그의 대표작이다. 이 책을 1919년 오리지널 초판본의 우아한 표지로 다시 만나보자.', '헤르만 헤세 (지은이), 이미영 (옮긴이), 김선형 (해설)', '코너스톤', 'https://image.aladin.co.kr/product/9871/8/cover200/k042535550_2.jpg', '국내도서>소설/시/희곡>독일소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('세상의 마지막 기차역 (리커버 에디션)', '우리를 뜨겁게 울린 화제의 소설 ‘세상의 마지막 기차역’ 의 20만 부 기념 리커버 에디션이 출간되었다. 이번 리커버 에디션은 20만 부 판매 돌파를 기념한 것으로, 믿고 보는 일러스트레이터 반지수 작가와의 콜라보레이션을 통해 화사한 봄날을 배경으로 재탄생했다.', '무라세 다케시 (지은이), 김지연 (옮긴이)', '모모', 'https://image.aladin.co.kr/product/31196/62/cover200/k272831051_1.jpg', '국내도서>소설/시/희곡>판타지/환상문학>외국판타지/환상소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2024 큰별쌤 최태성의 별★별한국사 기출 500제 한국사능력검정시험 심화 (1, 2, 3급) - 2024 시험 대비, 68~59회 수록', '2023년 68회 시험부터 2022년 59회 시험까지 최신 기출문제 10회분, 총 500문항으로 구성되었다. ''기출 BOOK''은 실제 시험지를 그대로 구현하여 실전과 같은 문제풀이가 가능하며, ''해설 BOOK''은 모든 선택지의 자세한 해설뿐만 아니라 기출 선택지와 핵심 내용도 수록하였다.', '최태성 (지은이)', '이투스북', 'https://image.aladin.co.kr/product/33213/38/cover200/k392937055_1.jpg', '국내도서>수험서/자격증>한국사능력검정시험>심화(1,2,3급)', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('불안의 서', '포르투갈의 국민작가로 추앙받는 페르난두 페소아가 쓴 &lt;불안의 서&gt;. 짧으면 원고지 2~3매, 길면 20매 분량인 에세이 480여 편이 실려 있다. 어둠, 모호함, 실패, 곤경, 침묵 등을 자신의 헤테로님 베르나르두 소아레스를 통해 노래하고 있다. 소설가 배수아의 완역본.', '페르난두 페소아 (지은이), 배수아 (옮긴이)', '봄날의책', 'https://image.aladin.co.kr/product/3921/29/cover200/899699796x_1.jpg', '국내도서>에세이>외국에세이', 1, 1);
