INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('생의 이면 - 개정판', '이승우의 초기 대표작 『생의 이면』을 각고정려해 문학동네 한국문학전집 제32권으로 선보인다. 제1회 대산문학상 수상작이기도 한 『생의 이면』은 1992년 발간된 이래 끊임없이 쇄를 거듭하며 한국문학의 흔치 않은 스테디셀러로 자리매김했다.', '이승우 (지은이)', '문학동네', 'https://image.aladin.co.kr/product/34554/98/cover200/k012933162_1.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이후 한국소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('코드네임 숏컷', '&lt;코드네임&gt; 시리즈가 완간된 이후 ‘코드네임’을 소재로 한 뮤지컬과 애니메이션으로 아쉬움을 달래고 있을 독자들에게 2024년 여름, 무더위 속의 시원한 소나기처럼 《코드네임 숏컷》이 찾아온다.', '강경수 (지은이)', '시공주니어', 'https://image.aladin.co.kr/product/34452/24/cover200/k552932438_2.jpg', '국내도서>어린이>동화/명작/고전>국내창작동화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('만일 내가 그때 내 말을 들어줬더라면 - 예일대 정신과 나종호 교수의 자기 공감 수업', '예일대학교 정신의학과 나종호 교수가 처음으로 자신의 진솔한 인생 이야기를 고백하며 독자들에게 위로와 공감을 전하는 책. 스스로를 채찍질하는 많은 현대인을 보며 나종호 교수가 ‘이제 자책하는 것은 그만두라’는 위로를 전하는 책이다.', '나종호 (지은이)', '다산북스', 'https://image.aladin.co.kr/product/34361/12/cover200/k932932902_1.jpg', '국내도서>인문학>인문 에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('오렌지와 빵칼', '사회생활 속에서 자기 검열은 일상적으로 일어난다. 가끔은 그것이 자신도 모르게 강화되고, 남을 바라보는 시선 또한 각박해진다. 자신만의 ‘정의’를 내세우며 그것이 ‘선’이라 고집하는 이들에게 작가는 말한다. “너무 단편적으로만 생각하는 거 아닐까?” 이 생각으로부터 이야기가 시작됐다.', '청예 (지은이)', '허블', 'https://image.aladin.co.kr/product/34259/77/cover200/k852932962_1.jpg', '국내도서>소설/시/희곡>과학소설(SF)>한국 과학소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('도둑맞은 집중력 (아이스 에디션) - 집중력 위기의 시대, 삶의 주도권을 되찾는 법', '집중력 부족을 개인 탓, 도파민 탓이라고 생각했던 통념에서 깨어나 사회 시스템의 문제를 인식하자는 제안을 던진 책 《도둑맞은 집중력》이 30만 부 기념 특별 한정판 ‘아이스 에디션’으로 돌아왔다. 집중력 문제를 새롭게 진단하고, 개인을 넘어선 사회적 해결책을 강구하기 위해 폭넓은 탐사를 시작한다.
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('감정의 이해', '롤러코스터를 타듯 우리는 하루하루 수많은 감정을 느끼며 살아간다. 왜 감정은 매일 제멋대로 요동치는 걸까? 이 감정들을 어떻게 다스릴 수 있을까? 『감정의 이해』는 누구나 한 번쯤 해봤을 이런 고민에 대해 해답을 제시한다.', '엠마 헵번 (지은이), 김나연 (옮긴이)', '포레스트북스', 'https://image.aladin.co.kr/product/33799/80/cover200/k052930572_1.jpg', '국내도서>인문학>심리학/정신분석학>교양 심리학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('꼬르륵 꼬르륵 캠핑', '모처럼 쉬는 날이라 멍멍 씨가 캠핑을 왔다. 야옹이들이 팬케이크를 굽는 멍멍 씨를 몰래 엿보고 있다. “캠핑장에서 먹는 아침밥 맛있겠다, 야옹.” 하지만 그냥 구경만 하고 있을 야옹이들이 아니다. 멍멍 씨와 마미가 자리를 비운 사이에 한 입만 한 입만 하면서 팬케이크를 몽땅 먹어치우고 마는데….', '구도 노리코 (지은이), 윤수정 (옮긴이)', '책읽는곰', 'https://image.aladin.co.kr/product/34433/38/cover200/k962932123_1.jpg', '국내도서>유아>그림책>나라별 그림책>일본', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('나에게 신경 쓰기 - 불안과 자책을 멈추고 나를 사랑하기 위한 심리학', '당신이 고민하고 불안해하며 자책하고 있는 문제들이 사실은 당신이 스스로 만들어 낸 문제였다면 믿겠는가? 이런 말을 들으면 대부분 믿기 어려울 것이다. 당신이 가진 문제들의 진짜 원인은 당신의 무의식 속 또 다른 자아인 방어 기제의 작용이다. 이 책은 당신이 처한 문제의 진짜 모습과 당신도 몰랐던 내면의 힘을 알아차릴 수 있도록 도와준다.', '하시모토 쇼타 (지은이), 김슬기 (옮긴이)', '유노북스', 'https://image.aladin.co.kr/product/34385/9/cover200/k142932405_1.jpg', '국내도서>자기계발>힐링>마음 다스리기', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('들풀의 구원 - 부서진 땅에서도 왕성하게 자라난 희망에 관하여', '영국의 시인 빅토리아 베넷의 아름다운 들풀 에세이 『들풀의 구원』이 한국의 독자들을 찾아왔다. 무명 시인인 그가 야생 정원을 가꾸면서 언니의 죽음과 아들의 지병 등 피할 수 없는 상실과 고통을 자연의 생명력으로 바꿔나간 10년의 기록을  선연하게 그려낸 에세이다.', '빅토리아 베넷 (지은이), 김명남 (옮긴이)', '웅진지식하우스', 'https://image.aladin.co.kr/product/34325/32/cover200/8901286378_1.jpg', '국내도서>에세이>외국에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('금빛 종소리 - 김하나의 자유롭고 쾌락적인 고전 읽기', '『말하기를 말하기』, 『당신과 나의 아이디어』, 『힘 빼기의 기술』 김하나 작가가 『금빛 종소리_김하나의 자유롭고 쾌락적인 고전 읽기』를 출간하였다. 김하나 작가는 다섯 작품을 중심으로, 고전 산책 안내자가 되어 ‘자유롭고 쾌락적으로’ 독자와 함께 ‘고전 읽기’ 골목들을 걷는다.', '김하나 (지은이)', '민음사', 'https://image.aladin.co.kr/product/34129/97/cover200/893745677x_2.jpg', '국내도서>에세이>독서에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('해커스 토익 RC Reading(리딩) 기본서 - 최신기출경향 완벽 반영｜실전모의고사 4회분 [시험장에도 들고 가는 토익 RC 필수 어휘 & 단어암기 MP3 제공]', '토익 시험 최신기출경향을 완벽하게 분석하고 반영했다. 최신 토익 시험의 문제 유형과 경향을 철저하게 분석하여 최신 경향을 제시하고, 경향에 맞춘 파트별 공략법을 상세히 정리하여 중점적으로 학습해야 할 내용을 완벽하게 정리할 수 있다.', 'David Cho (지은이)', '해커스어학연구소(Hackers)', 'https://image.aladin.co.kr/product/30640/30/cover200/s432931235_1.jpg', '국내도서>외국어>토익>Reading', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('너는 방과 후 인섬니아 14 - 완결', '‘1월에 있는 대학 공통 시험을 둘이서 본다’ 그 약속을 지키기 위해 입원하기로 결심한 이사키. 이사키가 무사히 돌아오길 기도하는 간타. 그리고 맞이하는 졸업식. 미래를 바라는 두 사람은 일곱 다리 옆에 선다.', '오지로 마코토 (지은이), 오경화 (옮긴이)', '서울미디어코믹스(서울문화사)', 'https://image.aladin.co.kr/product/34527/8/cover200/k202932855_1.jpg', '국내도서>만화>순정만화>틴에이지 순정', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('테러리스트 김구', '김구의 테러 활동을 실증 분석해서 ‘테러리즘 있는 테러(terror with terrorism)’와 ‘테러리즘 없는 테러(terror without terrorism)’를 분별하고, 테러리즘의 구조와 특질, 논리를 밝히고자 한다. 요컨대 세계적인 테러리스트와 대한민국 국부라는 환상적 부조화에 대한 비판적 성찰과 ‘백범 김구’라는 거대 신화의 탈신화에 도전하는 본격적인 학술연구다.', '정안기 (지은이)', '미래사', 'https://image.aladin.co.kr/product/34429/55/cover200/8970871551_1.jpg', '국내도서>사회과학>한국정치사정/정치사>한국정치사정/정치사-일반', 1, 1);
