INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('인공지능은 나의 읽기-쓰기를 어떻게 바꿀까 - 지금 준비해야 할 문해력의 미래', '논의의 중심을 바꿔 인간처럼 읽고 쓴다는 생성형 인공지능을 이제까지와는 다른 관점으로 보기를 제안한다. 생산성과 효율 대신 ‘나’ ‘우리’ ‘인간’ ‘삶’과 같은 단어를 중심으로, 인공지능과 어떤 식으로 관계 맺고 공존하는 것이 가장 인간적이며 효율을 넘어 우리 삶의 유익을 추구하는 쪽인지 고민한다.', '김성우 (지은이)', '유유', 'https://image.aladin.co.kr/product/34385/38/cover200/k382932406_1.jpg', '국내도서>인문학>교양 인문학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('츠츠츠츠', '2020년 출간되어 많은 사랑을 받고 있는 작품, 『이파라파냐무냐무』의 다음을 잇는 이야기로, 이야기의 주 무대는 털숭숭이가 사는 섬이다. 털숭숭이의 집은 어디일까? 궁금했던 독자라면, 이번 신작에서 한층 스펙터클한 이야기와 함께 속속들이 확인할 수 있다.', '이지은 (지은이)', '사계절', 'https://image.aladin.co.kr/product/34215/17/cover200/k482931037_2.jpg', '국내도서>유아>그림책>나라별 그림책>한국 그림책', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('자유로부터의 도피', '&lt;소유냐, 존재냐&gt;, &lt;사랑의 기술&gt; 등을 통해, 수많은 독자를 확보하고 있는 철학자이자 심리학자 에리히 프롬의 주저가 번역가 김석희의 정확하고 유려한 번역으로 새로 태어났다.', '에리히 프롬 (지은이), 김석희 (옮긴이)', '휴머니스트', 'https://image.aladin.co.kr/product/25011/15/cover200/k892632723_1.jpg', '국내도서>인문학>심리학/정신분석학>정신분석학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('해독 혁명 - 질병 없는 몸을 위한 5단계 독소 해방', '피부과 전문의이자 미국 IFM 기능의학 인증의인 저자가 제안하는 체내 독소 배출 솔루션을 담고 있다. 먼저 저자는 우리 몸에 내재되어 있는 간, 담즙, 위, 장내세균, 세포로 이어지는 우리 몸에서 일어나는 독소 배출의 경로를 설명하며 독소들이 우리 몸의 어떤 부분에서 어떻게 처리되어 어디를 통해 나가는지 보여준다.', '닥터 라이블리(최지영) (지은이)', '웅진지식하우스', 'https://image.aladin.co.kr/product/34206/77/cover200/8901285142_1.jpg', '국내도서>건강/취미>건강정보>건강에세이/건강정보', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('대화의 힘 - 원하는 모든 것을 얻어내는 최고의 기술', '퓰리처상 수상 저널리스트이자 미국 최고의 논픽션 작가 찰스 두히그가 8년 만에 펴낸 신작이다. 습관의 매커니즘을 풀기 위해 스스로 나쁜 습관 끊어내기 실험을 했던 저자는 이번에는 탁월한 슈퍼 커뮤니케이터를 찾아내 그들의 능력을 낱낱이 해부한다.', '찰스 두히그 (지은이), 조은영 (옮긴이)', '갤리온', 'https://image.aladin.co.kr/product/34173/51/cover200/890128510x_1.jpg', '국내도서>자기계발>성공>성공학', 1, 1);
