INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('ETS 토익 단기 공략 750+ (LC + RC) - 기출문제 한국 독점출간 / 기출 문항으로 보강된 단기완성 시리즈 / 본책 + 해설집 + 실전 모의고사 1회분 + 무료 MP3 파일', '출제기관 ETS에서 독점 제공하는 토익 단기공략 시리즈 중 하나로, 750점 이상을 목표로 하는 수험자들을 위한 교재다. 최신경향을 분석하여 전략을 제시하고, 엄선된 기출문제 및 ETS 개발문제를 수록하여 실전에 확실히 대비할 수 있도록 하였다.', 'ETS (지은이)', '(주)YBM(와이비엠)', 'https://image.aladin.co.kr/product/24919/64/cover200/8917235948_1.jpg', '국내도서>외국어>토익>실전', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('적당히 잊어버려도 좋은 나이입니다 - 도쿄의대 노년내과 의사가 알려주는 인생 후반을 위한 현실 조언', '나이 들어 깜빡깜빡하는 일이 잦아지면 의기소침해지기 마련이다. ‘내가 벌써 이렇게 됐나’ 싶고, 지금부터라도 기억력을 향상시켜야 하는 건 아닌지 불안해진다. 그러나 도쿄의대 출신 노년내과 전문의이자 75세 현역 의사인 저자는 ‘잊는 힘’이야말로 인생 후반에 꼭 필요한 능력이라고 말한다.', '가마타 미노루 (지은이), 지소연 (옮긴이)', '더퀘스트', 'https://image.aladin.co.kr/product/34168/13/cover200/k562931915_1.jpg', '국내도서>자기계발>중년의 자기계발', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('괴수 8호 5', '결국 방위대에 구속된 히비노 카프카. 그리고 방위대 사상 최강이라 칭송받으며, 대괴수 2호의 힘을 지닌 시노미야 장관에 의해 카프카의 처분이 시작된다. 자신이 ‘사람’이라 주장하는 카프카는 장관에게 맞서 싸우지만, 괴수 8호의 신체를 제어할 수 없게 되는데…?!', '마츠모토 나오야 (지은이), 유유리 (옮긴이)', '서울미디어코믹스(서울문화사)', 'https://image.aladin.co.kr/product/33923/55/cover200/k162930937_1.jpg', '국내도서>만화>소년만화>액션', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('열다섯에 곰이라니', '&lt;벙커&gt;, &lt;내 이름은 망고&gt; 등으로 독자들에게 오래도록 사랑받고 있는 추정경 작가의 작품이다. &lt;열다섯에 곰이라니&gt;는 정체불명의 현상으로 갑작스럽게 동물이 되어버린 아이들의 우여곡절 성장기를 담은 작품이다.', '추정경 (지은이)', '다산책방', 'https://image.aladin.co.kr/product/30723/39/cover200/k272830341_1.jpg', '국내도서>청소년>청소년 문학>청소년 소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('더 그레이트 1', '면접에 지각한 인생 최악의 날, 자신의 일생을 뒤흔들 남자와 만나 사랑에 빠진 후, 평범하지만 위대한 일생을 살아가게 되는 여자 유보라의 일대기를 그린 작품이다. 전(前) 세대의 여성, 이 세대의 어머니, 다음 세대의 어른으로 깊이 각인될 그녀의 삶은, 독자들에게 향기로 다가가 눈물샘을 자극한다.', '광진 (지은이), 지민 (그림)', '대원씨아이(만화)', 'https://image.aladin.co.kr/product/34526/32/cover200/k012932853_1.jpg', '국내도서>만화>인터넷 연재 만화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('나는 도대체 왜 피곤할까 - 이 죽일 놈의 피로와 결별하는 법', '피로가 내 삶을 지배하게 둘 수는 없다. 하버드, 컬럼비아, 코넬, 알베르트아인슈타인 면역학/알레르기 전문가인 ‘에이미 샤’는 삶의 활력을 불어넣기 위해 과학적 통찰로 무장한 근본적인 해결책을 제시한다.', '에이미 샤 (지은이), 김잔디 (옮긴이)', '북플레저', 'https://image.aladin.co.kr/product/34208/30/cover200/k242931835_1.jpg', '국내도서>자기계발>힐링>건강한 몸', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('괴수 8호 9', '사상 처음으로 6호의 적합자가 되어 훈련하는 이치카와 레노. 그리고 카프카는 호시나로부터 대식 격투술을 배워 노력을 이어나간다. 방위대 역시 9호 재해를 예측해  각 부대가 준비에 들어간다. 한편, 나루미는 키코루에게 어머니의 유품인 식별 괴수 병기 4호의 사용 지시를 내리는데?', '마츠모토 나오야 (지은이), 유유리 (옮긴이)', '서울미디어코믹스(서울문화사)', 'https://image.aladin.co.kr/product/31560/35/cover200/k662832851_1.jpg', '국내도서>만화>소년만화>액션', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('맡겨진 소녀', '2009년 데이비 번스 문학상을 수상한 작품으로, 애정 없는 부모로부터 낯선 친척 집에 맡겨진 한 소녀의 이야기를 그린다. 이 책을 원작으로 하는 영화 「말없는 소녀」 또한 세계 관객들의 열렬한 호평을 받으며 올해 5월 31일 국내 개봉을 앞두고 있다.', '클레어 키건 (지은이), 허진 (옮긴이)', '다산책방', 'https://image.aladin.co.kr/product/31555/26/cover200/k832832758_1.jpg', '국내도서>소설/시/희곡>세계의 소설>아일랜드소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('히브리어의 시간', '히브리어를 통해 성경의 깊은 맛을 음미하는 시간이면서, 동시에 히브리어가 쓰이던 성경의 시간을 뜻한다. 하나님이 히브리어로 말씀하시는 이유는 간단하다. 이 언어로 말씀하셔야 성경 시대에 살던 사람들이 가장 잘 이해할 수 있었기 때문이다.', '송민원 (지은이)', '복있는사람', 'https://image.aladin.co.kr/product/34385/13/cover200/k242932405_1.jpg', '국내도서>종교/역학>기독교(개신교)>기독교(개신교) 목회/신학>신학일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('하나님의 DNA', '하나님을 닮은 하나님의 자녀의 정체성을 되찾을 뿐만 아니라 그에 따른 놀라운 능력과 권세 또한 회복시켜주기 원하시는 하나님 아버지의 마음이 담겼다.', '최상훈 (지은이)', '규장(규장문화사)', 'https://image.aladin.co.kr/product/34335/66/cover200/k892932796_1.jpg', '국내도서>종교/역학>기독교(개신교)>기독교(개신교) 목회/신학>신학일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('괴수 8호 11', '9호가 만들어낸 다섯 마리의 식별 클래스 괴수가 동방 사단 에어리어에 집결했다. 방위대의 주력을 말살하기 위해 설계된 식별 괴수를 상대로 고전을 면치 못하는 각 대장. 그 위협이 시노노메 소대장에게까지 미치는데….', '마츠모토 나오야 (지은이), 유유리 (옮긴이)', '서울미디어코믹스(서울문화사)', 'https://image.aladin.co.kr/product/33188/12/cover200/k732937531_1.jpg', '국내도서>만화>소년만화>액션', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('괴수 8호 8', '식별 괴수 병기를 완벽하게 다루기 위해 훈련을 이어가는 이치카와 레노. 드디어 훈련 성과를 보이기 위한 괴수 토벌 테스트가 시작됐다. 한편, 레노의 성장을 목도한 이하루는 성장하지 않는 자신의 성적에 고뇌하며 복잡한 마음을 품고 있었다.', '마츠모토 나오야 (지은이), 유유리 (옮긴이)', '서울미디어코믹스(서울문화사)', 'https://image.aladin.co.kr/product/30695/41/cover200/k692830840_1.jpg', '국내도서>만화>소년만화>액션', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('나와 너 - 개정판', '세계적인 교양서이자 우리 시대의 살아 있는 고전. 인간 본질에 대한 통찰로 신학뿐만 아니라 문화 전반에 걸쳐 많은 영향을 끼친 마르틴 부버의 대표작이다. 부버는 이 책에서 세상에는 ''나와 너''(Ich-Du)의 관계와 ''나와 그것''(Ich-Es)의 관계가 존재하는데, 참다운 삶을 살기 위해서는 ''나와 너''의 관계를 맺어야 한다고 주장한다.', '마르틴 부버 (지은이), 김천배 (옮긴이)', '대한기독교서회', 'https://image.aladin.co.kr/product/23654/41/cover200/8951119920_1.jpg', '국내도서>종교/역학>기독교(개신교)>기독교(개신교) 신앙생활>신앙생활일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('어떻게 극단적 소수가 다수를 지배하는가 - 우리의 민주주의가 한계에 도달한 이유', '정치 분야 최장기 스테디셀러 《어떻게 민주주의는 무너지는가》 후속작. 하버드대 정치학자 스티븐 레비츠키와 대니얼 지블랫이 극단적 사상을 가진 소수가 상식적 다수를 지배하게 되는 현대 민주주의 체제의 한계를 분석한다.', '스티븐 레비츠키, 대니얼 지블랫 (지은이), 박세연 (옮긴이)', '어크로스', 'https://image.aladin.co.kr/product/33934/72/cover200/k402930238_1.jpg', '국내도서>사회과학>정치학/외교학/행정학>정치학 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('온 더 볼 4 - 슈퍼 세이브', '‘골키퍼는 실점을 막는 것이 아니라 팀의 패배를 막는 것’이라는 문장이 있을 정도로 마지막까지 팀의 승패를 좌우하는 선수가 바로 ‘골키퍼’이다. 그동안 주목받지 못했던 골키퍼에 대한 이야기를 4권에서 적극적으로 드러냄으로써 골키퍼의 중요성과 매력을 보여 주고자 했다.', '성완 (지은이), 돌만 (그림)', '다산어린이', 'https://image.aladin.co.kr/product/34535/53/cover200/k582932956_2.jpg', '국내도서>어린이>동화/명작/고전>국내창작동화', 1, 1);
