INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('의사 어벤저스 19 : 근육 질환, 더 단단해져라! - 어린이 의학 동화', '‘의사 어벤저스’ 시리즈는 어린이 버전 메디컬 스토리다. 어린이만 진료하는 어린이 종합 병원이라는 설정을 통해, 어린이 의사에 대한 나이나 사회적 편견 등의 한계를 극복하고, 어린이 의사라는 장점을 살려 어린이 환자의 마음, 고민, 아픔 등을 더 잘 이해하는 따뜻한 공감을 보여 준다.', '고희정 (지은이), 조승연 (그림), 류정민 (감수)', '가나출판사', 'https://image.aladin.co.kr/product/34474/29/cover200/k582932632_1.jpg', '국내도서>어린이>동화/명작/고전>국내창작동화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('바빌론 부자들의 돈 버는 지혜 - 부의 본질을 꿰뚫는 7가지 비결과 통찰 질문 152', '누구나 부자가 되고 싶어 하지만, 정작 그 방법을 아는 사람은 많지 않다. 고대 바빌론의 현자들은 이미 부를 얻는 비결을 알고 있었다. 그들의 지혜를 담은 『바빌론 부자들의 돈 버는 지혜』는 시대를 초월한 부의 원칙을 명쾌하게 제시한다.', '조지 S. 클레이슨 (지은이), 이선주 (옮긴이)', '현대지성', 'https://image.aladin.co.kr/product/34367/16/cover200/k552932005_1.jpg', '국내도서>경제경영>재테크/투자>재테크/투자 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('일본의 굴레 - 헤이안 시대에서 아베 정권까지, 타인의 눈으로 안에서 통찰해낸 일본의 빛과 그늘', '여기 태가트 머피라는 미국인이 쓴 &lt;일본의 굴레&gt;라는 두툼한 인문서가 있다. 부제가 독특하다. "타인의 눈으로 안에서 통찰해낸 일본의 빛과 그늘"이란 말은 이 책의 정체성을 가장 잘 드러내준다. 이 책의 저자는 국제정치경제 전문가인 미국인이다.', 'R. 태가트 머피 (지은이), 윤영수, 박경환 (옮긴이)', '글항아리', 'https://image.aladin.co.kr/product/26370/16/cover200/8967358628_2.jpg', '국내도서>역사>일본사>일본사 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('따뜻한 여사의 5분 컷 전자레인지 레시피 100 - 버튼 한 번으로 끝내는 요리', '저자는 일과 육아, 살림을 병행하기 위해 최대한 본인이 편안해질 수 있는 효율적인 전자레인지 요리법을 찾았다. 그리고 그 노하우를 그녀의 첫 번째 책 《따뜻한 여사의 5분 컷 전자레인지 레시피 100》에 녹여 냈다.', '김수림 (지은이)', '싸이프레스', 'https://image.aladin.co.kr/product/34366/33/cover200/k812932002_1.jpg', '국내도서>요리/살림>생활요리', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('빨간내복야코 맞춤법 절대 안 틀리는 책 2', '귀여운 캐릭터와 중독성 높은 노래로 107만 구독자의 사랑을 받고 있는 빨간내복야코의 어린이 맞춤법 교양 툰 &lt;빨간내복야코 맞춤법 절대 안 틀리는 책&gt;이 2권으로 더 강력하게 돌아왔다. 맞춤법 강박증 야코와 맞춤법 파괴범 사동이의 톡톡 튀는 카톡 대화를 통해 초등 교과서 속 필수 맞춤법을 자연스럽게 익힌다.', '빨간내복야코 (원작), 박종은 (글), 이영아 (그림), 샌드박스네트워크 (감수)', '위즈덤하우스', 'https://image.aladin.co.kr/product/34313/25/cover200/k822932470_1.jpg', '국내도서>어린이>책읽기/글쓰기>글쓰기 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('사이먼 가라사대, 우리는 모두 별이다 - 2024 뉴베리 아너상', '100년 넘는 전통을 가진 어린이 문학상, 뉴베리상과 장애에 대한 이해를 예술적으로 승화한 작품에 수여하는 슈나이더 패밀리 북을 동시에 수상한 주니어 소설이다.', '에린 보우 (지은이), 천미나 (옮긴이)', '밝은미래', 'https://image.aladin.co.kr/product/34469/53/cover200/8965467004_1.jpg', '국내도서>어린이>동화/명작/고전>외국창작동화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('나의 돈키호테', '150만 독자가 애독하며 수만 개의 입소문 리뷰를 탄생시킨 국민 힐링소설 『불편한 편의점』의 작가 김호연이 소설 『나의 돈키호테』로 돌아왔다. 이제는 사라진 옛날 동네 비디오 가게에서 시작되는 꿈과 모험의 여정을 담은 소설이다.', '김호연 (지은이)', '나무옆의자', 'https://image.aladin.co.kr/product/33798/94/cover200/k852930477_2.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이후 한국소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('JLPT 일본어능력시험 한권으로 끝내기 N3 - 최신 개정판', '시험에 완벽하게 대응할 수 있도록 실제 시험 문제와 같은 형식인 1교시 언어지식(문자·어휘·문법)·독해, 2교시 청해 순으로 구성되었다. 이번 개정판에서는 2010년부터 2021년까지 실제 시험에서 출제된 어휘와 문법을 연도별로 실었으며, 문제 유형을 철저히 분석.반영하였다.', '이치우, 기타지마 치즈코 (지은이)', '다락원', 'https://image.aladin.co.kr/product/28087/6/cover200/8927712439_3.jpg', '국내도서>외국어>일본어>JLPT>N3', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('데일 카네기 인간관계론 (무삭제 완역본)', '가장 단순한 원칙들로 복잡한 인간관계에 대한 우리의 고민을 명쾌하게 해결해 준다. 사람을 다루는 기본적인 방법, 사람들이 당신을 좋아하도록 만드는 방법, 사람들을 설득하는 방법, 반발 없이 상대를 변화시키는 방법까지, 실생활에 적용할 수 있는 다양한 원칙을 풍부한 사례와 함께 제시한다.', '데일 카네기 (지은이), 임상훈 (옮긴이)', '현대지성', 'https://image.aladin.co.kr/product/20945/79/cover200/s952638241_3.jpg', '국내도서>자기계발>인간관계>인간관계', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('침묵의 세계 - 개정3판', '''침묵''에 관한 깊고 넓은 사유. 막스 피카르트는 ''침묵''이 우리의 삶과 정신과 언어살이에 어떤 식으로 기능하며 어떤 식으로 내재하는가에 대해 단정하게 이야기하고 있다. 막스 피카르트에게 침묵은 수동적이고 말하기를 멈추는 행위가 아니라 능동적이고 말의 포기 그 이상의 의미를 가진 것이다. ''침묵''에 관한 성찰과 사유의 산문집.', '막스 피카르트 (지은이), 최승자 (옮긴이)', '까치', 'https://image.aladin.co.kr/product/749/82/cover200/8972914673_1.jpg', '국내도서>인문학>철학 일반>교양 철학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('내 인생 최악의 일주일 1 - 월요일', '소심하지만 사랑스러운 6학년 소년 저스틴의 인생에서 가장 끔찍하게 웃기는 한 주를 담은 초등 동화 시리즈의 첫 권, 『내 인생 최악의 일주일 1 : 월요일』이 출간되었다. 호주와 뉴질랜드에서만 약 20만 부가 판매되었고 전 세계 34개 언어 50여 개국에 수출된 베스트셀러 시리즈다.', '이바 이모리스, 맷 코스그로브 (지은이), 김영진 (옮긴이)', '비룡소', 'https://image.aladin.co.kr/product/34534/61/cover200/8949144514_1.jpg', '국내도서>어린이>동화/명작/고전>외국창작동화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('마음의 온도는 몇 도일까요? (스페셜 에디션) - 그림 시집', '제23회 우체국 예금 보험 어린이 글짓기 대상 수상자이자 SBS [영재 발굴단]에서 문학 영재로 소개된 정여민이 쓴 《마음의 온도는 몇 도일까요?》 스페셜 에디션이다. 2016년에 출간되어 많은 사랑을 받아온 시집에 미발표 시 &lt;자작나무 가는 길&gt;과 이제 어엿한 성인이 된 저자가 시를 사랑해 주신 독자들을 위해 쓴 서문이 새롭게 수록되었다.', '정여민 (지은이), 허구 (그림)', '김영사', 'https://image.aladin.co.kr/product/34469/24/cover200/8934910550_1.jpg', '국내도서>어린이>초등 전학년>동시/동요', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('하루 한 장 초등 경제 신문 - 문해력과 경제 상식을 동시에 키워주는', '매일경제 경제경영연구소에서 개최하는 ‘매경&#8231;퍼핀 주니어 경제금융 TEST’의 출제위원이기도 한 선생님들이 엄선해서 쉽게 풀어 쓴 100가지 최신·최고급 기사들로 구성되어 있으며, 초등학생들이 알아야 할 필수 경제 어휘, 기사의 주제를 파악하는 핵심 질문, 기사와 관련한 요즘 뜨는 경제 이슈와 상식, 메타인지 향상을 위한 심화 지식 문제들이 담겨 있다.', '윤지선, 김선 (지은이), 퍼핀 (감수)', '매경주니어북스', 'https://image.aladin.co.kr/product/34173/67/cover200/k462931111_1.jpg', '국내도서>어린이>사회/역사/철학>정치/경제/법', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('왕구리네 떡집', '밀리언 셀러를 넘어 누적 판매 160만 부를 돌파한 「만복이네 떡집」은 제각기 결핍과 고민을 품은 아이들이 이야기마다 새로운 주인공으로 등장하며, 꼬랑지가 만들어 내는 세상에 하나밖에 없는 맞춤형 소원 떡을 통해 아이들의 웃음을 되찾아 주는 선한 판타지 동화다. 열 번째 이야기의 주인공은 꼬랑지를 도와 열심히 떡집 운영을 돕던 왕구리다.', '김리리 (지은이), 김이랑 (그림)', '비룡소', 'https://image.aladin.co.kr/product/34108/48/cover200/8949162601_1.jpg', '국내도서>어린이>동화/명작/고전>국내창작동화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('해커스 토익 LC Listening(리스닝) 기본서 - 최신기출경향 완벽 반영｜실전모의고사 4회분 [시험장에도 들고 가는 토익 LC 필수 어휘 & 단어암기 MP3 제공]', '파트별 최신 출제 경향과 문제 풀이 방법을 확인할 수 있는 구성을 매 영역마다 제공하고, 자주 틀리는 오답패턴을 수록하여 실수 없이 Part 1, 2 점수 향상이 가능하다. 시각 자료, 의도 파악 문제 등 토익 최신 출제 경향을 완벽하게 반영하였다.', 'David Cho (지은이)', '해커스어학연구소(Hackers)', 'https://image.aladin.co.kr/product/30640/55/cover200/s182931386_1.jpg', '국내도서>외국어>토익>Listening', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('죽음의 수용소에서 - 개정보급판', '20세기를 대표하는 사상가이자 정신 의학자인 빅터 프랭클의 자전적인 에세이. 나치 강제 수용소에서 겪은 참혹한 고통을 건조하고 담담한 시선으로 술회한다.', '빅터 프랭클 (지은이), 이시형 (옮긴이)', '청아출판사', 'https://image.aladin.co.kr/product/24212/1/cover200/8936811541_2.jpg', '국내도서>인문학>심리학/정신분석학>이론 심리학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('인간 실격', '자살 미수와 약물 중독, 39세의 젊은 나이에 자살로 생을 마감한 다자이 오사무의 작품 <인간 실격>이 출간됐다. 오직 순수함만을 갈망하는 한 젊은이가 파멸해가는 과정이 그려진다. 뉴욕 타임스는 ''인간의 나약함을 드러내는 데 있어 다자이보다 뛰어난 작가는 드물다''고 평했다.', '다자이 오사무 (지은이), 김춘미 (옮긴이)', '민음사', 'https://image.aladin.co.kr/product/49/16/cover200/893746103x_3.jpg', '국내도서>소설/시/희곡>일본소설>1950년대 이전 일본소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('열다섯에 곰이라니 2', '‘사춘기의 동물화’라는 재미난 설정의 성장소설 『열다섯에 곰이라니』는 청소년들뿐만 아니라 길고도 어두운 사춘기의 터널을 함께 지나는 부모님과 선생님 들에게도 많은 사랑을 받은 작품이다. 2024년 부산시 올해의 책 선정을 비롯해 여러 기관의 추천 도서로 선정되며 작품성까지 인정받은 『열다섯에 곰이라니』가 출간 2년 반만에 그 두 번째 이야기로 돌아왔다.', '추정경 (지은이)', '다산책방', 'https://image.aladin.co.kr/product/34476/18/cover200/k532932636_1.jpg', '국내도서>청소년>청소년 문학>청소년 소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2025 문동균 한국사 기출은 문동균', '2024년 최근 시행된 국가직·지방직 등 주요 공무원 시험의 기출 문제와 최신 출제 경향에 따라 핵심 기출만 엄선한 1,000제를 수록한 기출문제집이다. 주제별로 대표 기출 문제를 먼저 배치하여 최신 출제 경향을 제시하였다.', '문동균 (지은이)', '에스티유니타스', 'https://image.aladin.co.kr/product/34288/64/cover200/k252932172_1.jpg', '국내도서>수험서/자격증>공무원 수험서>7/9급 공무원>한국사>기출/문제집/모의고사', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('제철 행복 - 가장 알맞은 시절에 건네는 스물네 번의 다정한 안부', '세상에 행복이란 게 존재한다면 잠시 머무는 이 계절의 모습을 하고 있을 것이다. 지금 곁에 와 손짓하고 있지만 무심코 지나쳐버리기 쉽기에 알맞은 시절에 챙겨야 하는 작은 기쁨들, 이 책은 바로 그 ''제철 행복''에 대한 이야기다.', '김신지 (지은이)', '인플루엔셜(주)', 'https://image.aladin.co.kr/product/33842/43/cover200/k982930091_1.jpg', '국내도서>에세이>한국에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('파친코 (합본 한정판)', '한 세기에 걸친 재일조선인 가족의 이야기를 담은 세계적 베스트셀러, 이민진 장편소설 《파친코》가 합본 한정판으로 선보인다. 《파친코》는 2017년 전미도서상 최종 후보, 《뉴욕타임스》 베스트셀러 등 75개 이상의 주요 해외 매체의 ‘올해의 책’에 이름을 올리며 세계적인 화제작으로 떠올랐다.', '이민진 (지은이), 신승미 (옮긴이)', '인플루엔셜(주)', 'https://image.aladin.co.kr/product/33047/39/cover200/k952937890_1.jpg', '국내도서>소설/시/희곡>영미소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('괴수 8호 7', '방위대 장관 시노미야 이사오가 가진 괴수 2호의 힘을 빼앗으러 온 괴수 9호. 사투 끝에 이사오는 9호를 몰아붙이지만, 결국엔 힘이 다해 2호의 힘을 빼앗기고 만다. 그리고 장관을 쓰러트리고 융합한 괴수 9호가 현장에 도착한 카프카와 키코루, 나루미를 상대로 전투를 벌이는데…?!', '마츠모토 나오야 (지은이), 유유리 (옮긴이)', '서울미디어코믹스(서울문화사)', 'https://image.aladin.co.kr/product/29922/42/cover200/k042838532_1.jpg', '국내도서>만화>소년만화>액션', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('황천의 츠가이 6 (특별판) - 일러스트 포스트 카드 + 소책자 + 특별판 한정 부록', '히가시무라에 홀로 침입해서, 한 소녀와 가짜 아사를 유괴한 이반은 유르에게 전언을 남긴다. 그것은 인질을 내세워 유르를 불러내는 전언이었다. 유르는 두사람을 구출하기 위해 지정된 장소 근처에서 잠복하지만, 소리도 없이 나타난 츠가이에게 납치당하고 마는데...', '아라카와 히로무 (지은이)', '대원씨아이(만화)', 'https://image.aladin.co.kr/product/34526/39/cover200/k332932853_1.jpg', '국내도서>만화>본격장르만화>추리/미스터리', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('바람공방의 페어아일 니팅 - 기초부터 스틱 기법까지 페어아일의 모든 것', '뜨개의 대가, 바람공방이 작품 예시를 통해 페어아일 뜨는 법을 차근차근 알려준다. 작품을 만드는 모든 과정을 사진과 자세한 설명으로 알려준 후, 배운 기법으로 뜰 수 있는 작품들을 소개한다.', '바람공방 (지은이), 배혜영 (옮긴이)', '한즈미디어(한스미디어)', 'https://image.aladin.co.kr/product/34499/90/cover200/k982932752_1.jpg', '국내도서>건강/취미>공예>뜨개질/퀼트/십자수/바느질', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('괴수 8호 : 밀착! 제3부대 - 소설', '방위대 제3부대에 다큐멘터리를 찍기 위한 밀착 취재 요청이 들어온다. 카프카와 레노가 도전한 가혹한 장애물 경주란? 키코루가 받은 환상의 전용 무기와 호시나의 과거도 밝혀지는데……. 그리고 카프카는 취재팀 앞에서 끝까지 정체를 숨길 수 있을 것인가. 총 4편이 수록된 첫 소설판.', '마츠모토 나오야 (지은이), 유유리 (옮긴이)', '서울미디어코믹스(서울문화사)', 'https://image.aladin.co.kr/product/34199/20/cover200/k392931519_1.jpg', '국내도서>소설/시/희곡>라이트 노벨>기타 라이트노벨', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('우리는 모두 죽는다는 것을 기억하라 (알라딘 단독 리커버)', '웨인 다이어 박사가 이 세상에 마지막으로 남기고 간 작품이다. 삶의 현자들로 불리는 작가, 철학자, 영성가, 명성가 등등 다양한 인물들의 뜨거운 목소리가 담겨 있다. 아울러 삶의 다른 답, 다른 방식을 찾아낸 사람들의 따뜻하고 유쾌한 이야기가 담겨 있다.', '웨인 다이어 (지은이), 정지현 (옮긴이)', '토네이도', 'https://image.aladin.co.kr/product/34545/1/cover200/k692933964_1.jpg', '국내도서>자기계발>성공>성공학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2025 써니 행정법총론 핵심집약 (기출지문 암기 App 이용쿠폰 제공) - ''이론, 기출, 키워드''로 정리하는 단권화 요약서', '핵심 이론, 기출○×, 키워드를 1:1로 매칭하여 효과적으로 압축 정리한 수험서다. 콤팩트한 ‘이론편’ & 워크북 형태의 ‘문제편’을 분리하여 자신의 학습 상황에 맞게 활용할 수 있다. 다양한 학습 비기와 학습 프로그램을 활용해 전략적으로 학습할 수 있도록 구성하였다.', '박준철 (지은이)', '지금(도서출판)', 'https://image.aladin.co.kr/product/34337/32/cover200/k822932899_1.jpg', '국내도서>수험서/자격증>공무원 수험서>7/9급 공무원>행정법>7/9급 교재', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('탕비실', '150만 독자가 사랑한 《달러구트 꿈 백화점》 이미예 작가가 이번에는 《탕비실》로 독자들을 찾았다. 여러 직장에서 ‘탕비실 빌런’으로 꼽힌 사람들을 한데 모은 7일간의 리얼리티 쇼를 배경으로 한 이 작품은 쇼의 재미는 물론 인간 심리를 깊이 있게 분석해 두 마리 토끼를 잡았다.', '이미예 (지은이)', '한끼', 'https://image.aladin.co.kr/product/34193/41/cover200/k602931315_1.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이후 한국소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('악마의 영문법 100법칙 - 읽으면서 이해하고 암기 필요없는', '영어를 쓰고 말하기 위해서 어떤 관점으로 영문법을 접하면 좋을 지에 따라 우선순위를 두어 설명했다. 이러한 문법 해설을 읽으면, 쓰고 말하는 실력은 물론이고 읽기 실력도 향상될 수 있다.', '도키요시 히데야 (지은이), 김의정 (옮긴이)', '더북에듀', 'https://image.aladin.co.kr/product/34030/43/cover200/k562931161_1.jpg', '국내도서>외국어>영문법', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('흐르는 강물처럼', '자신이 아무것도 아닌 줄만 알았던 열일곱 살 소녀가 사랑의 환희와 상실의 고통을 온몸으로 감내하는 번데기 시절을 거쳐 비로소 나비가 되는 이야기다. 뒤돌아보지 않는 자연에서 배운, 거스를 수 없는 회복력으로 살아내는 주인공은 끝내 누구도 빼앗을 수 없는 결실을 거머쥔다.', '셸리 리드 (지은이), 김보람 (옮긴이)', '다산책방', 'https://image.aladin.co.kr/product/33161/91/cover200/k622937235_2.jpg', '국내도서>소설/시/희곡>영미소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('룩 백', '자신의 재능에 절대적인 자신감이 있는 후지노와 은둔형 외톨이인 쿄모토. 시골 마을에 사는 두 소녀를 서로에게 이끌어서 묶어 준 것은 만화 그리기를 향한 한결같은 마음이었다. 시간은 흘러도 등 뒤를 든든히 받쳐 준 것은 언제나….', '후지모토 타츠키 (지은이)', '학산문화사(만화)', 'https://image.aladin.co.kr/product/29088/67/cover200/k932837765_1.jpg', '국내도서>만화>소년만화>청소년 성장', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('더 그레이트 2', '면접에 지각한 인생 최악의 날, 자신의 일생을 뒤흔들 남자와 만나 사랑에 빠진 후, 평범하지만 위대한 일생을 살아가게 되는 여자 유보라의 일대기를 그린 작품이다. 전(前) 세대의 여성, 이 세대의 어머니, 다음 세대의 어른으로 깊이 각인될 그녀의 삶은, 독자들에게 향기로 다가가 눈물샘을 자극한다.', '광진 (지은이), 지민 (그림)', '대원씨아이(만화)', 'https://image.aladin.co.kr/product/34526/35/cover200/k112932853_1.jpg', '국내도서>만화>인터넷 연재 만화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('바닷가의 루시 - 루시 바턴 시리즈', '인간의 복잡한 내면세계를 예리하게 포착해, 가장 명징한 문장으로 감정의 본질을 증류해내는 작가 엘리자베스 스트라우트의 신작이 문학동네에서 출간되었다. 2022년 부커상 최종후보에 오른 『오, 윌리엄!』의 후속작이자 ‘루시 바턴’ 시리즈의 최신작인 『바닷가의 루시』는 루시와 첫 남편 윌리엄이 세상을 집어삼킨 바이러스를 피해 한적한 바닷가의 집으로 가게 되면서 일어난 일을 그린 장편소설이다.', '엘리자베스 스트라우트 (지은이), 정연희 (옮긴이)', '문학동네', 'https://image.aladin.co.kr/product/34435/76/cover200/k812932228_1.jpg', '국내도서>소설/시/희곡>영미소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('승승 형제 택배 소동', '마음 잇는 아이 19권. 호기심을 자극하는 소재, 택배로 아파트 사람들을 조명한 『승승 형제 택배 소동』은 오늘날 아파트에 사는 수많은 사람들에게‘함께 사는 삶’을 생각하게 하는 좋은 이웃 같은 동화이다.', '박현정 (지은이), 임광희 (그림)', '마음이음', 'https://image.aladin.co.kr/product/32420/21/cover200/k182935993_1.jpg', '국내도서>어린이>동화/명작/고전>국내창작동화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('진짜 쓰는 실무 엑셀 - 유튜브 대표 엑셀 채널, 오빠두가 알려 주는 엑셀 함수, 보고서 작성, 데이터 분석 노하우!', '대기업 직장 생활 10년의 실무 노하우와 엑셀 유튜브 채널을 운영하면서 들은 수많은 직장인의 고민을 해결할 수 있는 다양한 엑셀 비법을 담았다. ''진짜 쓰는 실무 엑셀'' 한 권이면 빠른 일 처리로 워라밸을 실현하고, 일잘러로 거듭날 수 있다!', '오빠두(전진권) (지은이)', '제이펍', 'https://image.aladin.co.kr/product/28819/83/cover200/s292938843_1.jpg', '국내도서>컴퓨터/모바일>오피스(엑셀/파워포인트)>엑셀', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('구움과자의 기술 - 당신의 5년을 절약해 줄', '구움과자로 많은 사랑을 받고 있는 ‘과자방’에서 실제로 판매했거나 판매중인 과자 가운데 소비자들의 검증을 거친 제품을 엄선해 실은 책이다. 그러나 단순히 숫자로 익히는 레시피 북은 아니다. 이름 그대로 매일 생산하는 제품을 균일하게 만들 수 있는 ‘기술’에 관한 책이다.', '정용현, 강현지 (지은이)', '비앤씨월드', 'https://image.aladin.co.kr/product/34555/79/cover200/k192933163_1.jpg', '국내도서>요리/살림>제과제빵', 1, 1);
