INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('나 홀로 도쿄 자취방 도감 - 일러스트 + 코믹스', '스미다가와 강변에 있는 빈티지 맨션에 사는 마리 상, 쵸후 역과 가깝고 채소밭에 둘러싸인 주택가에서 자취하는 유코 상, 고슈 가도변에 있는 자동차 소리가 시끄러운 다세대 주택에서 사는 나츠 상 등등 현실에서 튀어나온 듯한 11명의 캐릭터들이 자신의 자취방을 소개하고 최애 공간과 소장품들을 보여준다.', 'mame (지은이), 권미량 (옮긴이)', '인간희극', 'https://image.aladin.co.kr/product/34474/27/cover200/8993784833_1.jpg', '국내도서>예술/대중문화>미술>미술 실기', 1, 1);
