INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('주택청약의 모든 것 - 한국부동산원 청약홈이 선보이는 대한민국 주택청약 바이블, 2024~2025년 전면 개정판', '2024년 개정된 청약제도를 정확하고 체계적으로, 이해하기 쉽게 설명한다. 청약 업무를 수행하고, 주택청약의 관문인 ‘청약홈’을 운영하는 한국부동산원이 한 줄 한 줄 검토하며 변경된 내용은 수정하고 새로운 내용은 추가했다.', '한국부동산원 (지은이)', '한빛비즈', 'https://image.aladin.co.kr/product/34317/73/cover200/k292932570_1.jpg', '국내도서>경제경영>재테크/투자>부동산/경매', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('내가 한 말을 내가 오해하지 않기로 함', '133만 명의 구독자를 보유한 유튜브 채널 &lt;빠더너스&gt;의 크리에이터 문상훈이 첫 산문집을 출간했다. 문쌤, 문이병, 문상 등 다양한 부캐로 그를 기억하는 사람들에게는 이 소식이 새삼스러울지도 모른다. 하지만 그의 오랜 팬이라면, 혹은 매체를 통해 그의 편지글 한 문장이라도 본 사람이라면 누구보다 기다려왔을 소식임이 분명하다.', '문상훈 (지은이)', '위너스북', 'https://image.aladin.co.kr/product/33038/40/cover200/k562937688_1.jpg', '국내도서>에세이>명사에세이>방송연예인에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('열세 번째 배심원', '존 그리샴, 마이클 코넬리의 뒤를 잇는 법정 스릴러계의 다크호스로 주목받고 있는 스티브 캐버나 장편소설. 작가 마이클 코넬리가 "법조계에서의 경험과 굉장히 스릴 있는 놀이기구를 전문가답게 엮어낸 작가의 최고작. 이렇게 기발한 책은 자주 나오지 않는다"라 대찬사를 보냈다.', '스티브 캐버나 (지은이), 서효령 (옮긴이)', '북로드', 'https://image.aladin.co.kr/product/18684/73/cover200/k192635663_1.jpg', '국내도서>소설/시/희곡>액션/스릴러소설>외국 액션/스릴러소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('아이를 무너트리는 말, 아이를 일으켜 세우는 말 - 상처 받기 쉬운 아이의 마음을 지키는 대화법 70가지', '소아청소년 정신건강의학과 전문간호사라는 독특한 직업을 가졌으며, 동시에 한 아이의 아버지가 쓴 첫 책이다. 아이의 마음을 일으켜 세워 주는 대화와 행동을 해 주었으면 하는 바람을 담아 70가지 대화법을 소개한다.', '고도칸 (지은이), 한귀숙 (옮긴이), 이은경 (감수)', '21세기북스', 'https://image.aladin.co.kr/product/34545/35/cover200/k232933966_1.jpg', '국내도서>좋은부모>육아>육아법/육아 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('괴수 8호 6', '괴수 9호와 조우했으나 변신이 되지 않아 위기에 몰린 히비노 카프카. 그때 나타난 시노미야 키코루와 함께 괴수 9호와 대치하지만, 변신이 되지 않는 이유가 카프카 자신의 두려움 때문임을 깨닫는다. 한편, 나루미 대장의 앞에도 나타난 괴수 9호가 전투 도중 진화를 이루는데…?!', '마츠모토 나오야 (지은이), 유유리 (옮긴이)', '서울미디어코믹스(서울문화사)', 'https://image.aladin.co.kr/product/34278/77/cover200/k342932269_1.jpg', '국내도서>만화>소년만화>액션', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('내장비만 - 국민 주치의 이왕림 박사의 34년 비만 해독 연구 결정판', '하버드 의대와 뉴욕 의대에서 세포면역학과 해독학을 연구하며 국내 의료계에 해독과 항노화를 최초로 소개하고 ‘내장비만’을 최초로 명명한 최고 권위자 이왕림 박사. 그는 내장지방에서 대한 인식이 낮던 20년 전 내장지방의 위험성을 대중적으로 알리기 위해 다년간의 연구와 임상 치료 경험을 집대성해 『내장비만』을 펴냈다.', '이왕림 (지은이)', '포레스트북스', 'https://image.aladin.co.kr/product/34273/42/cover200/k692932066_1.jpg', '국내도서>건강/취미>건강정보>기타', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('나는 나의 스무 살을 가장 존중한다', '이하영 원장이 인생의 숱한 고비를 넘기며, 오늘의 부와 행복을 펼치기까지의 과정을 담고 있다. 지금의 성공은 이미 스무 살에 상상하고 생각해왔던 것으로, ‘내 미래에 대한 확신이 꿈을 현실로 만든다’는 메시지를 담고 있다.', '이하영 (지은이)', '토네이도', 'https://image.aladin.co.kr/product/33372/60/cover200/k952938310_2.jpg', '국내도서>자기계발>성공>성공학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('지적 대화를 위한 넓고 얕은 지식 1 - 현실 편 : 역사 / 경제 / 정치 / 사회 / 윤리', '고대 이후부터 현대까지 우리에게 좀 더 가까운 시간의 지식들을 다룬 책이다. 고대 이후는 이원론이 지배하는 시대였기에, 책의 구조는 이분법을 따른다. 현실 세계를 지배자와 피지배자로 구분해 어렵고 복잡한 지식들을 하나의 단순한 구조로 재편한다.', '채사장 (지은이)', '웨일북', 'https://image.aladin.co.kr/product/22872/79/cover200/k992636841_2.jpg', '국내도서>인문학>교양 인문학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('1984', '민음사에서 조지 오웰 탄생 100주년을 맞아 그의 대표작 <1984>를 새롭게 펴냈다. <1984>는 널리 알려진 바와 같이 전체주의를 비판하는 디스토피아 소설로, 날카로운 풍자와 정치적 함의로 유명하다. ''절대 권력은 절대 부패한다''는 명언을 탁월하게 형상화한 작품.', '조지 오웰 (지은이), 정회성 (옮긴이)', '민음사', 'https://image.aladin.co.kr/product/41/89/cover200/s122531356_1.jpg', '국내도서>소설/시/희곡>영미소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2025 문동균 한국사 한 권으로 모든 것을 정리하는 판서노트 (한정판)', '&lt;한권으로 모든 것을 정리하는 판서노트&gt;는 문동균 강사의 강의와 판서 내용, 수험 필수 내용을 한 권으로 집약한 교재로 흐름과 이해, 심화까지 한 번에 정리·완성할 수 있도록 구성된 최단기 이론 완성 교재이다.', '문동균 (지은이)', '에스티유니타스', 'https://image.aladin.co.kr/product/34018/36/cover200/k742931063_1.jpg', '국내도서>수험서/자격증>공무원 수험서>7/9급 공무원>한국사>강의노트/기타', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('백귀야행 30', '리쓰와 마찬가지로 이(異)세계의 것을 볼 수 있는 리쓰의 친척, 사카모토 카이에게 요마들이 접근한다. 형, 엄마, 아빠를 언급하며 제각기 다른 조언을 하는 요마들에 카이는 누구의 말을 믿어야 할지 알 수 없다. 한편, 리쓰의 대학교로 한 여성이 찾아온다. 그녀에게는 중학교 때부터 곁을 지켜 주던 존재가 있었는데….', '이마 이치코 (지은이), 한나리 (옮긴이)', '시공사(만화)', 'https://image.aladin.co.kr/product/34386/75/cover200/k472932509_1.jpg', '국내도서>만화>본격장르만화>호러/스릴러', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('바다, 어디까지 내려가 봤니? - 해수면부터 심해까지 바다 동물과 누비는 여행', '생명이 시작된 바다와 그 안에 살고 있는 기기묘묘한 해양 생물들, 그리고 무너져가는 생태계, 해저 깊이 내려간 잠수정 트리에스테, 바다 밑에 있는 깊은 골짜기 마리아나 해구까지, 이 모든 이야기가 담겨 있다. 지금까지 단편적으로 알고 있었던 바다가 이 책 한 권으로 정리된다.', '자눔베르토 아치넬리 (지은이), 줄리아 차파로니 (그림), 김여진 (옮긴이)', '런치박스', 'https://image.aladin.co.kr/product/34353/87/cover200/k152932099_2.jpg', '국내도서>유아>유아 교양/학습>유아 자연관찰/과학>지구/우주/환경', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('무정형의 삶 (양장) - 김민철 파리 산문집', '22년째 한결같은 마음으로 사랑하고 있는 작가의 최애 도시 파리에 두 달간 머물면서 쓴 이야기다. 특별한 바람과 빛, 소리가 있고 감촉이 있던 풍경, 그곳에만 존재했던 것들이 입체적이고 선명하게 작가의 렌즈를 통해 전달되어 여행의 설렘을 가득 느낄 수 있는 책이다.', '김민철 (지은이)', '위즈덤하우스', 'https://image.aladin.co.kr/product/34278/24/cover200/k892932167_1.jpg', '국내도서>에세이>한국에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('흔한남매 이무기 2 - 이해하면 무서운 이야기', '흔한남매 유튜브 채널의 ‘이무기’ 스토리를 오싹하고 코믹한 만화로 풀어 낸 책이다. 으스스하면서도 유머러스한 이야기를 어린이의 눈높이에 맞춰 재구성하였으며, 이야기마다 개성과 매력 만점인 흔한남매의 부캐들이 등장한다.', '흔한남매 (원작), 이종혁 (글), 도니패밀리 (그림), 흔한컴퍼니 (감수)', '미래엔아이세움', 'https://image.aladin.co.kr/product/34240/43/cover200/k252931432_2.jpg', '국내도서>어린이>TV/만화/영화>만화 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('원피스 108 - 죽는 편이 나은 세계', '미래섬에서 탈출을 꾀하는 루피 일행. 그러나 해군의 대선단이 섬을 에워싼다. 이를 이끄는 자는 대장 ''키자루''!! 그 안에는 오로성의 모습도 있고, 일찍이 없던 규모의 전투가 벌어질 예감이 소용돌이치는데...', '오다 에이치로 (지은이)', '대원씨아이(만화)', 'https://image.aladin.co.kr/product/33988/86/cover200/k782930542_1.jpg', '국내도서>만화>본격장르만화>판타지>액션 판타지', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('일류의 조건', '《일류의 조건》이 국내 첫 출간 후 18년 만에 필름출판사에서 복간되었다. 이 책은 한 분야에 있어 통달한 전문가, 즉 ‘일류’가 되기 위해서는 근본적인 조건 세 가지인 ‘훔치는 힘’, ‘요약하는 힘’, ‘추진하는 힘’을 갖추어야 함을 전하는 실용적인 자기계발서다.', '사이토 다카시 (지은이), 정현 (옮긴이)', '필름(Feelm)', 'https://image.aladin.co.kr/product/33626/89/cover200/k662939214_1.jpg', '국내도서>자기계발>성공>성공학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('참을 수 없는 존재의 가벼움 - 국내 출간 30주년 기념 특별판', '현재에서 멀지 않은 20세기 작가이지만 이미 살아 있는 신화가 된 작가, 밀란 쿤데라. 쿤데라 작품을 독점 계약, 출판하고 있는 민음사가 밀란 쿤데라 국내 소개 30주년을 맞아 선보이는 &lt;참을 수 없는 존재의 가벼움&gt; 리뉴얼 판이다.', '밀란 쿤데라 (지은이), 이재룡 (옮긴이)', '민음사', 'https://image.aladin.co.kr/product/15251/16/cover200/8937437562_1.jpg', '국내도서>소설/시/희곡>세계의 소설>동유럽소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('라비우와 링과', '2023년 단편소설 〈폴터가이스트〉와 첫 장편소설 《너는 내 목소리를 닮았어》를 통해 타인과의 만남이 빚어내는 관계의 빛깔을 감각적으로 그려온 김서해 작가의 신작 《라비우와 링과》가 위즈덤하우스 단편소설 시리즈 위픽으로 출간되었다.', '김서해 (지은이)', '위즈덤하우스', 'https://image.aladin.co.kr/product/34474/50/cover200/k482932633_1.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이후 한국소설', 1, 1);
