INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('일본어 단어 무작정 따라하기 - 이 한 권으로 정리된다!', '누적 85만 부에 빛나는 ‘일본어 무작정 따라하기 시리즈’에서 또 하나의 독보적인 신간이 출간되었다. 이번에는 단어를 하나하나 제대로 익힐 수 있도록 꼼꼼하게 정리한 단어책 《일본어 단어 무작정 따라하기》다.', '후지이 아사리 (지은이)', '길벗이지톡', 'https://image.aladin.co.kr/product/33977/57/cover200/k142930540_1.jpg', '국내도서>외국어>일본어>일본어 어휘/한자/문법', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('일본어 문법 무작정 따라하기', '16년 간 일본어 문법 분야 베스트셀러 자리를 굳건히 지키며 12만 독자의 사랑을 받은 &lt;일본어 문법 무작정 따라하기&gt;. 국내 최초로 문법도 귀로 들으면서 익히는 ‘소리 패턴 학습법’을 적용했다. 문법을 귀로 배우면 듣기와 회화 실력까지 보장된다.', '후지이 아사리 (지은이)', '길벗이지톡', 'https://image.aladin.co.kr/product/30963/87/cover200/k212831201_1.jpg', '국내도서>외국어>일본어>일본어 어휘/한자/문법', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('해커스 일본어 첫걸음 : 기초 20일 독학 완성! - 기초 문형으로 회화부터 JLPT까지ㅣ일본어회화 무료 동영상강의 + 단어 쓰기 노트 + 무료MP3 제공', '일본어를 하나도 몰라도, 따라 읽기만 하면 말문이 트이도록 체계적으로 학습을 구성했다. 문형 활용한 긴 문장 말하기, 연습문제, 실력 확인하기로 배운 것을 내 것으로 확인할 수 있다. 문자 암기와 문형 학습을 도와줄 히라가나/가타카나표, 동사 활용표, 쓰기 노트, 워크북을 수록했다.', '해커스JLPT연구소 (지은이)', '해커스어학연구소(Hackers)', 'https://image.aladin.co.kr/product/27470/88/cover200/8965424216_1.jpg', '국내도서>외국어>일본어>일본어 첫걸음', 1, 1);
