INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('나를 위해 살지 않으면 남을 위해 살게 된다 - 지혜에 관한 작은 책, 엥케이리디온', '스토아 철학자 에픽테토스가 전하는 ‘내 삶의 주도권을 되찾는 법’. 2000년 동안 수많은 사람에게 영감을 준 책으로, 단순히 이론적인 철학서를 넘어 실제로 삶에 적용할 수 있는 불변의 진리를 담은 고전으로 평가받고 있다.', '에픽테토스 (지은이), 노윤기 (옮긴이)', '페이지2(page2)', 'https://image.aladin.co.kr/product/34233/55/cover200/k112931235_1.jpg', '국내도서>인문학>철학 일반>교양 철학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('만일 나에게 단 한 번의 아침이 남아 있다면 - 오늘이 끝나기 전 반드시 깨달아야 할 것들', '여기, 가야 할 길을 잃은 당신을 위한 아주 특별하고도 지적인, 경쾌하고 진솔한 여섯 번의 인생 수업이 펼쳐진다. 《뉴욕 타임스》 기자인 저자는 삶의 마지막을 향해 유유히 걸어가는 노인 여섯 명과 1년에 걸쳐 인터뷰를 진행하기로 한다.', '존 릴런드 (지은이), 최인하 (옮긴이)', '북모먼트', 'https://image.aladin.co.kr/product/34082/99/cover200/k892931080_2.jpg', '국내도서>인문학>인문 에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('미오기傳 - 활자 곰국 끓이는 여자', '‘알려지지 않아서 안타까운 책’을 페이스북에 소개하며 유명해진 김미옥 작가가 자신의 삶을 풀어낸 에세이집을 출간했다. 그의 삶은 맵고 쓰고 짠 사연들로 버무려져 있지만 이를 풀어내는 문장은 유쾌함과 유머로 가득하다.', '김미옥 (지은이)', '이유출판', 'https://image.aladin.co.kr/product/33933/84/cover200/k932930135_1.jpg', '국내도서>에세이>한국에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('천 개의 파랑 - 2019년 제4회 한국과학문학상 장편 대상', '한국과학문학상 장편 대상 수상작. 2019년 첫 장편소설 &lt;무너진 다리&gt;로 SF 팬들에게 눈도장을 찍었고, 2020년 7월, 소설집 &lt;어떤 물질의 사랑&gt;을 통해 우리 SF의 대세로 굳건히 자리 잡은 천선란의 작품이다.', '천선란 (지은이)', '허블', 'https://image.aladin.co.kr/product/24895/69/cover200/s142939648_1.jpg', '국내도서>소설/시/희곡>과학소설(SF)>한국 과학소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('생각을 모으는 사람', '<행복한 청소부>. 많은 사람들에게 책과 예술의 기쁨을 안겨주었던 바로 그 책 <행복한 청소부>의 두 저자, 모니카 페트와 안토니 보라틴스키의 작품. 아주 독특한 상상력이 돋보이는 책이다.', '모니카 페트 (지은이), 안토니 보라틴스키 (그림), 김경연 (옮긴이)', '풀빛', 'https://image.aladin.co.kr/product/29/44/cover200/8974749246_2.jpg', '국내도서>유아>그림책>나라별 그림책>유럽', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2025 전한길 한국사 3.0 기출문제집 - 전2권', '2024 최신 기출문제를 모두 수록하였고, 출제 트렌드를 빠르고 정확하게 확인할 수 있도록 주제의 맨 앞에 배치하였다. 최근 출제 경향에 맞춰 단계를 단순화하면서도 각 chapter 후반부에 “+10 고난도 문제” 코너를 두어 만점 획득에 도움을 주고자 하였다.', '전한길 (지은이)', '메가스터디교육(공무원)', 'https://image.aladin.co.kr/product/34534/64/cover200/k642932955_1.jpg', '국내도서>수험서/자격증>공무원 수험서>7/9급 공무원>한국사>기출/문제집/모의고사', 1, 1);
