INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('마음을 보내려는 마음', '2004년 시로 작품활동을 시작하여 산문과 소설로 장르를 자유롭게 넘나들며 작품세계를 끊임없이 확장해온 박연준이 여덟번째 에세이 『마음을 보내려는 마음』을 창비 에세이& 시리즈로 선보인다. 산문 읽는 즐거움을 독자에게 가득 안겨준 탁월한 에세이스트 박연준 시인이 이번에는 그만의 우아한 사색이 담긴 필치로 ‘마음’을 관찰한다.', '박연준 (지은이)', '창비', 'https://image.aladin.co.kr/product/34454/92/cover200/893643957x_1.jpg', '국내도서>에세이>한국에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('라이프 재킷', '창비청소년문학 127권. 『푸른 사자 와니니』 『1945, 철원』 『호수의 일』 등 어린이 청소년에게 큰 사랑을 받아 온 밀리언셀러 작가 이현의 장편소설. 이번 작품에서 이현은 학교와 집, 학원 등 청소년소설에서 주로 다루어 온 제한된 공간을 벗어나 광활한 바다를 배경으로 손에 땀을 쥐게 하는 모험과 묵직한 여운을 선사한다.', '이현 (지은이)', '창비', 'https://image.aladin.co.kr/product/34366/88/cover200/8936457276_1.jpg', '국내도서>청소년>청소년 문학>청소년 소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('처음 읽는 그리스 로마 신화 12 - 트로이 전쟁', '서양 역사와 철학은 물론 미술, 문학 등의 예술을 이해하기 위해서 꼭 읽어야 할 그리스 로마 신화. 필수 고전으로 꼽히는 그리스 로마 신화를 처음 접하는 어린이들을 위해, 흥미롭고 재미있는 만화와 풍성하고 친절한 설명으로 신들의 세계를 안내하는 인문학 시리즈이다.', '최설희 (지은이), 한현동 (그림), 정수영 (구성)', '미래엔아이세움', 'https://image.aladin.co.kr/product/34311/64/cover200/k662932376_1.jpg', '국내도서>어린이>사회/역사/철학>신화/종교', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('괴수 8호 1', '괴수 발생률이 세계에서도 손에 꼽히는 일본. 지금은 괴수 전문 청소업체에서 일하고 있는 히비노 카프카. 어느 날, 카프카는 알 수 없는 생물에 의해 괴수로 변하고, 괴수 토벌을 맡은 일본 방위대로부터 ‘괴수 8호’란 코드네임으로 불리는 존재가 되는데….', '마츠모토 나오야 (지은이), 유유리 (옮긴이)', '서울미디어코믹스(서울문화사)', 'https://image.aladin.co.kr/product/31870/2/cover200/k242833523_1.jpg', '국내도서>만화>소년만화>액션', 1, 1);
