INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('고블린 슬레이어 15 - SL Comic', '소치기 소녀, 접수원 아가씨와 함께 엘프의 마을로 향하던 고블린 슬레이어 파티는 고블린에게 습격을 당한다. 게다가 마을을 덮친 거대한 짐승의 등에도 역시 고블린이…. 당황하는 엘프들은 아랑곳하지 않은 채 신비한 숲에서 고블린 슬레이어의 지휘에 따라 냉철한 고블린 퇴치가 시작된다!', '쿠로세 코스케 (지은이), 카규 쿠모 (원작), 칸나츠키 노보루 (그림), 김성래 (옮긴이)', '디앤씨미디어(주)(D&C미디어)', 'https://image.aladin.co.kr/product/34474/26/cover200/k112932632_1.jpg', '국내도서>만화>본격장르만화>판타지>액션 판타지', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('행복할 거야 이래도 되나 싶을 정도로', '하루를 열심히 살아 내고 또 나아가고자 도전하는 당신에게 틀림없는 행복이 찾아올 거라 확신을 주는 『행복할 거야 이래도 되나 싶을 정도로』. 매일을 버텨 내고 있는 당신이 가슴 깊이 새겨 두면 좋을 글이 가득하다.', '일홍 (지은이)', '부크럼', 'https://image.aladin.co.kr/product/34385/53/cover200/k082932407_2.jpg', '국내도서>에세이>한국에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('아몬드 (청소년판) - 제10회 창비 청소년문학상 수상작', '2017년 처음 출간된 이래 국내 종이책 기준 100만 부 판매를 기록한 베스트셀러이자 스테디 셀러 『아몬드』가 절판기간을 거쳐 재출간된다. 강렬하고 새로운 2종의 표지로 청소년과 성인 독자에게 새롭게 다가가며, 단편 외전이 함께 수록돼 상상력과 읽는 재미를 배가시킨다.', '손원평 (지은이)', '다즐링', 'https://image.aladin.co.kr/product/31893/33/cover200/k242833749_2.jpg', '국내도서>청소년>청소년 문학>청소년 소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('해리엇 - 175년 동안 바다를 품고 살았던 갈라파고스 거북 이야기', '보름달문고 시리즈 45권. 제11회 문학동네어린이문학상 대상작 &lt;봉주르, 뚜르&gt;의 작가 한윤섭이 선보이는 두 번째 장편동화이다. 숲이든 동물원이든 사람의 세상에서 벗어나지 못하고 있는 수많은 동물들. 우리는 과연 그들의 자유를 빼앗아 무엇을 얻으려는 것인지, 그들은 무슨 잘못으로 아프게 살아야만 하는지, 가슴 저린 이야기를 통해 들려준다.', '한윤섭 (지은이), 서영아 (그림)', '문학동네', 'https://image.aladin.co.kr/product/1210/80/cover200/895461504x_1.jpg', '국내도서>어린이>동화/명작/고전>국내창작동화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('괴테와의 대화 1', '젊은 문학도 에커만이 인생, 예술, 학문 그리고 사랑에 대해 괴테와 나눈 대화. 에커만이 괴테가 세상을 떠날 때까지 10년간 약 1000번의 만남을 가지며 그와 나눈 대화를 꼼꼼하게 기록, 정리한 것이다. 에커만에게 멘토와도 같았던 괴테가 젊은 이들에게 전하는 주옥같은 메시지들이 들어있다.', '요한 페터 에커만 (지은이), 장희창 (옮긴이)', '민음사', 'https://image.aladin.co.kr/product/214/60/cover200/8937461765_2.jpg', '국내도서>소설/시/희곡>문학의 이해>작가론', 1, 1);
