INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('내게 빌어봐 1~4 리미티드 에디션 박스 세트', '2021년 8월 연재를 시작한 이래 총 213일간 독자들의 열렬한 지지를 받으며 웹소설 플랫폼 및 각종 온라인서점 베스트셀러 순위에 오른 리베냐의 대표작 『내게 빌어봐』가 양장본으로 출간되었다. 독자 대망의 양장본에는 종이책 출간에 맞춰 처음 공개되는 세 번째 외전 「내 아이를 유괴하라」가 수록되어 있다.', '리베냐 (지은이)', '마카롱', 'https://image.aladin.co.kr/product/34558/53/cover200/k112933166_1.jpg', '국내도서>소설/시/희곡>로맨스소설>한국 로맨스소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('To Love 트러블 5', '해수욕 하러 가려고 했는데, 라라가 발명한 메카로 워프해서 무인도에 도착! 그런데 그 섬에서 공룡이나 괴물 같은 형태의 사람을 잡아먹는 나무 등 터무니없는 생물들이 차례차례 나타나 친구들을 습격한다!', '야부키 켄타로 (지은이), 서울미디어코믹스 편집부 (옮긴이), Saki Hasemi (각본)', '서울미디어코믹스(서울문화사)', 'https://image.aladin.co.kr/product/34554/77/cover200/k342933161_1.jpg', '국내도서>만화>본격장르만화>판타지>드라마틱 판타지', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('글쓰기의 감각 - 21세기 지성인들을 위한 영어 글쓰기의 정석', '인지 과학자이자 언어학자이자 베스트셀러 작가인 스티븐 핑커는 흥미진진하면서도 교훈적인 이 책에서 21세기에 맞는 어법 지침서란 어때야 할지를 다시 생각해 본다.', '스티븐 핑커 (지은이), 김명남 (옮긴이)', '사이언스북스', 'https://image.aladin.co.kr/product/34403/23/cover200/k602932612_1.jpg', '국내도서>인문학>책읽기/글쓰기>글쓰기', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('첫 1년 움직임의 비밀 - 평생을 좌우하는 영유아기 움직임 발달', '소아 전문 물리치료사로 50년간 일한 지은이가 3,000명 이상의 영유아를 만나며 작업한 경험을 바탕으로 수많은 부모와 동료의 요청으로 만들어진 책이다. 이 책은 아기가 어떤 과정을 거쳐 성장 발달하는지, 그리고 운동 발달이 올바르게 일어나도록 도와주는 것이 왜 중요한지를 알려준다.', '마리안 헤름센-판 완로이 (지은이), 하주현 (옮긴이)', '푸른씨앗', 'https://image.aladin.co.kr/product/33180/45/cover200/k392937332_1.jpg', '국내도서>좋은부모>육아>육아법/육아 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('나는 소망한다 내게 금지된 것을', '작가 양귀자의 장편소설. 1992년에 초판이 나오자마자 바로 페미니즘 논란과 함께 화제의 중심에 올랐고 그해 최고의 베스트셀러가 되었다. 젊은 여성이 인기 남자배우를 납치해서 감금하고 조종하는 이야기이다.', '양귀자 (지은이)', '쓰다', 'https://image.aladin.co.kr/product/18936/52/cover200/8998441071_1.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이전 한국소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('세계를 건너 너에게 갈게 - 제8회 문학동네청소년문학상 대상 수상작', '제8회 문학동네청소년문학상 대상 수상작. 서로 다른 시간을 살아가는 두 은유가 편지를 주고받으면서 벌어지는 이야기로, 2016년의 은유가 1년을 살아가는 동안 1982년의 은유는 20년의 세월을 살아간다.', '이꽃님 (지은이)', '문학동네', 'https://image.aladin.co.kr/product/13366/86/cover200/895465021x_1.jpg', '국내도서>청소년>청소년 문학>청소년 소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('누나의 친구가 성가신 이야기 2', '이쿠타 세나를 좋아한다고 고백한 미스미 나오. 놀란 세나는 재빨리 소중한 친구 미스미 리오의 동생으로밖에 볼 수 없다고 대답하지만, 나오는 이제 와서 누나의 친구로 볼 수 없다고 말한다.', '아즈사 키나 (지은이), 심이슬 (옮긴이)', 'HUSH(허쉬)', 'https://image.aladin.co.kr/product/34558/48/cover200/k852933165_1.jpg', '국내도서>만화>순정만화>틴에이지 순정', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('환생학교 요괴반 4 - 웃소의 판타지 미션 코믹북', '드라큘라의 성에서 마법진이 그려진 종이를 발견한 환생학교 요괴반 친구들. 이들은 차원의 문을열 수 있지 않을까 하는 기대감에 웅성댄다. 그러나 불쾌한 기색을 비치며 드라큘라는 소정이가 든 마법진 종이를 빼앗고, 이때 종이에 베인 소정이의 손가락에서 피가 나기 시작하는데….', '웃소 (원작), 윤종문 (글), 파키나미 (그림)', '미래엔아이세움', 'https://image.aladin.co.kr/product/34469/64/cover200/k572932533_1.jpg', '국내도서>어린이>TV/만화/영화>만화 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('마인드 박스 - 인생의 중심을 잡는 거인의 16가지 생각', '대한민국 1호 기록학자이자 전작 『거인의 노트』를 통해 10만 명이 넘는 독자에게 ‘기록’이 가진 힘을 소개했던 김익한 교수는 이번에 ‘생각’이라는 주제를 통해 인생을 관통하는 더 깊고 핵심적인 삶의 자세와 성장법을 제안한다.', '김익한 (지은이)', '다산북스', 'https://image.aladin.co.kr/product/34317/37/cover200/k692932579_1.jpg', '국내도서>자기계발>성공>성공학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('일인칭 가난 - 그러나 일인분은 아닌, 2023 우수출판콘텐츠 선정작', '올해 26살인 저자는 2019년까지 20여 년간 국민기초생활수급자로 살아왔다. 하지만 자신이 한국의 가난을 대표할 수 없다는 것을 안다. 덜 가난해서가 아니라 가난의 양태가 가지각색이기 때문이다. 그래서 이 책은 철저히 일인칭으로 쓰였다.', '안온 (지은이)', '마티', 'https://image.aladin.co.kr/product/32956/26/cover200/k202936242_1.jpg', '국내도서>인문학>인문 에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('파친코 2 - 개정판', '4대에 걸친 재일조선인 가족의 이야기를 그린 세계적 베스트셀러, 이민진 작가의 장편소설 《파친코》가 새롭게 출간되었다. 《파친코》는 재미교포 1.5세대인 이민진 작가가 30년에 달하는 세월에 걸쳐 집필한 대하소설로, 2017년 출간되어 《뉴욕타임스》 베스트셀러에 올랐다.', '이민진 (지은이), 신승미 (옮긴이)', '인플루엔셜(주)', 'https://image.aladin.co.kr/product/29496/41/cover200/k192838537_2.jpg', '국내도서>소설/시/희곡>영미소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('삐뽀삐뽀 119 소아과 (개정13판) - 2005년 대한의사협회 선정추천도서', '1997년 초판 출간 이래 아기 엄마들의 든든한 육아 동반자로서 함께해 온 소아과가 새로운 옷으로 갈아입었다. 내용상으로는 아기 키우기 전에 반드시 먼저 짚고 넘어가야 할 월령별 체크리스트와 아이들 수면에 관련된 부분을 새롭게 보강하며 최신 육아 트렌드를 반영했다.', '하정훈 (지은이)', '유니책방', 'https://image.aladin.co.kr/product/8763/77/cover200/s082636187_2.jpg', '국내도서>좋은부모>육아>육아백과', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('To Love 트러블 6', '리토 일행에게 도착한 수수께끼의 「초대장」. 카드를 열어보니, 갑자기 다른 차원의 게임 세계로 말려들고 말았다! 라라가 벌인 짓인 줄 알았지만, 마지막에 나타난 수수께끼의 미소녀 두 명의 정체는?!', '야부키 켄타로 (지은이), 서울미디어코믹스 편집부 (옮긴이), Saki Hasemi (각본)', '서울미디어코믹스(서울문화사)', 'https://image.aladin.co.kr/product/34554/79/cover200/k422933161_1.jpg', '국내도서>만화>본격장르만화>판타지>드라마틱 판타지', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('달리는 말', '국내에 소개된 적이 없던 전설적인 작품, 여러 번 노벨 문학상 후보로 거론되었던 미시마 유키오의 ‘풍요의 바다’ 시리즈 두 번째 권 『달리는 말』이 출간되었다. ‘풍요의 바다’ 4부작은 메이지 시대 말기부터 1975년까지를 아우르는, 원고지 약 6000매 분량의 대작이다.', '미시마 유키오 (지은이), 유라주 (옮긴이)', '민음사', 'https://image.aladin.co.kr/product/34474/42/cover200/8937479842_1.jpg', '국내도서>소설/시/희곡>일본소설>1950년대 이후 일본소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('세뇌의 역사 - 파블로프에서 한국전쟁 그리고 소셜 미디어까지', '미국의 저명한 정신의학자인 지은이는 중세시대의 종교재판부터 과학적 실험을 통해 행동을 조건화하려 했던 파블로프 그리고 한국전쟁을 거쳐 현대에 이르기까지 우리를 유령처럼 늘 따라다녔던 세뇌의 역사를 추적한다.', '조엘 딤스데일 (지은이), 임종기 (옮긴이)', '에이도스', 'https://image.aladin.co.kr/product/34455/21/cover200/k672932431_1.jpg', '국내도서>인문학>교양 인문학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2025 이기적 컴퓨터활용능력 2급 필기 기본서 - 최신 출제기준 반영 + 동영상 강의 무료 + CBT 온라인 문제집 제공', '컴퓨터활용능력 2급 필기 시험을 처음 준비하는 초보자를 위한 &lt;이기적 컴퓨터활용능력 2급 필기 기본서&gt;가 출간되었다. 이론서에서는 출제 경향을 오랜 기간 분석하여 이론을 정리하였다. 각 파트와 섹션별로는 출제 빈도를 표기해, 효과적으로 학습을 진행할 수 있도록 구성하였다.', '홍태성, 영진정보연구소 (지은이)', '영진.com(영진닷컴)', 'https://image.aladin.co.kr/product/34429/89/cover200/8931476094_1.jpg', '국내도서>컴퓨터/모바일>활용능력>컴퓨터활용능력>컴퓨터활용능력 필기', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('동사 X 전치사 도감 - 외우지 않는 편안함', '우리가 흔히 알고 있는 ‘전치사 30개 X 동사 42개’로 1,000개가 넘는 다양한 표현을 머릿속에 저절로 기억되도록 만들었다. 그림 학습법으로 이름을 알려온 저자가 직접 스케치한 1,000개가 넘는 이미지로 표현을 억지로 외우지 않아도 개념이 저절로 잡히고 기억이 되게 했다.', '권은희 (지은이)', '길벗이지톡', 'https://image.aladin.co.kr/product/34312/43/cover200/k132932479_1.jpg', '국내도서>외국어>영어회화>생활영어', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2025 시나공 컴퓨터활용능력 1급 필기 총정리', '빠른 합격을 희망하는 수험생을 위해 핵심요약과 기출문제, 실전 모의고사로 구성한 초단기 합격 전략집이다. 가로 넘김형 도서로 학습편리성을 높이고 시원시원한 내용 배치로 학습이 용이하게 구성했다.', '길벗 R&D, 강윤석, 김용갑, 김우경, 김종일 (지은이)', '길벗', 'https://image.aladin.co.kr/product/34201/52/cover200/k542931921_1.jpg', '국내도서>컴퓨터/모바일>활용능력>컴퓨터활용능력>컴퓨터활용능력 필기', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('그릿 GRIT (골드 에디션) - IQ, 재능, 환경을 뛰어넘는 열정적 끈기의 힘', '2016년 출간 즉시 아마존 베스트셀러 1위에 오르며 교육, 산업, 방송할 것 없이 전세계적으로 신드롬을 일으킨 책 《그릿》이 국내 50만 부 돌파, 150쇄를 기념한 골드 에디션으로 다시 우리를 찾아왔다. 이번 리커버는 고급스러운 하드커버로 소장 가치를 높였다.', '앤절라 더크워스 (지은이), 김미정 (옮긴이)', '비즈니스북스', 'https://image.aladin.co.kr/product/30657/56/cover200/k272830938_1.jpg', '국내도서>자기계발>창의적사고/두뇌계발', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('타이탄의 도구들 (블랙 에디션) - 정상의 자리에 오른 사람들의 61가지 성공 비밀', '2017년 출간 즉시 아마존과 &lt;뉴욕 타임스&gt; 베스트셀러 차트 1위를 동시에 석권하며, 국내 독자들에게도 큰 사랑을 받은 《타이탄의 도구들》이 6년 연속 최고의 베스트셀러를 기념한 리커버 블랙 에디션으로 다시 우리를 찾아왔다.', '팀 페리스 (지은이), 박선령, 정지현 (옮긴이)', '토네이도', 'https://image.aladin.co.kr/product/29714/21/cover200/k232838895_1.jpg', '국내도서>자기계발>성공>성공학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('파친코 1 - 개정판', '4대에 걸친 재일조선인 가족의 이야기를 그린 세계적 베스트셀러, 이민진 작가의 장편소설 《파친코》가 인플루엔셜에서 새로운 번역으로 출간되어 한국 독자들과 만난다. 《파친코》는 재미교포 1.5세인 이민진 작가가 30년에 달하는 긴 세월에 걸쳐 집필한 대하소설이다.
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('현자의 제자를 자칭하는 현자 20 - S Novel+', '범죄조직 ‘이라 무에르테’를 괴멸시킴으로써 이리스 호위 임무에서 벗어나게 된 미라. 이리스 일행과 투기대회가 개최되고 있는 거리로 나가기 위해 고스로리 패션으로 갈아입기로 한다.', '류센 히로츠구 (지은이), 후지 초코 (그림), 정대식 (옮긴이)', '㈜소미미디어', 'https://image.aladin.co.kr/product/34547/50/cover200/k942933060_1.jpg', '국내도서>소설/시/희곡>라이트 노벨>S 노벨', 1, 1);
