INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('단식 존엄사 - 의사 딸이 동행한 엄마의 죽음', '스스로는 먹을 수도, 걸을 수도, 말할 수도 없는 삶. 진통제 없이는 버틸 수 없고, 본인뿐 아니라 가족까지 침몰시키고 마는 삶. 나을 수 있다는 희망이 아닌, 벗어날 수 없는 지옥을 견디며 사는 삶. 이 책은 바로 그런 삶을 사는 당사자와 그 가족에게 구체적이고도 현실적인 대안을 제시한다.', '비류잉 (지은이), 채안나 (옮긴이)', '글항아리', 'https://image.aladin.co.kr/product/34329/67/cover200/k062932682_1.jpg', '국내도서>에세이>외국에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('간소한 삶에 관한 작은 책', '진민영의 미니멀리즘은 공간, 삶, 생각, 태도, 감정을 관통하는 키워드로서 중요한 한 가지에 집중할 몰입의 여비를 벌어준다고 강조한다. 이는 무엇이 중요하고 덜 중요한지 자신의 가치 순위를 정립하게 해주며, 더 나아가 ‘어떻게 살아야 하는가’라는 다소 무거운 질문에 해답을 찾기 위한 길을 제시한다.', '진민영 (지은이)', '책읽는고양이', 'https://image.aladin.co.kr/product/34240/32/cover200/k532931431_1.jpg', '국내도서>에세이>한국에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('마녀와의 7일', 'I의 감시 체제가 강화된 가까운 미래를 무대로 ‘라플라스의 마녀’ 마도카와 함께 아버지의 죽음을 좇는 소년의 모험과 사건의 진상을 추적하는 형사의 활약상을 담은 작품이다. ‘AI’라는 소재를 전면에 내세우며 한층 거대하면서도 현실에 밀착된 시의성 있는 이야기를 선보인다.', '히가시노 게이고 (지은이), 양윤옥 (옮긴이)', '현대문학', 'https://image.aladin.co.kr/product/34193/64/cover200/k642931316_1.jpg', '국내도서>소설/시/희곡>추리/미스터리소설>일본 추리/미스터리소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('소년이 온다 (10주년 한정 특별판, 양장)', '2014년 만해문학상, 2017년 이탈리아 말라파르테 문학상을 수상하고 전세계 20여개국에 번역 출간되며 세계를 사로잡은 우리 시대의 소설 『소년이 온다』를 출간 10주년을 맞아 특별한정판으로 새롭게 선보인다.', '한강 (지은이)', '창비', 'https://image.aladin.co.kr/product/33921/85/cover200/k532930932_1.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이후 한국소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('반딧불이의 혼례 1', '때는 메이지. 가문도 미모도 부족함 없이 태어났으나 살날이 얼마 남지 않았다는 말을 듣는 백작가 영양 키리가야 사토코의 꿈은 가문에 이익이 되는 결혼을 하는 것뿐. 어느 날 갑자기 정체를 알 수 없는 살인 청부업자가 자신을 노리자, 사토코는 당장 목숨을 부지하기 위해 살인 청부업자 고토 신페이에게 제안한다.', '타치바나 오레코 (지은이), 유유리 (옮긴이)', '서울미디어코믹스(서울문화사)', 'https://image.aladin.co.kr/product/33762/58/cover200/k922930063_1.jpg', '국내도서>만화>본격장르만화>판타지>드라마틱 판타지', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('이동 평균선 투자법 - 차트 분석의 시작과 끝은 이동 평균선이다', '이동 평균선으로 ‘에지가 있는 상태’를 찾아내서 투자 수익을 내는 방법이 담긴 책이다. 에지가 있는 상태란 가격이 끊임없이 변동하는 가운데, 사는 것이 유리하거나 파는 것이 유리한 국면을 뜻한다.', '고지로 강사 (지은이), 김정환 (옮긴이)', '이레미디어', 'https://image.aladin.co.kr/product/33739/94/cover200/k982930766_1.jpg', '국내도서>경제경영>재테크/투자>주식/펀드', 1, 1);
