INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('거래량 투자 기법 - 거래량과 가격의 비밀을 밝힌다!', '거래량 가격 분석법으로 번역되는 VPA 트레이딩법의 특징은 세력과 기관, 즉 소위 스마트 머니를 이기는 것이 아닌 그들을 따라 매매하는 것이다. 그렇다면 어떻게 스마트 머니를 따라 매매할 수 있을까? 무엇보다 세력과 기관이 매수, 매도한 것을 어떻게 알 수 있을까? 이 책은 그 부분에 대해서 다룬다.', '애나 쿨링 (지은이), 송미리 (옮긴이)', '이레미디어', 'https://image.aladin.co.kr/product/34529/30/cover200/k042932950_1.jpg', '국내도서>경제경영>재테크/투자>주식/펀드', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('황천의 츠가이 6', '히가시무라에 홀로 침입해서, 한 소녀와 가짜 아사를 유괴한 이반은 유르에게 전언을 남긴다. 그것은 인질을 내세워 유르를 불러내는 전언이었다. 유르는 두사람을 구출하기 위해 지정된 장소 근처에서 잠복하지만, 소리도 없이 나타난 츠가이에게 납치당하고 마는데….', '아라카와 히로무 (지은이)', '대원씨아이(만화)', 'https://image.aladin.co.kr/product/34526/40/cover200/k462932853_1.jpg', '국내도서>만화>본격장르만화>추리/미스터리', 1, 1);
