INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('5번 레인 - 제21회 문학동네어린이문학상 대상 수상작', '제21회 문학동네어린이문학상 대상 수상작. 열세 살 수영부 아이들의 고락을 담은 이야기로 아동 문학에서 흔치 않은 스포츠물이라는 점이 눈에 띈다. 수영이라는 소재를 통해 ''몸과 마음의 성장''이라는 주제를 그리고 있다.', '은소홀 (지은이), 노인경 (그림)', '문학동네', 'https://image.aladin.co.kr/product/25151/14/cover200/8954674631_2.jpg', '국내도서>어린이>동화/명작/고전>국내창작동화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('친구의 누나를 사랑한 이야기 2', '중3 겨울에 미스미 리오에게 고백한 아리사와 하루토. 입시를 마치고 고등학생이 될 때까지 리오와 만나지 못했지만 멋쩍은 나머지 리오의 집에 놀러 가지도 못하고, 오랜만에 만났는데도 어떤 표정을 지어야 할지 혼란스러울 따름이었다.', '아즈사 키나 (지은이), 심이슬 (옮긴이)', 'HUSH(허쉬)', 'https://image.aladin.co.kr/product/34558/48/cover200/k842933165_1.jpg', '국내도서>만화>순정만화>틴에이지 순정', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('거의 모든 상황별 적절한 영어 표현들 - 한국인의 마음을 담은 영어 표현들', '상황 따라 가볍게도 거하게도 표현하고픈 한국인의 마음을 담은 영어책이다. 한국인들이 원래 전하고 싶던 것을 영어로 마음껏 표현할 수 있도록 세세한 상황을 짚고 그에 해당하는 적절한 표현을 제시한다.', 'June Sweeney (지은이)', '사람in', 'https://image.aladin.co.kr/product/34474/40/cover200/k132932633_1.jpg', '국내도서>외국어>영어어휘', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('빛이 이끄는 곳으로', '천재 건축가 백희성의 첫 번째 장편소설. 어느 날 아침 부동산으로부터 걸려온 전화 한 통이 파리의 건축가 뤼미에르의 인생을 바꿔버렸다. 그는 평범한 직장인의 돈으로는 절대 살 수 없는 시테 섬의 유서 깊은 저택이 헐값에 나와 찾아간 곳에서, 자신이 건축가이기 때문에 선택되었다는 사실을 알게 된다.', '백희성 (지은이)', '북로망스', 'https://image.aladin.co.kr/product/34449/77/cover200/k052932333_1.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이후 한국소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('이름 없는 여자의 여덟 가지 인생', '한 요양원의 치매 환자 구역에 흙을 먹는 걸로 악명 높은 노인, ‘묵 할머니’가 입원해 있다. 묵 할머니는 요양사에게 부고를 써달라고 부탁하면서, 자신의 인생을 요약하는 “여덟 단어”를 들려준다.', '이미리내 (지은이), 정해영 (옮긴이)', '위즈덤하우스', 'https://image.aladin.co.kr/product/34316/99/cover200/k862932578_1.jpg', '국내도서>소설/시/희곡>역사소설>외국 역사소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('신독, 혼자 있는 시간의 힘 - 당신은 혼자 있는 시간을 무엇으로 채우는가?', '‘다산의 마지막’ 시리즈, 《말 공부》 등으로 70만 독자들의 사랑을 받은 고전연구가 조윤제가 ‘신독’이라는 키워드로 돌아왔다. 신독은 ‘홀로 있을 때 도리에 어긋나지 않게 더욱 삼가다’라는 뜻으로, 개인 수양의 최고 단계로 꼽힌다. 혼자 있는 시간을 어떻게 보내며 그 시간을 무엇으로 채우는지가 한 사람을 완성하는 핵심이라는 것.', '조윤제 (지은이)', '비즈니스북스', 'https://image.aladin.co.kr/product/34291/46/cover200/k662932174_2.jpg', '국내도서>자기계발>성공>성공학', 1, 1);
