INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('아이가 없는 집', '유서 깊은 목재 재벌로 만하임 그룹을 운영하는 페르 귄터가 탐정 율리아를 찾아가면서 시작한다. 자신의 휴대폰에서 발견한 시체 사진 한 장 때문에 하룻밤 사이 살인 용의자가 되었다는 페르 귄터. 하지만 정작 그는 사진이 찍힌 시간에 술에 취해 잠들어 기억이 없다고 한다. 게다가 사진 속 남자가 누구인지, 장소가 어디인지조차 알아내기 어렵다.', '알렉스 안도릴 (지은이), 유혜인 (옮긴이)', '필름(Feelm)', 'https://image.aladin.co.kr/product/34117/59/cover200/k962931994_1.jpg', '국내도서>소설/시/희곡>추리/미스터리소설>기타국가 추리/미스터리소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('오백 년째 열다섯 3 - 두 개의 구슬', '단군 신화와 우리 옛이야기에서 탄생한 야호족과 호랑족이라는 참신한 세계관, 두 족속이 최초 구슬을 두고 벌이는 구슬 전쟁이라는 흥미진진한 스토리, 그리고 오백 년을 열다섯으로 살아온 여자아이라는 독보적인 캐릭터로 사랑받고 있는 『오백 년째 열다섯』 세 번째 이야기다.', '김혜정 (지은이)', '위즈덤하우스', 'https://image.aladin.co.kr/product/33993/84/cover200/k522930642_1.jpg', '국내도서>청소년>청소년 문학>청소년 소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('율의 시선', '『완득이』 『위저드 베이커리』 『페인트』 등 생생한 청소년의 현실을 담으며 꾸준한 사랑을 받아 온 창비청소년문학상이 반짝이는 신예 작가의 새 소설을 선보인다. “마지막 장을 넘긴 뒤에도 잔상이 남을 정도로 인상적이다.”라는 청소년심사단의 찬사와 함께 압도적인 지지를 받으며 수상작으로 선정된 김민서 장편소설 『율의 시선』이다.', '김민서 (지은이)', '창비', 'https://image.aladin.co.kr/product/33858/83/cover200/893645725x_1.jpg', '국내도서>청소년>청소년 문학>청소년 소설', 1, 1);
