INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('괴수 8호 10', '식별 괴수 병기 10호를 장비하여 군발 재해에 대응하는 호시나. 사상 첫 자아를 지닌 병기와의 실전은 몹시도 험난한 것이었지만, 호시나의 기지로 인해 공세로 넘어가게 된다. 한편, 카프카가 있는 오오이즈미 에어리어에서는 초대형 괴수가 6마리나 출현해 전황이 악화되어 간다.', '마츠모토 나오야 (지은이), 유유리 (옮긴이)', '서울미디어코믹스(서울문화사)', 'https://image.aladin.co.kr/product/32536/2/cover200/k022935127_1.jpg', '국내도서>만화>소년만화>액션', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('사카모토 데이즈 1 - 전설의 킬러', '푸근한 풍채의 동네 상점 주인 사카모토 타로. 그의 정체는 모든 악당이 두려워함과 동시에 동경했던 전직 전설의 살인청부업자였다! 위협과 위기로부터 가정과 일상을 지키기 위한 사카모토의 나날들. 지켜야 할 것이 있기에 강해진다! 배틀과 코미디가 뒤섞인 새로운 액션 활극이 시작되는데...', '스즈키 유우토 (지은이)', '대원씨아이(만화)', 'https://image.aladin.co.kr/product/28135/83/cover200/k942734557_1.jpg', '국내도서>만화>코믹/명랑만화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('수레바퀴 아래서 (오리지널 초판본 표지디자인) - 최신 원전 완역본', '성적 위주의 교육 속에서 경쟁에 지쳐 자신을 잃어버린 모든 이들에게 공감과 위로를 전하는 &lt;수레바퀴 아래서&gt;는 헤르만 헤세 자신의 청소년기를 반영한 자전적 소설이다. 이제 &lt;수레바퀴 아래서&gt;를 1906년 오리지널 초판본 표지로 만나 보자.', '헤르만 헤세 (지은이), 박지희 (옮긴이), 김선형 (해설)', '코너스톤', 'https://image.aladin.co.kr/product/9870/83/cover200/k822535559_2.jpg', '국내도서>소설/시/희곡>독일소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('엄마의 역사 편지 - 초등학생을 위한 세계사', '&lt;한국사 편지&gt;의 박은봉 선생님과 떠나는 ''세계사'' 여행! 250만 독자가 읽은 &lt;한국사 편지&gt;와 마찬가지로, 어린이 독자를 흡인력 있게 역사의 세계로 안내한다. 방대한 역사를 35개 주제로 집약, 세계사에서 알아야 할 주요 내용을 빠짐없이 담아낸다. 초등 3학년 이상, 열 살 이상이면 혼자서 너끈히 읽어나갈 수 있다.', '박은봉 (지은이), 우지현 (그림)', '책과함께어린이', 'https://image.aladin.co.kr/product/692/57/cover200/s692931072_1.jpg', '국내도서>어린이>사회/역사/철학>세계사', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('실전 추세 투자법 - 상승장 하락장 모두 통하는 1% 매매 전략', '저자인 고지로 강사는 일본 최고의 차트 연구가이자 트레이딩 기법 연구가다. 저자는 독창적으로 개발한 ‘이동평균선 대순환 분석’과 ‘대순환 MACD’를 통해 투자자들이 추세 투자를 하면서 안정적으로 목표하는 수익을 쟁취할 수 있도록 한다.', '고지로 강사 (지은이), 박명진 (옮긴이), 최성민 (감수)', '한국경제신문', 'https://image.aladin.co.kr/product/34429/15/cover200/8947549673_1.jpg', '국내도서>경제경영>재테크/투자>주식/펀드', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('가끔씩 툭하고 러시아어로 부끄러워하는 옆자리의 아랴 양 7 (특별판) - L Novel', '어수선했던 문화제는 마사치카의 분투 덕분에 아랴의 지지도를 높여주며 막을 내렸다. 아랴는 깊어져 가는 마음을 품은 채 마사치카와 함께 다음 행사인 운동회 준비에 나선다!', 'SUN SUN SUN (지은이), 모모코 (그림), 이승원 (옮긴이)', '디앤씨미디어(주)(D&C미디어)', 'https://image.aladin.co.kr/product/34397/49/cover200/k762932511_1.jpg', '국내도서>소설/시/희곡>라이트 노벨>L 노벨', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('백년 목 2 : 치료편 - 내 목 사용설명서, 개정증보판', '경부통 및 상지 통증의 가장 흔한 원인인 목 디스크 질환을 독자 스스로 해결할 수 있는 치료 방법을 제시한다. 별처럼 많은 치료법 중 나에게 가장 적합한 치료는 무엇인지를 알려주며 목 디스크 손상에 대한 가장 근본적인 치료방법인 척추위생에 대한 원칙과 구체적인 방법을 사진과 함께 소개한다. 목 디스크 질환이 있는 사람이 운동을 할 때 주의해야 할 점에 대해서도 자세히 설명하고 있다.목 디스크 환자의 필독서이다.', '정선근 (지은이)', '언탱글링', 'https://image.aladin.co.kr/product/34326/33/cover200/k112932585_1.jpg', '국내도서>건강/취미>질병치료와 예방>허리/디스크/척추/통증', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('네가 유성처럼 스러지는 모습을 지켜볼 운명이었다', '생사를 넘나드는 중상을 입고 구사일생으로 살아났지만 트라우마로 인해 무력한 나날을 보내던 무로사키 토우야는 어느 날 백 끼의 식사가 끝나면 죽음에 이르는 희귀병인 ‘여명백식’에 걸린 여자, 사키무라 리이를 만난다.', '미나토 쇼 (지은이), 황누리 (옮긴이)', '필름(Feelm)', 'https://image.aladin.co.kr/product/34314/16/cover200/k542932473_1.jpg', '국내도서>소설/시/희곡>일본소설>1950년대 이후 일본소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('히카루가 죽은 여름 4 - SL Comic, 초판한정 일러스트 카드 2종', '요시키는 어느 날, 히카루가 다른 무언가로 바뀌어 있음을 확신한다. 과거의 「히카루」가 아닌 「히카루」는 대체 누구인지, 그 정체를 알아내기 위해 「노우누키 님」과 「쿠비타치」의 역사를 조사해보기로 하는데….', '모쿠모쿠렌 (지은이), 송재희 (옮긴이)', '디앤씨미디어(주)(D&C미디어)', 'https://image.aladin.co.kr/product/34285/60/cover200/k972932074_1.jpg', '국내도서>만화>순정만화>틴에이지 순정', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('포스 윙', '출간 즉시 미국, 영국, 독일, 호주 베스트셀러 1위에 오르는 동시에 “이제껏 본 적 없는 압도적인 판타지로맨스”라는 극찬을 받으며 2023년 아마존 올해의 책 선정에 이어 전 세계에 꺼질 줄 모르는 ‘은빛 팬덤’ 열풍을 몰고 온 최고의 화제작 《포스 윙》 한국어판이 드디어 출간되었다.', '레베카 야로스 (지은이), 이수현 (옮긴이)', '북폴리오', 'https://image.aladin.co.kr/product/34155/96/cover200/k192931590_1.jpg', '국내도서>소설/시/희곡>판타지/환상문학>외국판타지/환상소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('온전히 평등하고 지극히 차별적인', '‘아름다울 기회의 평등’은 모두에게 허락되는가? 변호사에서 무용수가 되는 새로운 경험을 거치는 가운데, 장애가 있는 몸으로 마주한 질문과 춤의 역사를 넘나들며 그가 오랫동안 천착해온 차별과 평등의 관계을 탐구한 기록이다.', '김원영 (지은이)', '문학동네', 'https://image.aladin.co.kr/product/34130/11/cover200/k112931290_2.jpg', '국내도서>인문학>교양 인문학', 1, 1);
