INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('인간은 노력하는 한 방황한다', '인문학자 김종원 작가의 100번째 책으로 세상에 내놓은 《김종원의 세계철학전집》. 여기에는 저자 김종원이 지난 16년 동안 같은 텍스트를 접하면서도, 사람들의 변화가 제각각인 이유에 관해 사색한 답이 들어있다.', '김종원 (지은이)', '마인드셋', 'https://image.aladin.co.kr/product/33782/79/cover200/k212930377_2.jpg', '국내도서>인문학>서양철학>서양철학 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('강인함의 힘 - 회복탄력성에 대한 오해 그리고 강인함의 비밀', '유명한 성과 전문가이자 경영 코치, 《피크 퍼모먼스》와 《열정 패러독스》의 공동 저자인 스티브 매그니스는 《강인함의 힘》을 통해 최신 스포츠 과학과 심리학에 바탕을 둔 새로운 회복탄력성 모델을 제안한다. 역경과 도전을 극복하는 데 도움이 되는 새로운 종류의 강인함, 즉 진정한 강인함을 처방한다.', '스티브 매그니스 (지은이), 이주만 (옮긴이)', '상상스퀘어', 'https://image.aladin.co.kr/product/33613/59/cover200/k722939915_2.jpg', '국내도서>자기계발>성공>성공학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('나에게 들려주는 예쁜 말', '인문학과 자녀교육 주제로 90여 권의 책을 써 온 김종원 작가는 그동안 아이들에게 어떻게 말해야 아이 내면을 단단하게 키울 수 있을지 강연하고 책을 쓰며 50만 학부모 독자들의 마음을 울렸다. 이번에는 처음으로 어린이를 위한 그림책 「김종원의 예쁜 말 시리즈」를 선보이며 따뜻한 그림과 함께 예쁜 말을 어린이들에게 전한다.', '김종원 (지은이), 나래 (그림)', '상상아이(상상아카데미)', 'https://image.aladin.co.kr/product/33273/48/cover200/k172938774_1.jpg', '국내도서>어린이>초등1~2학년>자기계발', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('퓨처 셀프 - 현재와 미래가 달라지는 놀라운 혁명', '‘미래의 나는 어떤 모습일까?’라는 질문은 우리가 인생에서 쉽게 놓치는 중요한 진실에 이르게 한다. 즉, 미래의 나와 연결될수록 현재 더 나은 삶을 살게 된다는 것이다. 이 책은 미래의 내가 어떤 모습일지 깊이 생각해보고, 지금 그 사람이 되는 방법을 구체적으로 알려주는 인생 지침서이다.', '벤저민 하디 (지은이), 최은아 (옮긴이)', '상상스퀘어', 'https://image.aladin.co.kr/product/32354/81/cover200/k342935366_1.jpg', '국내도서>자기계발>성공>성공학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('백년 허리 2 : 치료편 - 내 허리 사용 설명서', '서울대 의대 재활의학과 정선근 교수의 스테디셀러인 백년허리1 진단편에 이은 실제적인 치료 방법을 제시하고 있는 백년허리2 치료편이다. 치료편에서 저자는 허리 통증에서 벗어날 수 있는 방법을 정확히, 구체적으로, 누구나 따라할 수 있도록 설명하고 있다.', '정선근 (지은이)', '언탱글링', 'https://image.aladin.co.kr/product/27124/76/cover200/k272731146_1.jpg', '국내도서>건강/취미>질병치료와 예방>허리/디스크/척추/통증', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('순례 주택', '블루픽션 81권. 한국어린이도서상, IBBY 어너리스트 수상작가 유은실의 신작 청소년 소설『순례 주택』이 비룡소에서 출간되었다. 약 3년 만에 발표되는 이번 새 청소년 소설에서도 독특한 캐릭터, 유머, 촌철살인의 진한 메시지까지 작가 특유의 장기를 보여 준다.', '유은실 (지은이)', '비룡소', 'https://image.aladin.co.kr/product/26674/56/cover200/8949123495_1.jpg', '국내도서>청소년>청소년 문학>청소년 소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('1984 (오리지널 초판본 표지 디자인)', '조지 오웰이 남긴 마지막 정치소설. 1984년 핵전쟁 이후의 오세아니아라는 가상의 전체주의 사회를 설정하여 언어를 제한해 개인의 사고를 통제하고, 과거의 기록을 끊임없이 수정하며, 세뇌와 억압을 통해 거짓을 진실이라 강요하는 암울한 디스토피아를 치밀한 필치로 그려냈다.', '조지 오웰 (지은이), 박유진 (옮긴이), 박경서 (해설)', '코너스톤', 'https://image.aladin.co.kr/product/24555/31/cover200/s642937439_1.jpg', '국내도서>소설/시/희곡>영미소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('지적 대화를 위한 넓고 얕은 지식 2 - 현실 편 : 철학 / 과학 / 예술 / 종교 / 신비', '''제로'' 편에 이어, 출간 5년 만에 처음으로 개정증보판이 나왔다. 거칠고 부족했던 부분은 부드럽게 매만지고 채워 넣었다. 낡은 이야기는 시대에 맞게 바꿨다. 꼼꼼하게 수선된 개정판을 읽는 순간, 왜 지난 5년간 이 책의 아성이 깨지지 않았는지 확인하게 될 것이다.', '채사장 (지은이)', '웨일북', 'https://image.aladin.co.kr/product/22872/85/cover200/k912636841_1.jpg', '국내도서>인문학>교양 인문학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('순재와 키완 - 두 아이가 만난 괴물에 대한 기록, 제19회 문학동네어린이문학상 대상 수상작', '보름달문고 75권. ‘절망적인 색채의 미래 전망들과 어떤 공식처럼 지구와 인간의 파멸을 그린 작품들 속에서 지금 여기가 맨 앞’이라는 메시지를 전하며 제19회 문학동네어린이문학상 대상을 받은 작품이다.', '오하림 (지은이), 애슝 (그림)', '문학동네', 'https://image.aladin.co.kr/product/17381/81/cover200/8954653502_1.jpg', '국내도서>어린이>동화/명작/고전>국내창작동화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('허구의 철학 - 지속 가능한 삶과 정신을 위한 허구의 올바른 자리 찾기', '포스트모더니즘 그리고 디지털 혁명기를 지나며 우리는 실재와 허구의 구분이 모호해졌다고 여기는, 이른바 〈탈사실적 시대〉의 존재론적 혼란에 사로잡혔다. 유한성에서 벗어나고자 하는 너무도 인간적인 욕망에서 비롯한 이 경향성은, 그러나 허구를 실재에서 분리하여 별도의 영역으로 가두어 버림으로써 오히려 인간 상상력의 경계를 스스로 좁히는 결과를 불러온다.', '마르쿠스 가브리엘 (지은이), 전대호 (옮긴이)', '열린책들', 'https://image.aladin.co.kr/product/34526/67/cover200/8932924589_1.jpg', '국내도서>인문학>서양철학>서양철학 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('언데드 언럭 21 - 1분', '립과 라트라, 두 사람이 마스터 룰(절대적 이치) 텐스(10위) 식과의 전투에 참전한다. 그런 가운데, 식과의 전투와 병행해 진행되는 라일라의 수술에 고전을 면치 못하는 후코 일행. 절체절명의 위기에 닥친 후코 앞에, 마침내 그 남자가 모습을 드러낸다.', '토즈카 요시후미 (지은이)', '대원씨아이(만화)', 'https://image.aladin.co.kr/product/34526/43/cover200/k582932853_1.jpg', '국내도서>만화>본격장르만화>판타지>드라마틱 판타지', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2025 이기적 빅데이터분석기사 필기 기본서 - 동영상 강의 무료 + CBT 온라인 문제집 제공', '최신 출제기준을 적용한 도서로, 빅데이터분석기사 필기 시험의 출제 경향을 철저히 분석하여 수험생들이 혼자서도 학습할 수 있도록 한 완벽 대비서이다. 시행처인 한국데이터산업진흥원에서 공개한 출제기준을 바탕으로 집필되었다.', '나홍석, 배원성, 이건길, 이혜영, 고려사이버대학교 AI.빅데이터 연구소 (지은이)', '영진.com(영진닷컴)', 'https://image.aladin.co.kr/product/34393/72/cover200/893147718x_1.jpg', '국내도서>컴퓨터/모바일>활용능력>신규 자격증/자격증 기타', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('뭉크의 별이 빛나는 밤 - 고독 속 절규마저 빛나는 순간', '세기의 전환기에 현대 미술에 가장 큰 영향을 미친 화가 중 하나로 꼽히는 에드바르 뭉크(1863년~1944년). 올해는 뭉크가 사망한 지 80주기를 맞이하는 해이다. 『뭉크의 별이 빛나는 밤』은 우리가 몰랐던 표현주의의 거장 뭉크의 삶 속으로 우리를 초대한다.', '이미경 (지은이)', '더블북', 'https://image.aladin.co.kr/product/34387/21/cover200/k262932500_1.jpg', '국내도서>예술/대중문화>미술>미술가', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('반딧불이의 혼례 2', '때는 메이지. 살날이 얼마 남지 않은 화족 영애 사토코는, 납치되어 ‘유녀도’에 갇혀버린다. 살인 청부업자 신페이를 자신의 편으로 만들기 위해, 거짓 약혼을 하지만, 신페이에게 기이할 정도의 사랑을 받으며 도망칠 수 없는 상황이 되는데….', '타치바나 오레코 (지은이), 유유리 (옮긴이)', '서울미디어코믹스(서울문화사)', 'https://image.aladin.co.kr/product/34195/72/cover200/k382931411_1.jpg', '국내도서>만화>본격장르만화>판타지>드라마틱 판타지', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('독학으로 끝내는 공기업 NCS 통합기본서 (PSAT형+모듈형+피듈형) - 전2권 - 한국전력공사·한국철도공사·한국도로공사·한국가스공사·수자원공사·한국수력원자력·국민건강보험공단·근로복지공단·국민연금공단·인천국제공항공사·한국토지주택공사·중소벤처기업진흥공단·농어촌공사·한국관광공사·한국공항공사·한국남동발전·한국중부발전·지역난방공사·서울교통공사·부산교통공사', '실제 다수의 기업에서 출제하는 유형인 PSAT형 개념과 문제 및 해설이 강화된 통합기본서다. 합격자의 풀이순서 및 시간단축 Tip을 제시하는 등 공기업 취업준비에 있어 NCS에 대한 정보가 부족한 입문자 및 기 학습자들에게 실질적인 도움을 주고자 만들어졌다.', '길잡이연구소, 애드투북스 (지은이)', '애드투북스', 'https://image.aladin.co.kr/product/34168/42/cover200/k302931916_2.jpg', '국내도서>수험서/자격증>취업/상식/적성>공사 공단 수험서>기타', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('석가모니 인생수업 - 석가모니가 세상에 남긴 삶의 지혜', '독자들에게 석가모니의 가르침을 쉽게 접근하고 이해할 수 있는 방법을 제공하며, 석가모니의 가르침을 바탕으로 한 다양한 주제들을 현대적으로 다룬 책이다.', '석가모니 (지은이), 김지민 (엮은이)', '하이스트', 'https://image.aladin.co.kr/product/34017/27/cover200/k812931062_1.jpg', '국내도서>종교/역학>불교>불교명상/수행', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('키위엔 영어회화 하루 5분의 기적 - 기초도 3개월이면 회화가 되는 특허 받은 영어 학습법', '영어의 어순과 문장의 구조를 쉽게 이해시켜주며 그로 인해 더 이상 문장을 통째로 외우지 않더라도 스스로 원하는 문장을 만들어 자유롭게 말할 수 있게 해준다. 이 책의 마지막 페이지를 넘겼을 때 여러분의 영어가 어느새 중급이 되어 있는 놀라운 경험을 해보자.', '박강준 (지은이)', '키위엔', 'https://image.aladin.co.kr/product/30539/13/cover200/s932936383_3.jpg', '국내도서>외국어>영어회화>생활영어', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('국립존엄보장센터', '전국국어교사모임 독서교육분과 물꼬방 교사들이 기획한 ‘함께 읽는 소설’ 시리즈 첫 권. 하나의 주제를 다각도로 고민하게 이끄는 작품들을 가려 엮은 테마소설선집. 빛나는 상상력으로 사고의 관성을 허무는 우리 시대 SF 작가 남유하, 원종우, 김이환, 김주영, 김창규의 작품을 한 권에 담았다.', '남유하, 원종우, 김이환, 김주영, 김창규, 김진영 (지은이), 김애연, 김영희, 최지혜 (엮은이)', '서해문집', 'https://image.aladin.co.kr/product/29307/46/cover200/k662837605_2.jpg', '국내도서>청소년>청소년 문학>청소년 소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('채식주의자 (리마스터판)', '2016년 인터내셔널 부커상을 수상하며 한국문학의 입지를 한단계 확장시킨 한강의 장편소설. 상처받은 영혼의 고통과 식물적 상상력의 강렬한 결합을 정교한 구성과 흡인력 있는 문체로 보여주며 섬뜩한 아름다움의 미학을 한강만의 방식으로 완성한 역작이다.', '한강 (지은이)', '창비', 'https://image.aladin.co.kr/product/29137/2/cover200/8936434594_1.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이후 한국소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('전설로 떠나는 월가의 영웅 - 주식투자에서 상식으로 성공하는 법, 2021 최신개정판', '월가의 전설적인 인물 피터 린치가 개인투자자들을 위해 1989년에 출간했던 《월가의 영웅》최신 개정판이다. 월가에서 은퇴한 후 처음으로 저술한 책으로, 자서전 형식을 띠면서 주식투자에 관한 저자의 철학을 유쾌하게 서술한 투자지침서이기도 하다.', '피터 린치, 존 로스차일드 (지은이), 이건 (옮긴이), 홍진채 (감수)', '국일증권경제연구소', 'https://image.aladin.co.kr/product/27663/60/cover200/8957825940_1.jpg', '국내도서>경제경영>재테크/투자>주식/펀드', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('성취하는 뇌 - 기억력·집중력·공부머리를 끌어올려 최상의 뇌로 이끄는 법', '‘최상의 성과를 내는 뇌’의 비밀을 파헤친 책 《성취하는 뇌》는 독일의 대표적인 신경과학자 마르틴 코르테 박사의 최신작이다. 저자는 책에서 특정 영역에만 적용되거나 일회성에 그치는 단순한 뇌 단련법 대신, 최신 뇌과학과 신경학을 기반으로 연령별·단계별 ‘최적화된’ 두뇌 훈련법을 제시한다.', '마르틴 코르테 (지은이), 손희주 (옮긴이)', '블랙피쉬', 'https://image.aladin.co.kr/product/25358/39/cover200/8968332797_2.jpg', '국내도서>자기계발>창의적사고/두뇌계발', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('사업의 철학 - 성공한 사람들은 절대 말해 주지 않는 성공의 모든 것', '마이클 거버 컴퍼니의 창립자이자 CDO인 마이클 E. 거버의 책. 이 책은 미국에서 출간 즉시 경영의 고전이 된 책으로, 사업에 대해 아무것도 모른 채 무작정 창업에 뛰어들었다가 이내 지옥의 고통을 맛봐야 했던 수많은 사업가들을 폐업의 위기로부터 구한 밀리언셀러다.', '마이클 E. 거버 (지은이), 이제용 (옮긴이)', '라이팅하우스', 'https://image.aladin.co.kr/product/6579/50/cover200/8998075202_2.jpg', '국내도서>경제경영>기업 경영>경영전략/혁신', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('데드데드 데몬즈 디디디디 디스트럭션 8', '오오바에게 충격적인 사실을 듣게 된 마코토. 두 사람은 오오바가 가진 장치를 사용해 오란의 기억을 찾는 여행을 떠난다. 현재로부터 8년 전. 그곳에는 지금과는 다른, 초등학교 4학년 오란과 카도데가 있었다.', '아사노 이니오 (지은이), 유유리 (옮긴이)', '서울미디어코믹스(서울문화사)', 'https://image.aladin.co.kr/product/34548/24/cover200/k532933061_1.jpg', '국내도서>만화>본격장르만화>판타지>드라마틱 판타지', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('종이접기 매니아를 위한 Origami PRO : 신화 종이접기편', '신화 속 동물과 존재들을 생동감 넘치는 종이접기로 표현한 작품들이다. 수록된 각 작품은 신화적 인물을 형상화하여, 한 장의 종이로 신화 속 상징을 재현하는 특별한 경험을 선사한다. 이 작품들은 (사)한국종이접기협회의 수석창작위원인 김진우, 박종우, 장용익, 이인섭, 맹형규, 정재일, 한지우, 유태용 작가들이 참여하여 완성되었다.', '김진우, 박종우, 장용익, 이인섭, 맹형규, 정재일, 한지우, 유태용 (지은이)', '한국종이접기협회', 'https://image.aladin.co.kr/product/34529/83/cover200/k312932951_1.jpg', '국내도서>건강/취미>공예>종이접기/종이공예', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('임영웅, 우리들의 미라클 히어로 - 영웅시대와 함께한 800여 일의 현장 기록', '임영웅 팬튜브로 많은 사랑을 받고 있는 &lt;앵커리의 똑TV&gt; 제작진이 영웅시대가 되어, 별처럼 빛나는 수많은 영웅시대와 함께한 800여 일간의 현장 기록이다. 2022년 5월 역사적인 임영웅 콘서트의 시작을 알리는 고양 콘서트부터 2024년 서울월드컵경기장 ‘2024 아임 히어로-더스타디움’까지 &lt;앵커리의 똑TV&gt;는 임영웅 콘서트 현장을 다니면서 수많은 영웅시대들을 인터뷰했다.', '&lt;앵커리의 똑TV&gt; 제작진 (지은이)', '서사원', 'https://image.aladin.co.kr/product/34527/3/cover200/k022932855_2.jpg', '국내도서>예술/대중문화>음악>음악가', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('속삭임 우묵한 정원', '배수아를 수식하는 단어들 중 가장 즐겨 사용되었던 단어는 ‘낯섦’ 혹은 ‘이국적인’이다. 두 단어의 이면에 구축하고 있는 의미는 아마도 ‘새로움’일 것이다. 그러므로 배수아는 낯설어 새롭고 이국적이라 새롭다. 배수아가 5년 만에 신작 장편소설로 돌아왔다. 아주 멀고도 우묵한 곳에서 올라오는 속삭임들이 홑씨들처럼 퍼져나가 한 번도 가보지 못한 새로운 영토에 내려앉아 발아된다.', '배수아 (지은이)', '은행나무', 'https://image.aladin.co.kr/product/34316/66/cover200/s322932503_1.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이후 한국소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('임신 출산 육아 대백과 (최신개정판) - 술술 잘 읽히는 첫아기 잘 키우는 법', '모든 것이 어색하고 새로운 예비 엄마를 위해 눈으로 읽을 수 있도록 구성했다. 모유 수유를 위한 가슴 마사지, 신생아 돌보기 등 글로만 읽어서는 잘 이해하기 어려운 내용, 배 속 태아의 성장처럼 막연한 내용들을 동영상식 구성과 사진으로 선명하게 보여준다.', '삼성출판사 편집부 (엮은이), 김진영, 김영훈 (감수)', '삼성출판사', 'https://image.aladin.co.kr/product/34274/93/cover200/8915999002_1.jpg', '국내도서>좋은부모>임신/출산', 1, 1);
