INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('흔한남매 16', '평범한 일상에 웃음을 불어넣는 흔한남매의 통통 튀는 이야기. 에이미의 화 참기 도전, 뮤지컬 전쟁을 펼치는 흔한남매, 아이돌 학교에서 살아남는 방법, 너튜버가 된 엄마, 데굴데굴 왕풍선 체험, 공포의 노래방 안내 방송 등 개성이 통통 튀는 흔한남매의 일상을 들려준다.', '흔한남매 (지은이), 백난도 (글), 유난희 (그림), 흔한컴퍼니 (감수)', '미래엔아이세움', 'https://image.aladin.co.kr/product/33815/12/cover200/k832930683_2.jpg', '국내도서>어린이>TV/만화/영화>만화 일반', 1, 1);
