INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('데미안', '2차 세계대전 중 많은 독일 젊은이들이 전장에 나가면서 군복 주머니 속에 품고 갔던 책. 어른이 되기 위해 보이지 않는 껍질을 깨고 고통스런 현실의 세계로 나서는 젊은이들을 그렸다. 지금까지도 젊은이들에게 ''통과의례''처럼 읽히고 있는 명작을 새로 옮겼다.', '헤르만 헤세 (지은이), 전영애 (옮긴이)', '민음사', 'https://image.aladin.co.kr/product/26/0/cover200/s742633278_1.jpg', '국내도서>소설/시/희곡>독일소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('하나노이 군과 상사병 15', '야오의 위기 앞에서 하나노이와의 약속을 깨고 만 호타루. 애써 괜찮은 척 상처 입은 하나노이의 모습을 본 호타루는 관계를 회복하고픈 마음에 사귄지 1주년이 된 기념일, 한 단계 나아가고 싶다는 대담한 제안을 하는데...?!', '모리노 메구미 (지은이)', '대원씨아이(만화)', 'https://image.aladin.co.kr/product/34474/46/cover200/k362932633_1.jpg', '국내도서>만화>순정만화>틴에이지 순정', 1, 1);
