INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('동경일일 3 - 완결', '30년간 일했던 대형 출판사를 나와 줄곧 꿈꾸던 만화 잡지를 만들기로 한 시오자와. 자신이 사랑했던 만화가들을 찾아가 집필진을 모으지만, 출간까지의 나날은 험난하기만 하다. 과연 시오자와는, 만화가들은 무사히 ‘꿈의 잡지’를 펴낼 수 있을까?', '마츠모토 타이요 (지은이), 이주향 (옮긴이)', '문학동네', 'https://image.aladin.co.kr/product/34193/44/cover200/k712931315_1.jpg', '국내도서>만화>논픽션/휴먼만화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('어른의 기분 관리법 - 감정에 휘둘려 손해 봤던 어른을 위한 조언', '어른을 위한 구체적인 기분 관리법. 이 책에서는 이 시대를 살아가는 모든 어른을 위한 구체적인 지침을 제시한다. 우리가 모호하게 알고 있는 기쁨, 슬픔, 행복, 상실 모든 감정에 대해 저자의 경험담부터 전문가의 견해까지 담겨 읽기 쉬우면서도 독자 입장에서 일상 속에서 따라할 수 있게 내용이 구성되어 있다.', '손힘찬, 이영탁, 이현정, 승PD, 박은주, 아이릿, 이주희, 김영미, 이민영 (지은이)', '어센딩', 'https://image.aladin.co.kr/product/33973/61/cover200/k102930444_1.jpg', '국내도서>에세이>한국에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('파과 (리커버)', '한국 소설에 가장 강렬하게 새겨질 새로운 여성 서사를 탄생시킨 구병모 작가의 《파과》가 새 옷을 갈아입었다. 40여 년간 날카롭고 냉혹하게 청부 살인을 업으로 삼아온 60대 여성 킬러 ‘조각(爪角)’. 몸도 기억도 예전 같지 않게 삐걱거리기 시작하면서 이제는 퇴물 취급을 받는다.', '구병모 (지은이)', '위즈덤하우스', 'https://image.aladin.co.kr/product/33728/6/cover200/k262939544_2.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이후 한국소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('작은 땅의 야수들 (리커버 특별판)', '베스트셀러 『작은 땅의 야수들』이 리커버 특별판으로 출간되었다. 호랑이와 인간이 대치하는 강렬한 프롤로그로 시작하는 『작은 땅의 야수들』은 혼란스러운 시대에 서로 다른 욕망을 품은 다양한 인간 군상들이 운명적으로 얽혀 흥망성쇠하는 장대한 대서사시다.', '김주혜 (지은이), 박소현 (옮긴이)', '다산책방', 'https://image.aladin.co.kr/product/31793/43/cover200/k212833117_1.jpg', '국내도서>소설/시/희곡>영미소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('사카모토 데이즈 2 - 하드보일드', '사카모토를 노리는 기묘한 킬러와 대치하는 신. 생각을 읽을 수 없는 적에게 대응할 방법은...?! 어떻게든 놀이공원에서의 평화로운 휴일을 사수하려는 사카모토 일행에게 강력한 킬러 콤비의 마수가 뻗어온다!! 가족들에게 비밀로 진행되는 초고난도 미션의 결과는?!', '스즈키 유우토 (지은이)', '대원씨아이(만화)', 'https://image.aladin.co.kr/product/28463/97/cover200/k552835825_1.jpg', '국내도서>만화>코믹/명랑만화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('백년 허리 1 : 진단편 - 내 허리 통증 해석하기, 개정증보판', '서울대 의대 재활의학과 정선근 교수의 스테디셀러인 ''백년허리''의 개정증보판이다. 백년허리의 초판에서 고쳐야 할 부분을 대거 보충 하였을 뿐 아니라, 허리 통증은 진화의 축복이라는 요통에 대한 새로운 관점을 제시하고 있다.', '정선근 (지은이)', '언탱글링', 'https://image.aladin.co.kr/product/26952/44/cover200/k872730815_2.jpg', '국내도서>건강/취미>질병치료와 예방>허리/디스크/척추/통증', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('이방인', '''민음사 세계문학전집'' 266권. 20세기의 지성이자 실존주의 문학의 대표 작가, 알베르 카뮈의 대표작. 1942년 &lt;이방인&gt;이 처음 발표되었을 때, 카뮈는 젊은 무명작가에 불과했다. 낯선 인물과 독창적인 형식으로 현대 프랑스 문단에 이방인처럼 나타난 이 소설은 출간 이후 한순간도 프랑스 베스트셀러 목록에서 빠진 적이 없는 걸작이 되었다.', '알베르 카뮈 (지은이), 김화영 (옮긴이)', '민음사', 'https://image.aladin.co.kr/product/21224/66/cover200/8937443848_1.jpg', '국내도서>소설/시/희곡>프랑스소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('생각에 관한 생각 - 우리의 행동을 지배하는 생각의 반란!', '2002년 심리학자로는 최초로 노벨경제학상을 수상한 ‘행동경제학’의 창시자이자, 세계에서 7번째로 영향력이 막강한 경제학자(〈이코노미스트〉 선정, 2015)인 대니얼 카너먼의 기념비적인 저작. 최신판에는 번역과 편집을 보강해 세계적인 석학의 이론과 연구 결과를 더욱더 흥미롭고 충실하게 선보인다.', '대니얼 카너먼 (지은이), 이창신 (옮긴이)', '김영사', 'https://image.aladin.co.kr/product/14013/55/cover200/8934981210_1.jpg', '국내도서>경제경영>경제학/경제일반>경제이론/경제사상', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('망각 배터리 13', '코테사시 고등학교를 견학하러 온 호우야 시니어의 후배 타키와 히노모토. 그들의 도발에 카나메 일행은 한 타석 승부를 하게 되는데……. 여자 매니저가 던지는 공이 타키에게 얻어맞지 않도록 지장 카나메의 리드로 잘 커버해 가는 사이, 카나메는 리드의 복잡함을 알게 된다.', 'Eko Mikawa (지은이), 유유리 (옮긴이)', '서울미디어코믹스(서울문화사)', 'https://image.aladin.co.kr/product/34548/26/cover200/k692933061_1.jpg', '국내도서>만화>스포츠만화', 1, 1);
