INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('조직신학 1~2 세트 - 전2권 - 전면 개정증보판', '웨인 그루뎀의 대표작 『조직신학』 전면 개정증보판은 복음주의 신학의 모든 주제를 누구나 이해하기 쉽도록 명료하게 풀어낸 성경 교리 입문서, 1994년부터 현재까지 30년 동안 전 세계 100만 독자들이 애독해 온 조직신학 교과서, 신학이 지식에만 머물지 않고 마음을 움직여 하나님을 예배하도록 이끄는 교회를 위한 안내서, 교파와 세대를 아우르고 신학자, 목회자, 평신도가 함께 읽고 적용하는 모든 그리스도인의 필독서이다.', '웨인 그루뎀 (지은이), 박세혁 (옮긴이)', '복있는사람', 'https://image.aladin.co.kr/product/34371/20/cover200/k492932102_1.jpg', '국내도서>종교/역학>기독교(개신교)>기독교(개신교) 목회/신학>신학일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('초등 첫 문해력 신문 - 읽기로 시작해 쓰기로 완성하는', '7세~초등학교 4학년에 ‘읽는 습관’을 형성한 아이들은 낯선 글을 만나도 거침없이 읽어 내고 이해하며 새로운 지식을 습득한다. 이러한 아이들은 스스로 해낼 수 있다는 자신감, 자신을 인정하는 자존감까지 얻어 더 단단한 삶을 살아간다. 저자는 이 시기의 모든 아이들이 읽는 습관을 형성하도록 도와야겠다는 사명감으로 이 책을 만들었다.', '이다희 (지은이), 서희진 (그림)', '아울북', 'https://image.aladin.co.kr/product/34297/29/cover200/k552932277_1.jpg', '국내도서>어린이>책읽기/글쓰기>책읽기', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('평등의 짧은 역사', '세계적 차원의 불평등과 평등을 향한 역사적 움직임, 그리고 평등한 세계로 가는 방법. 저자는 평등을 향한 여정은 오래전에 시작된 투쟁의 역사이고, 이 투쟁은 21세기에도 여전히 계속되어야 한다고 주장한다.', '토마 피케티 (지은이), 전미연 (옮긴이)', '그러나', 'https://image.aladin.co.kr/product/34209/57/cover200/k072932228_2.jpg', '국내도서>사회과학>사회문제>빈곤/불평등문제', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('세계를 움직인 열 가지 프레임 - 현대 문명의 본질과 허상을 단숨에 꿰뚫는 세계사', '‘누구의 말도 그대로 믿지 말라’, ‘아는 것이 힘이다’, ‘시간은 돈이다’. ‘펜은 칼보다 강하다’… 누구나 들어봤을 법한 이러한 말들은 믿어 의심치 않은 지혜로 우리 사회에서 수용되고 있다. 『세계를 움직인 열 가지 프레임』은 현대 문명의 성취이자, 오랜 시간 지켜온 신념으로 공유되는 열 가지 핵심 가치의 이면을 살펴보며, 역사와 우리의 생각에 어떤 영향을 끼쳤는지 파헤친다.', '수바드라 다스 (지은이), 장한라 (옮긴이)', '북하우스', 'https://image.aladin.co.kr/product/34035/78/cover200/k512931265_2.jpg', '국내도서>역사>세계사 일반', 1, 1);
