INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('마법의 연금 굴리기 - 연금저축, IRP, ISA 절세 삼총사를 ETF로 자산배분하라, 전면 개정판', '20대부터 50대까지 자영업자와 월급쟁이를 위한 절세와 노후 준비를 돕기 위해 쓰였다. 바로 개인연금으로 연금저축펀드를, 퇴직연금으로 IRP를, 절세 계좌로 ISA를 활용하여 안전하면서도 수익을 내는 방법을 안내한다.', '김성일 (지은이)', '에이지21', 'https://image.aladin.co.kr/product/32581/2/cover200/8998342782_1.jpg', '국내도서>경제경영>재테크/투자>재테크/투자 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('설민석의 우리 고전 대모험 3 - 춘향전', '우리 민족의 정서와 삶의 모습이 고스란히 녹아 있는 우리나라 대표 고전 소설을 전기수가 된 설쌤의 입을 통해 들려주는 새로운 대모험 시리즈다. 재미있고 교훈적인 고전 소설을 줄글과 학습 만화가 혼합된 읽기물 형식으로 엮어 이야기에 재미와 깊이를 더했다.', '설민석, 최설희 (지은이), 강신영 (그림), 류수열 (감수)', '단꿈아이', 'https://image.aladin.co.kr/product/34481/85/cover200/k012932648_1.jpg', '국내도서>어린이>동화/명작/고전>어린이를 위한 고전', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('마법천자문 63 - 목표물에 가까이 다가가다! 접근 接近', '2,000만 독자가 선택한 마법천자문은 2003년 첫 출간된 이후, 지난 20년간 한자 학습의 열풍을 일으키며 어린이들에게 큰 사랑을 받아왔다. 54권부터『마법천자문』은 1~53권을 통해 익혔던 하나하나의 한자들을 ''어휘력''이라는 한층 고차원적인 방식으로 접근한다.', '스튜디오 훈훈 (지은이), 정수영 (그림), 강용철 (감수)', '아울북', 'https://image.aladin.co.kr/product/34311/64/cover200/k532932376_1.jpg', '국내도서>어린이>어린이 한자>학습만화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('블루 록 : Episode 나기 1', '『천재』란 찾아주는 사람이 있어야 비로소 그 윤곽을 드러낸다. 나기 세이시로, 고등학교 2학년.  "귀찮아"가 입버릇인 소년은 매일을 무기력하게 지내고 있었다. 일본 축구 월드컵 우승을 목표를 위해 설립된 육성 기숙사 "블루 록"과 파트너 미카게 레오가 그 재능을 발견하기 전까지는――.', '산노미야 코타 (지은이), 카네시로 무네유키 (원작), 노무라 유스케 (그림)', '학산문화사(만화)', 'https://image.aladin.co.kr/product/34259/69/cover200/k392932962_1.jpg', '국내도서>만화>스포츠만화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2025 선재국어 수비니겨 논리 - 가장 쉽고도 깊이 있는 공무원 논리', '2025 출제 기조 전환으로 새롭게 바뀌는 공무원 국어 시험에 맞추어, 신유형인 논리 영역에 완벽하게 대비할 수 있도록 만든 교재다. ‘서술형 이론 - 주관식 연습하기 - 객관식 실전 문제’의 체계적인 구성으로 빠르게 논리 문제를 푸는 방법을 익힐 수 있다.', '이선재, 선재국어연구소 (지은이)', '에스티유니타스', 'https://image.aladin.co.kr/product/34183/19/cover200/k732931311_1.jpg', '국내도서>수험서/자격증>공무원 수험서>7/9급 공무원>국어>7/9급 기본서 및 부교재', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('레버리지 (20만 부 기념 블랙에디션) - 자본주의 속에 숨겨진 부의 비밀', '수많은 젊은 부자가 선택한 『레버리지』의 20만 부 돌파 블랙 에디션이 출간되었다. 라이프해커 자청, 브라이언 트레이시가 강력 추천한 이 책은 2017년 국내 출간 이후 한국 독자들의 꾸준한 사랑을 받아 경제경영 바이블로 자리 잡았다. 아직까지 『레버리지』를 보지 못한 사람이라면 어떻게 읽고 활용해야 할까?', '롭 무어 (지은이), 김유미 (옮긴이)', '다산북스', 'https://image.aladin.co.kr/product/31064/69/cover200/k842831525_2.jpg', '국내도서>자기계발>성공>성공학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('사카모토 데이즈 6 - 운이 나쁘구나', '흉악 사형수,ORDER, 사카모토 상점. 마주치는 순간 서로 죽고 죽이는 혈투가 시작된다. 각개 각소에서 긴박한 전투가 펼쳐지는 가운데, 사카모토의 몸에 이변이 발생한다. 뒤에서 모든 것을 조종하는 ''x(슬러)''의 진정한 노림수가 밝혀질 때, 킬러계가 거세게 휘청인다.', '스즈키 유우토 (지은이)', '대원씨아이(만화)', 'https://image.aladin.co.kr/product/30113/38/cover200/k952839984_1.jpg', '국내도서>만화>코믹/명랑만화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('한 권으로 끝내는 해커스 토익 700+ (LC+RC+VOCA) - 최신기출유형 100%반영ㅣ기초부터 실전까지 20일 완성 [실전모의고사 2회(교재1회+온라인1회) 제공]', '토익 기초부터 실전까지 한 권으로 끝낼 수 있도록 구성한 교재. 확실한 목표점수 달성을 위한 ''기초다지기 & 영역별 기출 핵심 포인트''와 최신 신토익 유형을 완벽 반영한 풍부하고, 다양한 문제를 수록하였고, 상세한 강의식 해설을 담았다.', '해커스어학연구소 (지은이)', '해커스어학연구소(Hackers)', 'https://image.aladin.co.kr/product/28571/61/cover200/8965424623_1.jpg', '국내도서>외국어>토익>Listening', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('지구 끝의 온실', '김초엽 첫 장편소설. 『우리가 빛의 속도로 갈 수 없다면』을 통해 이미 폭넓은 독자층을 형성하며 열렬한 사랑을 받고 있는 김초엽 작가는 더스트로 멸망한 이후의 세계를 첫 장편소설의 무대로 삼았다.', '김초엽 (지은이)', '자이언트북스', 'https://image.aladin.co.kr/product/27692/63/cover200/s222930473_1.jpg', '국내도서>소설/시/희곡>과학소설(SF)>한국 과학소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('삼체 1~3 세트 - 전3권 (출간 10주년 기념 특별판 양장)', '아시아 최초로 휴고상을 수상하며 SF 거장으로 등극한 류츠신의 『삼체』의 개정 양장본 세트. 『1부―삼체문제』 『2부―암흑의 숲』 『3부―사신의 영생』으로 이어지는 ‘지구의 과거’ 3부작은 무수한 위험이 숨어 있는 ‘암흑의 숲’과 같은 우주에서 인류가 마주하게 될 운명을 대담한 상상력으로 그려냈다.', '류츠신 (지은이), 이현아, 허유영 (옮긴이)', '자음과모음', 'https://image.aladin.co.kr/product/24394/37/cover200/s142931089_1.jpg', '국내도서>소설/시/희곡>과학소설(SF)>외국 과학소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('삼국지 원전 완역판 세트 - 전10권', '삼국지는 중국의 후한 말부터 100여 년간 이어진 영웅호걸들의 흥망치란을 한데 담아낸 역사 소설로, 오늘날까지 많은 사랑을 받는 고전 작품 중 하나다. 그중 요시카와 에이지판은 나관중 원작의 고전에 가장 충실하면서도 생동감 넘치는 인물 묘사와 역사적 사실에 대한 재해석으로 높은 평가를 받고 있는 작품이다.', '요시카와 에이지 (엮은이), 나관중 (원작), 바른번역 (옮긴이)', '코너스톤', 'https://image.aladin.co.kr/product/23413/54/cover200/k072638169_1.jpg', '국내도서>소설/시/희곡>역사소설>외국 역사소설', 1, 1);
