INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('지금 사랑한다고 말하세요 - 당신에게 들려주고 싶은 80가지 짧은 이야기', '“당신의 사랑은 무엇인가요? 우리 삶은 나만의 사랑을 찾아 떠나는 여정입니다.” 수백 만 독자의 마음을 움직인 대한민국 대표강사 김창옥의 인생 해설집. 지난 20여 년간, 약 1만 회에 달하는 강연을 축적해 ‘변화와 전환을 가져올 작은 이야기들’을 한데 모았다.', '김창옥 (지은이)', '수오서재', 'https://image.aladin.co.kr/product/34483/46/cover200/k562932642_2.jpg', '국내도서>자기계발>힐링>마음 다스리기', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('구름관찰자를 위한 그림책', '구름 감상을 시작하려는 초보 구름관찰자들을 위한 감성적인 안내서이자, 구름 감상에 매료된 전 세계 구름관찰자들에게는 근사한 선물이 될 책. ‘구름감상협회’를 설립하고 시종일관한 구름 사랑을 이어온 개빈 프레터피니가 글을 쓰고, 케이트 그리너웨이상과 볼로냐 라가치상을 수상한 저명한 일러스트레이터 윌리엄 그릴이 그림을 그렸다.', '개빈 프레터피니 (지은이), 윌리엄 그릴 (그림), 김성훈 (옮긴이)', '김영사', 'https://image.aladin.co.kr/product/34410/12/cover200/8934933925_1.jpg', '국내도서>과학>지구과학>기상학/기후', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2025 심슨 독해', '『심슨 독해』는 『심슨 영어』의 세 번째 기본서로, 특히 개편된 시험에서 새롭게 출제되는 실용문(이메일, 안내문, 웹페이지 글) 유형에 대한 대비뿐만 아니라 기존 유형에 대한 기본기도 철저하게 다질 수 있도록 했다.', '심우철 (지은이)', '심슨북스', 'https://image.aladin.co.kr/product/34361/57/cover200/k962932904_1.jpg', '국내도서>수험서/자격증>공무원 수험서>7/9급 공무원>영어>단어/문법/독해 외', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('한국사는 없다 - 더 넓은 시각으로 바라본 우리 역사', '기후학, 지리학, 사회학 등의 역사 외적인 요소와 당대의 세계정세, 시대의 변화라는 폭 넓은 시각에서 한국사를 해석한 책이다. 환웅과 단군으로부터 일제 강점기에 이르는 오천 년 우리 역사를 통사적으로 훑어 내려오다가 한국사의 물줄기를 바꾼 결정적 사건에 이르러 깊이 파고들어가는 방식을 취한다.', '유성운 (지은이)', '페이지2(page2)', 'https://image.aladin.co.kr/product/34349/50/cover200/k042932990_1.jpg', '국내도서>역사>한국사 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('몸 번역하기', '『마이너 필링스』로 독자들에게 큰 사랑을 받은 캐시 박 홍의 시집. 언어와 몸의 경계, 분열, 충돌을 깊이 파고드는 이 시집에서 캐시 박홍은 영어와 한국어 사이, 제국과 순교자의 나라 사이, 몸과 마음 사이에서 끝없이 자신을 들여다보며, 시를 통해 소수자 되기를 실천한다.', '캐시 박 홍 (지은이), 정은귀 (옮긴이)', '마티', 'https://image.aladin.co.kr/product/34167/76/cover200/k162932648_2.jpg', '국내도서>소설/시/희곡>시>외국시', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2025 전한길 한국사 합격생 필기노트 (스프링)', '이미 완성된 교재로 검증받은 『전한길 한국사 합격생 필기노트』이기에 매년 개정 작업에는 더욱 심혈을 기울이고 있다. 한눈에 보는 시대 개관, 지엽적인 내용의 삭제, 최신 경향에 맞는 내용 추가 등 매년 수험생에게 조금이라도 한 단계 더 도움이 되고자 많은 부분을 업그레이드하였다.', '전한길 (지은이)', '메가스터디교육(공무원)', 'https://image.aladin.co.kr/product/34009/46/cover200/k562931866_1.jpg', '국내도서>수험서/자격증>공무원 수험서>7/9급 공무원>한국사>강의노트/기타', 1, 1);
