INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('당연하게도 나는 너를', '청소년 문학 최고의 페이지터너 이꽃님 작가의 청소년소설이다. 누군가를 막 좋아하기 시작한 십 대들의 풋풋한 마음과 그 마음 뒤에 숨겨진 쓰라리고 위태로운 감정이 미스터리한 사건과 맞물려 긴장감 넘치게 폭발한다.', '이꽃님 (지은이)', '우리학교', 'https://image.aladin.co.kr/product/31269/43/cover200/k872832466_1.jpg', '국내도서>청소년>청소년 문학>청소년 소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('급류', '2020년 《한경신춘문예》에 장편소설 &lt;GV 빌런 고태경&gt;이 당선되어 작품 활동을 시작한 소설가 정대건의 두 번째 장편소설 &lt;급류&gt;가 오늘의 젊은 작가 시리즈 40번으로 출간되었다. &lt;급류&gt;는 저수지와 계곡이 유명한 지방도시 ‘진평’을 배경으로, 열일곱 살 동갑내기인 ‘도담’과 ‘해솔’의 만남과 사랑을 그린 소설이다.', '정대건 (지은이)', '민음사', 'https://image.aladin.co.kr/product/30769/24/cover200/8937473402_1.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이후 한국소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('프로젝트 헤일메리', '데뷔작 《마션》과 후속작 《아르테미스》가 연달아 대성공을 거두며 뉴욕 타임스와 아마존 베스트셀러에 이름을 올린 명실상부 최고의 SF 작가, 앤디 위어의 신작. 지구를 구하기 위해서 정작 스스로는 지구로 돌아오지 못할 헤일메리호에 오른 ‘좋은 사람’인 주인공의 이야기가 펼쳐진다.', '앤디 위어 (지은이), 강동혁 (옮긴이)', '알에이치코리아(RHK)', 'https://image.aladin.co.kr/product/27045/43/cover200/8925588730_2.jpg', '국내도서>소설/시/희곡>과학소설(SF)>외국 과학소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('프레임 - 나를 바꾸는 심리학의 지혜', '2007년 출간 이래 대한민국 오피니언 리더의 필독 교양서로 불리며 30만 독자의 사랑을 받아온 ''프레임''의 10주년 개정증보판으로, 우리 사회에 ‘프레임’이라는 새로운 키워드를 던지고 개인과 세상의 변화를 주도한 ‘프레임’의 모든 것을 담은 결정판이다.', '최인철 (지은이)', '21세기북스', 'https://image.aladin.co.kr/product/26861/17/cover200/8950994518_2.jpg', '국내도서>자기계발>인간관계>교양심리학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('아주 작은 습관의 힘 - 최고의 변화는 어떻게 만들어지는가', '저자의 생생한 경험과 생물학, 뇌과학, 심리학의 최신 연구 결과를 집약해서 습관 하나로 인생을 변화시킬 수 있는 노하우를 제시한다. 어떤 습관을 자신의 무기로 만들기 위해서는 딱 네 가지만 기억하면 된다. 바로 ‘행동 변화의 4가지 법칙’인데, 모든 습관은 분명하고(제1법칙), 매력적이고(제2법칙), 쉽고(제3법칙), 만족스러워야(제4법칙) 한다는 것이다.', '제임스 클리어 (지은이), 이한이 (옮긴이)', '비즈니스북스', 'https://image.aladin.co.kr/product/18228/51/cover200/s982636214_1.jpg', '국내도서>자기계발>성공>성공학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('박태균의 이슈 한국사 - 둘만 모여도 의견이 갈리는 현대사 쟁점', '서울대 국제대학원 박태균 교수의 현대사 강의. 통사로서 한국현대사에 접근하지 않는다. 그 대신 한국현대사에서 꼭 짚고 넘어가야 할 10가지 이슈와 이와 관련해 꼭 알아야 할 역사적 사실을 일목요연하게 짚어준다.', '박태균 (지은이)', '창비', 'https://image.aladin.co.kr/product/6191/55/cover200/8936482750_1.jpg', '국내도서>역사>한국근현대사>한국전쟁 이후~현재', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('이반 일리치의 죽음', '''창비세계문학'' 7권. 똘스또이의 중단편 중에서 가장 훌륭한 작품이라는 평가를 받는 &lt;이반 일리치의 죽음&gt;은 삶에 대한 똘스또이의 생각과 문제의식이 잘 나타나 있다. 이 작품은 한 인간의 삶과 죽음을 냉철하게 관찰하고 분석하고 묘사했다.', '레프 니콜라예비치 톨스토이 (지은이), 이강은 (옮긴이)', '창비', 'https://image.aladin.co.kr/product/2013/23/cover200/8936464078_2.jpg', '국내도서>소설/시/희곡>러시아소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('초등 공부 정서보다 중요한 것은 없습니다 - 기분 상하지 않게 공부시키기 위한 부모의 대화법', '15년 차 현직 초등교사이자 EBS 공채 강사, 명실상부 대한민국 대표 초등 교육 멘토인 이서윤 작가의 『초등 공부 정서보다 중요한 것은 없습니다』가 출간되었다. 그는 이 책에서 부모의 양육 태도 및 대화법을 점검해 보아야 한다고 강조한다.', '이서윤 (지은이)', '카시오페아', 'https://image.aladin.co.kr/product/34529/79/cover200/k382932951_1.jpg', '국내도서>좋은부모>교육/학습>학교/학습법', 1, 1);
