INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('에도가와 란포 기담집', '본격 추리물뿐만 아니라 괴기·에로틱·그로테스크·잔학성이 강조된 작품들도 연이어 발표하며 수많은 히트작을 만들며 일본 미스터리 문학의 역사를 100년 앞당겼다는 평가를 받는 에도가와 란포. 그의 핏빛 기담은 100년이 지난 지금에도 여전히 당신에게 씻을 수 없는 공포를 가져다줄 것이다.', '에도가와 란포 (지은이), 김은희 (옮긴이)', '부커', 'https://image.aladin.co.kr/product/34371/3/cover200/k322932101_1.jpg', '국내도서>소설/시/희곡>추리/미스터리소설>일본 추리/미스터리소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('이런 진로는 처음이야 - 읽다 보면 저절로 쾌속 성장하는 자기 탐색 프로젝트', '진로 탐색은 단순히 생계를 위한 직업 선택을 넘어 삶의 방향을 정하는 중요한 과정이다. 그러나 오늘날 많은 청소년이 입시만을 위한 학업에 치여 진로 탐색 없이 어른이 된다. 그런 청소년들을 위해 서울대 진로교육 전문가 이찬 교수가 나섰다.', '이찬 (지은이)', '21세기북스', 'https://image.aladin.co.kr/product/34353/83/cover200/k942932099_1.jpg', '국내도서>청소년>청소년의 진로선택', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('부동산 계약 이렇게 쉬웠어?', '부동산 계약의 처음부터 끝까지, 투자자의 눈으로 일반인이 보지 못하는 포인트까지 모두 짚어냈고, 초보자가 쉽게 따라 할 수 있도록 관련 자료와 삽화까지 수록하였다. 누구든지 이 책을 읽는다면 더 이상 주변에 묻지 않더라도 스스로 부동산 계약 지식을 갖출 수 있을 것이다.', '송희창(송사무장) (지은이)', '지혜로', 'https://image.aladin.co.kr/product/34334/1/cover200/k742932791_1.jpg', '국내도서>경제경영>재테크/투자>부동산/경매', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('챔피언처럼 생각하고 거래하라 - 주식시장의 마법사가 지닌 비밀, 원칙 그리고 있는 그대로의 진실', '베스트셀러 저자 마크 미너비니의 대표작으로, 이 책은 저자 스스로도 연재물이 되어 버렸다고 말할 정도로 『초수익 성장주 투자』와 보완적 시리즈물 성격의 저서이다. 저자는 ‘그가 산 주식이 그가 원하는 대로 움직이지 않으면’ 어떻게 대응해야 할지 시나리오별 대응법을 마련해 놓고 있다.', '마크 미너비니 (지은이), 송미리 (옮긴이), 김대현 (감수)', '이레미디어', 'https://image.aladin.co.kr/product/34299/17/cover200/k762932371_1.jpg', '국내도서>경제경영>재테크/투자>재테크/투자 일반', 1, 1);
