INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2025 이동기 영어 신경향 All In One - 공무원 영어의 시작과 끝', '2025년 공무원 시험 출제기조 변화에 따라 바뀌는 영어 시험에 완벽 대비할 수 있도록 이동기 선생님의 노하우를 집약한 공무원 영어 기본서다. 2권 구성으로 1권은 문법.구문을, 2권은 어휘.생활영어.독해를 다루고 있다.', '이동기 (지은이)', '에스티유니타스', 'https://image.aladin.co.kr/product/33901/24/cover200/k572930921_1.jpg', '국내도서>수험서/자격증>공무원 수험서>7/9급 공무원>영어>7/9급 기본서', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('뿐이 토핑 이유식 - 최신 이유식 지침을 반영한 세상 쉽고 맛있는 레시피 253', '희야 작가는 첫째 튼이를 위해서 죽 이유식(밥솥 이유식)을, 이른둥이 뿐이를 위해서 토핑 이유식, 자기주도이유식까지 모두 만들어봤다. 또한 미국 소아과학회, WHO 자료, 해외 논문 등을 찾아보면서 변경된 이유식 지침에 대해서도 열심히 조사하고 공부했다. 그 모든 경험과 지식, 노하우를 이 책에 담았다.', '정주희 (지은이), 이수진 (감수)', '서사원', 'https://image.aladin.co.kr/product/33511/4/cover200/k742938457_1.jpg', '국내도서>요리/살림>이유식', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('아이는 무엇으로 자라는가 - 세계적 가족 심리학자 버지니아 사티어의 15가지 양육 법칙', '세계적 가족 심리학자, 가족치료의 1인자 버지니아 사티어의 역작이자 누적 부수 100만 부를 돌파한 책 『아이는 무엇으로 자라는가가 국내에서 출간되었다. 유수의 언론과 아동, 청소년 전문 교육자와 심리학자들이 극찬하는 이 책은 1988년에 첫 출간된 이후 전 세계 15개국에 번역 출간되었으며, 오랫동안 아마존 베스트셀러의 자리를 지켜온 전설적인 육아의 바이블로 통한다.', '버지니아 사티어 (지은이), 강유리 (옮긴이)', '포레스트북스', 'https://image.aladin.co.kr/product/33273/25/cover200/k302938773_2.jpg', '국내도서>좋은부모>부모교육', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('비스킷 - 제1회 위즈덤하우스 판타지문학상 청소년 부문 대상 수상작', '제1회 위즈덤하우스 판타지문학상 청소년 부문 대상 수상작. 『비스킷』은 자신을 지키는 힘을 잃어 보이지 않게 된 존재인 ‘비스킷’을 돕고자 하는 한 소년의 고군분투이자, 자존감을 지키기 위해 몸부림치는 청소년의 성장통 같은 작품이다.', '김선미 (지은이)', '위즈덤하우스', 'https://image.aladin.co.kr/product/32443/92/cover200/k002935195_1.jpg', '국내도서>청소년>청소년 문학>청소년 소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('영어 회화 핵심패턴 233 - 단어만 갈아 끼우면 회화가 튀어 나온다!', '2002년 출간 후 ‘20년 연속 베스트셀러’라는 기록을 남기며 패턴 영어의 절대 강자 자리를 지켜온 길벗이지톡의 《영어회화 핵심패턴 233 시리즈》가 최신 트렌드에 맞는 구성으로 개정 출간되었다.', '백선엽 (지은이)', '길벗이지톡', 'https://image.aladin.co.kr/product/30764/91/cover200/k242830553_1.jpg', '국내도서>외국어>영어회화>생활영어', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('돈의 뇌과학 - 뇌과학과 심리학으로 부를 끌어당기는 6가지 비밀', '돈을 못 모으게 만드는 신념은 무엇인지, 부자가 되는 가치관은 어떻게 다른지, 말버릇이 돈과의 관계에 어떤 영향을 주는지 등을 차근차근 살펴보며 뇌가 돈을 좋아하게 만든다. 또한, 인생 계획과 재무 목표를 어떻게 세워야 하는지, 투자할 때 주의할 점은 무엇인지 알려주며 돈 관리 능력을 한 단계 높여준다.', '우에하라 치카코 (지은이), 오정화 (옮긴이)', '동양북스(동양문고)', 'https://image.aladin.co.kr/product/34535/85/cover200/k592932957_1.jpg', '국내도서>경제경영>재테크/투자>재테크/투자 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('고블린 슬레이어 외전 : 이어 원 11 - SL Comic', '승급한 창잡이는 무기 상점에 의뢰하여 마법의 창을 획득한다. 고블린 슬레이어는 유적에 소굴을 만든 고블린을 죽이는 과정에서 평범한 검이 아닌 파랗게 불타는 마검을 든 고블린과 조우한다.', '사카에다 켄토 (지은이), 카규 쿠모 (원작), 아다치 신고, 칸나츠키 노보루 (그림), 김성래 (옮긴이)', '디앤씨미디어(주)(D&C미디어)', 'https://image.aladin.co.kr/product/34474/27/cover200/k212932632_1.jpg', '국내도서>만화>본격장르만화>판타지>액션 판타지', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('영어 잘하는 아이 이렇게 키웁니다 - 분당 영어교사 써니맘 트리샤의', '분당 현직 중학교 영어교사이자 엄마가 아이를 키우면서 적용한 엄마표 영어 노하우를 담았다. 엄마표 영어, 그 시작을 위한 모든 것을 담았다. 아울러 “영어유치원, 비싼 영어전집, 영어 거부…” 등 여러 이슈들에 대한 영어전문가의 솔직한 답변을 실었다.', '트리샤(양지현) (지은이)', '스마트북스', 'https://image.aladin.co.kr/product/34469/3/cover200/k212932531_2.jpg', '국내도서>좋은부모>교육/학습>언어/외국어 교육', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('작약은 물속에서 더 환한데', '문학동네시인선 217번으로 이승희 시인의 네번째 시집 『작약은 물속에서 더 환한데』를 펴낸다. 『여름이 나에게 시킨 일』(문예중앙, 2017) 이후 7년 만에 펴내는 이번 시집에서 시인은 어두운 곳을 들여다보기를 주저하지 않으며 슬픔과 죽음을 향해 한 걸음 더 나아간다.', '이승희 (지은이)', '문학동네', 'https://image.aladin.co.kr/product/34366/47/cover200/k442932003_1.jpg', '국내도서>소설/시/희곡>시>한국시', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('달님 송편 - 추석 그림책', '가장 밝고 커다란 보름달이 뜨는 추석 전날 밤, 사람들 몰래 야옹이들만의 축제가 열린다. 야옹이들은 이날을 위해 각자의 자리에서 열심히 꾹꾹이 실력을 갈고닦았다. 커다랗고 커다란 달님 반죽으로 송편을 만들려고 말이다.', '안영은 (지은이), 서영 (그림)', '키즈엠', 'https://image.aladin.co.kr/product/34330/73/cover200/k732932685_1.jpg', '국내도서>유아>그림책>나라별 그림책>한국 그림책', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('여름의 빛', '세계적 삽화가 장자크 상페가 선사하는 시원하고 찬란하게 빛나는 여름의 풍경. 프랑스를 대표하는 세계적 삽화가이자, ''순간을, 몸짓을, 태도를, 상황을 포착하는 하나의 선으로 전 세계에 널리 알려진 만화가''인 장자크 상페가 여름을 주제로 그린 그림을 엮었다.', '장자크 상페 (지은이), 양영란 (옮긴이)', '열린책들', 'https://image.aladin.co.kr/product/34273/53/cover200/8932924449_1.jpg', '국내도서>에세이>사진/그림 에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('역사를 보다', '구독자 195만, 누적 9억 2천만 뷰를 자랑하는 대한민국 대표 지식 채널 보다(BODA)의 초인기 시리즈 ‘역사를 보다’를 단행본으로 출간했다. 이집트·중동·유라시아를 오가며 알 듯 모를 듯한 역사적 궁금증을 속 시원히 풀어준다.', '박현도, 곽민수, 강인욱, 허준 (지은이)', '믹스커피', 'https://image.aladin.co.kr/product/34213/28/cover200/k582931937_1.jpg', '국내도서>역사>세계사 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('설민석의 한국사 대모험 29 - 강감찬 편 : 귀주 대첩', '고려 거란 전쟁의 과정과 귀주 대첩에서 활약한 강감찬의 이야기를 재미있는 만화로 담아냈다. 거란은 왜 고려에 세 번이나 침입했는지, 강감찬은 어떻게 거란군과 맞서 싸웠는지 설쌤과 함께하는 시간 여행을 통해 확인해 보자.', '설민석, 스토리박스 (지은이), 정현희 (그림), 강석화 (감수)', '단꿈아이', 'https://image.aladin.co.kr/product/34009/14/cover200/k542931865_1.jpg', '국내도서>어린이>초등 한국사', 1, 1);
