INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('노르웨이의 숲', '페이지를 처음 펼치는 오늘의 젊음들에게, 그리고 오랜 기억 속에 책의 한 구절을 간직하고 있는 어제의 젊음들에게, 한결같은 울림으로 예민하고 섬세한 청춘의 감성을 전하며 영원한 필독서로 사랑받고 있는 무라카미 하루키의 대표작이다.', '무라카미 하루키 (지은이), 양억관 (옮긴이)', '민음사', 'https://image.aladin.co.kr/product/11561/49/cover200/8937434482_1.jpg', '국내도서>소설/시/희곡>일본소설>1950년대 이후 일본소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('한국이 싫어서', '오늘의 젊은 작가 시리즈 7권. 사회 비판적 문제에서 SF까지 아우르는 다양한 소재, 흡인력 있는 스토리 전개, 날렵하고 군더더기 없는 문장. 일본 대중 문학의 기수 오쿠다 히데오에 비견되며 한국 문학에 새로운 활기를 불어 넣고 있는 작가 장강명의 장편소설.', '장강명 (지은이)', '민음사', 'https://image.aladin.co.kr/product/5971/37/cover200/8937473070_1.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이후 한국소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('꾸준히, 오래, 지치지 않고 - 일에 먹히지 않고 나를 지키는 마음의 태도에 대하여', '30년 넘게 환자들의 고민을 듣고 그 무게를 함께 나눠온 하지현 작가는 이러한 문제의식을 바탕으로 이 책에서 일하면서 상처 입은 많은 사람들을 위해 스스로를 지키며 일하는 마음의 태도에 대해 이야기한다. 그리고 정신과 의사이자 직장인, 작가로서 오랫동안 일해오면서 깨달은 자신의 경험과 통찰을 담아내고 있다.', '하지현 (지은이)', '마티스블루', 'https://image.aladin.co.kr/product/34455/39/cover200/k362932432_1.jpg', '국내도서>자기계발>성공>성공학', 1, 1);
