INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('경영, 이나모리 가즈오 원점을 말하다', '이나모리 가즈오 명예회장의 파란만장했던 경영 이력과 경영철학, 그리고 이 모든 것을 이룰 수 있었던 그의 경영 원칙을 완결한 책이다. 그의 생전에 기획되어 유지를 받든 이나모리 라이브러리가 완성한 책으로 1970년대부터 2010년대까지 수많은 강연에서 이나모리 경영론의 핵심을 발췌하였다.', '이나모리 가즈오 (지은이), 이나모리 라이브러리, 다이아몬드사 ''이나모리 가즈오 경영 강연 선집'' 공동팀 (엮은이), 양준호 (옮긴이)', '21세기북스', 'https://image.aladin.co.kr/product/34082/24/cover200/k552931088_3.jpg', '국내도서>경제경영>기업 경영>경영 일반', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2025 이유진 국어 독해 알고리즘 코어 - 전2권', '『2025 이유진 국어 독해 알고리즘 코어』는 『스키마』, 『코어』, 『딥러닝』으로 구성된 2025 이유진 국어 독해 알고리즘 시리즈의 중심이 되는 9·7급 공무원 국어 시험 대비 기본서이다.', '이유진 (지은이)', '메가스터디교육(공무원)', 'https://image.aladin.co.kr/product/33864/86/cover200/k522930509_1.jpg', '국내도서>수험서/자격증>공무원 수험서>7/9급 공무원>국어>7/9급 기본서 및 부교재', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('질병 해방 - 치매, 암, 당뇨, 심장병과 노화를 피하고 건강하게 오래 사는 법', '출간 1주 만에 13만 부, 12개월 만에 150만 부를 돌파하며 아마존과 《뉴욕타임스》 모두에서 종합 베스트셀러 1위에 오르는 전례가 드문 기록을 세운 화제작, 피터 아티아 박사의 《질병 해방》이 마침내 한국에 출간되었다. 《뉴욕타임스매거진》의 표현대로 “폭주하는(타의 추종을 불허하는) 베스트셀러”가 되어 지금도 아마존과 《뉴욕타임스》에서 1년 넘게 베스트셀러 행진을 이어가며 센세이션을 불러일으키고 있다.', '피터 아티아, 빌 기퍼드 (지은이), 이한음 (옮긴이)', '부키', 'https://image.aladin.co.kr/product/33821/13/cover200/k782930794_1.jpg', '국내도서>인문학>교양 인문학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2024 제15회 젊은작가상 수상작품집', '2010년 제정된 이래 해를 거듭하며 독자들의 뜨거운 사랑을 받고 있는 젊은작가상이 어느덧 15회를 맞았다. 저만의 문제의식과 치열한 언어로 문학의 지평을 넓혀온 데뷔 십 년 이하 작가들의 눈부신 발돋움을 조명하고자 마련된 젊은작가상은 지난해까지 모두 62명에 이르는 새로운 얼굴을 소개하며 한국문학에 생기를 더했다.', '김멜라, 공현진, 김기태, 김남숙, 김지연, 성해나, 전지영 (지은이)', '문학동네', 'https://image.aladin.co.kr/product/33711/13/cover200/8954638740_1.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이후 한국소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2024.2025 큰별쌤 최태성의 별별한국사 시대별 기출문제집 한국사능력검정시험 심화(1.2.3급)', '한국사능력검정시험 심화를 대비할 수 있는 기출문제집이다. 큰별쌤이 엄선한 기출문제 738문항을 시대순, 주제별로 구성하였고, 기출 선택지를 자연스럽게 익힐 수 있도록 기출 선택지만 따로 모아 문제를 만들었다.', '최태성 (지은이)', '이투스북', 'https://image.aladin.co.kr/product/33075/85/cover200/k242937404_1.jpg', '국내도서>수험서/자격증>한국사능력검정시험>심화(1,2,3급)', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('더 시스템 - 거의 모든 일에 실패하던 자가 결국 큰 성공을 이루어낸 방법, 개정판', '전 세계 65개국 2,000여개 신문사에 실린 세계에서 가장 유명한 만화 ‘딜버트’의 작가 스콧 애덤스. 그는 『더 시스템』을 통해 “성공하려면 열정을 좇으라”는 자기계발서의 진부한 메시지를 뒤엎는다. 무조건 열정을 좇으며 포기하지 않는다고 성공하는 것이 아니며, 오히려 성공이 열정을 불러온다고 반박한다.', '스콧 애덤스 (지은이), 김인수 (옮긴이)', '베리북', 'https://image.aladin.co.kr/product/33039/96/cover200/k232937683_1.jpg', '국내도서>자기계발>성공>성공학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('느리게 나이 드는 습관 - 노년내과 의사가 알려주는 감속노화 실천법', '서울아산병원 노년내과 전문의가 작심하고 전하는 감속노화 실천법. 생애 주기에 따라 생활의 요소를 조절해 노화 속도를 느리게 만들고 내재역량을 강화할 수 있는 구체적인 방법을 이야기한다. 누구나 아프지 않고 건강하게, 또래보다 느리게 나이 들 수 있다.', '정희원 (지은이)', '한빛라이프', 'https://image.aladin.co.kr/product/32941/79/cover200/k032936148_2.jpg', '국내도서>건강/취미>건강정보>건강에세이/건강정보', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('불편한 편의점 2 (단풍 에디션)', '출간 후 1년이 넘도록 독자의 사랑을 받으며 베스트셀러 상위권을 지키고 있는 소설, 김호연 작가의 『불편한 편의점』이 그 두 번째 이야기로 다시 찾아왔다. 청파동 골목의 작은 편의점을 무대로 힘겨운 시대를 살아가는 우리 이웃들의 삶을 따뜻하고 유쾌하게 그렸다.', '김호연 (지은이)', '나무옆의자', 'https://image.aladin.co.kr/product/30370/64/cover200/s122834213_1.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이후 한국소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('아버지의 해방일지', '김유정문학상 심훈문학대상 이효석문학상 등을 수상하며 문학성을 두루 입증받은 ‘리얼리스트’ 정지아가 무려 32년 만에 장편소설을 발표했다. 역사의 상흔과 가족의 사랑을 엮어낸 대작을 선보임으로써 선 굵은 서사에 목마른 독자들에게 한모금 청량음료 같은 해갈을 선사한다.', '정지아 (지은이)', '창비', 'https://image.aladin.co.kr/product/30048/51/cover200/8936438832_2.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이후 한국소설', 1, 1);
