INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('내가 틀릴 수도 있습니다 - 숲속의 현자가 전하는 마지막 인생 수업', '2022년 1월, 한 사람이 세상을 떠났다. 그러자 스웨덴 전역에 거대한 애도의 물결이 일었다. 비욘 나티코 린데블라드. 수많은 스웨덴인들을 불안에서 끌어내어 평화와 고요로 이끌었던 그는 2018년 루게릭병에 진단받은 후에도 유쾌하고 따뜻한 지혜를 전하며 살아갔다.', '비욘 나티코 린데블라드 (지은이), 토마스 산체스 (그림), 박미경 (옮긴이)', '다산초당', 'https://image.aladin.co.kr/product/29269/12/cover200/k822837807_3.jpg', '국내도서>인문학>인문 에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('밝은 밤', '공감을 불러일으키는 이야기와 서정적이며 사려 깊은 문장, 그리고 그 안에 자리한 뜨거운 문제의식으로 등단 이후 줄곧 폭넓은 독자의 지지와 문학적 조명을 두루 받고 있는 작가 최은영의 첫 장편소설.', '최은영 (지은이)', '문학동네', 'https://image.aladin.co.kr/product/27541/91/cover200/k312835925_1.jpg', '국내도서>소설/시/희곡>한국소설>2000년대 이후 한국소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('비실비실 선생님 1', '체력은 0, 목소리도 여리여리. 모든 스테이터스가 최약체인 ‘비실비실 선생님’ 히와무라 히요리. 모두들 그런 그녀를, 기분 상하게 하면 저주를 내리는 ‘무시무시 선생님’이라며 두려워하고 있지만, 학생들을 위해 열심히 노력하고 있다. 그런 선생님이 가냘프고 귀엽고 조금은 자극적인 러브코미디.', '후쿠치 카미오 (지은이)', '대원씨아이(만화)', 'https://image.aladin.co.kr/product/34526/47/cover200/k532932853_1.jpg', '국내도서>만화>순정만화>레이디스 코믹', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('책을 읽다가 잠이 들면 좋은 일이 일어남', '2009년 작품 활동을 시작해, 소설 『그럼 무얼 부르지』, 『겨울의 눈빛』, 『도시의 시간』, 『미래 산책 연습』, 『극동의 여자 친구들』 등을 출간하며 전위의 감각을 선사해온 작가 박솔뫼가 첫 에세이 『책을 읽다가 잠이 들면 좋은 일이 일어남』을 위즈덤하우스에서 출간했다.', '박솔뫼 (지은이)', '위즈덤하우스', 'https://image.aladin.co.kr/product/34390/22/cover200/k842932503_1.jpg', '국내도서>에세이>독서에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2025 심슨 전략서 + 암기 노트 세트 - 전2권 - 구문·문법·독해·생활영어 All in One 전략서', '『2025 심슨 전략서』는 28년 강의 경력의 심우철 선생님의 문제 풀이 전략을 압축하여 담아낸 압축 요약서로, 문제 풀이에 바로 적용 가능한 59개의 전략만을 엄선하여 연습 문제들과 함께 수록하였다', '심우철 (지은이)', '심슨북스', 'https://image.aladin.co.kr/product/34288/5/cover200/k252932171_1.jpg', '국내도서>수험서/자격증>공무원 수험서>7/9급 공무원>영어>단어/문법/독해 외', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('우리들의 재판을 시작하겠습니다', '우리 사회의 축소판인 아이들의 학교에서, 내 가족이나 친구, 어쩌면 내가 겪을지도 모를 부당한 일을 해결하기 위해 아이들이 직접 재판을 열고, 주어진 상황을 부단히 바로잡는 과정을 흥미진진하게 담고 있다.', '율리 체, 엘리사 호벤 (지은이), KATH(권민지) (그림), 배명자 (옮긴이)', '다산어린이', 'https://image.aladin.co.kr/product/34278/58/cover200/k012932269_1.jpg', '국내도서>어린이>동화/명작/고전>외국창작동화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('흔한남매 과학 탐험대 10 : 생태계', '《흔한남매 과학 탐험대》 시리즈는 과학을 처음 접하는 아이들이 과학의 주요 내용을 미리 맛볼 수 있는 초등 과학 입문서이다. 흔한남매가 최고의 과학 전문가들과 함께 우주, 지구와 달, 우리 몸, 정글 등을 여행하며 쉽고 재미있게 과학 지식을 배운다.', '흔한남매 (원작), 김언정, 이현진 (글), 이태영, 임혜영 (그림), 흔한컴퍼니 (감수), 정현철, 김희목, 권경아, 최진수 (기획)', '주니어김영사', 'https://image.aladin.co.kr/product/33970/53/cover200/8934905859_2.jpg', '국내도서>어린이>과학/수학/컴퓨터>지구와 우주', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2025 선재국어 예상 기출서 1', '‘학습 전략 - 대표 유형 문제 - 핵심 개념 - 예상 문제 풀이’의 4단계 학습 과정으로 구성되어 있다. 앞으로 어떻게 문제가 출제되고 무엇을 학습해야 되는지를 학습 전략과 대표 유형 문제를 통해서 파악할 수 있도록 하였다.', '이선재, 선재국어연구소 (지은이)', '에스티유니타스', 'https://image.aladin.co.kr/product/33900/94/cover200/k632930929_1.jpg', '국내도서>수험서/자격증>공무원 수험서>7/9급 공무원>국어>7/9급 기본서 및 부교재', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('꽃길이 따로 있나, 내 삶이 꽃인 것을 - 인생 후반을 따스하게 감싸줄 햇볕 같은 문장들 65', '출간 즉시 폭발적인 입소문을 통해 56주 연속 베스트셀러에 오른 책이 있다. 바로 오평선 작가의 에세이 《그대 늙어가는 것이 아니라 익어가는 것이다》이다. 후속작을 기다리는 독자들의 성원에 힘입어 봄날을 맞아 2년 만에 출간된 《꽃길이 따로 있나, 내 삶이 꽃인 것을》은 우리에게 더 깊어진 이야기를 전한다.', '오평선 (지은이)', '포레스트북스', 'https://image.aladin.co.kr/product/33489/7/cover200/k772938352_3.jpg', '국내도서>에세이>사진/그림 에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('열세 살의 걷기 클럽', '학교에 운동 클럽이 생기자, 열세 살 윤서는 ‘걷기 클럽’을 만들겠다고 고집한다. 그럴듯한 운동이 아닌 ‘걷기’ 클럽이면 아무도 가입하지 않을 테고, 그럼 클럽 활동 시간에 혼자 있을 수 있으니까! 그런데 뜻밖의 가입자들이 나타나, 윤서는 얼떨결에 클럽장이 된다.', '김혜정 (지은이), 김연제 (그림)', '사계절', 'https://image.aladin.co.kr/product/31577/41/cover200/k472833764_1.jpg', '국내도서>어린이>동화/명작/고전>국내창작동화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('28시간에 끝내는 토익스피킹 스타트 - 최신 개정', '2022년 6월 시험 변경 사항을 완벽히 반영하여 최신 출제 경향을 담은 유형별 문제 연습과 실전 문제 연습을 통해 단기간에 목표 점수 획득이 가능하다. 기초 문법을 모르는 영포자도 학습이 가능한 쉬운 교재로 저자가 제시하는 순서대로 따라만 해도 목표 점수 달성이 가능한 직관적인 답변 템플릿을 제공한다.', '황인기(제이크), 시원스쿨어학연구소 (지은이)', '시원스쿨LAB', 'https://image.aladin.co.kr/product/29530/42/cover200/k372837540_3.jpg', '국내도서>외국어>토익>토익 Speaking', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('글래스 호텔', '2008년 전 세계 금융계와 미국 사회를 충격에 빠뜨렸던 역사상 최대 폰지사기 사건을 다룬 소설이다. 소설 속 조너선 알카이티스의 실제 모델은 버나드 메이도프로, 1970년대부터 30여 년간 신규 투자자의 투자금으로 기존 투자자의 수익금을 지급하는 방식의 폰지사기를 벌여온 인물이다.', '에밀리 세인트존 맨델 (지은이), 김미정 (옮긴이)', '북로드', 'https://image.aladin.co.kr/product/29498/51/cover200/k342837045_1.jpg', '국내도서>소설/시/희곡>세계의 소설>기타 국가 소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('소년과 두더지와 여우와 말', '글과 그림의 조화가 아름다운 그림책이기도 이야기책이기도 하다. 밀레니얼들을 위한 삶의 지침을 담고 있는 철학책으로 분류되기도 한다. 그러나 무엇보다 삶에 대한 깊은 성찰을 아름다운 그림과 진실한 글로 드러내는, 모든 연령대의 독자를 아우르는 책이다.', '찰리 맥커시 (지은이), 이진경 (옮긴이)', '상상의힘', 'https://image.aladin.co.kr/product/23682/64/cover200/8997381679_2.jpg', '국내도서>에세이>사진/그림 에세이', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('괴테와의 대화 2', '젊은 문학도 에커만이 인생, 예술, 학문 그리고 사랑에 대해 괴테와 나눈 대화. 에커만이 괴테가 세상을 떠날 때까지 10년간 약 1000번의 만남을 가지며 그와 나눈 대화를 꼼꼼하게 기록, 정리한 것이다. 에커만에게 멘토와도 같았던 괴테가 젊은 이들에게 전하는 주옥같은 메시지들이 들어있다.', '요한 페터 에커만 (지은이), 장희창 (옮긴이)', '민음사', 'https://image.aladin.co.kr/product/214/60/cover200/8937461773_2.jpg', '국내도서>소설/시/희곡>문학의 이해>작가론', 1, 1);
