INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('귀매', '2002년 출간되었던 한국형 오컬트 장편소설 『귀매』가 문학동네 플레이 시리즈로 개정 출간되었다. 한국의 전통 신앙인 무속을 대대적으로 소설화한 장편 『귀매』는 ‘K-오컬트’에 대한 관심이 급부상한 바로 지금 독자들의 욕구를 완벽히 충족시켜줄 단 한 권의 소설이다.', '유은지 (지은이)', '문학동네', 'https://image.aladin.co.kr/product/34390/43/cover200/k922932505_1.jpg', '국내도서>소설/시/희곡>호러.공포소설>한국 호러.공포소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('시간 고양이 5 - 불타는 아마존의 반격', '아마존으로 환경 캠프를 떠난 서림과 은실이의 모험을 담았다. 아마존에서 서식하는 실제 동물과 식물, 아마존에 관한 지식, 한여름에 어울리는 으스스한 실종 이야기에 이소연 작가의 생생한 삽화가 더해져 풍성한 볼거리를 자랑한다.', '박미연 (지은이), 이소연 (그림)', '이지북', 'https://image.aladin.co.kr/product/34361/50/cover200/k432932903_1.jpg', '국내도서>어린이>동화/명작/고전>국내창작동화', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('우치다 햣켄 기담집 - 공포와 전율의 열다섯 가지 이야기', '우치다 햣켄의 공포문학 단편 걸작선이 나왔다. 나쓰메 소세키의 제자이자 아쿠타가와 류노스케의 문학 동료인 햣켄은 분위기 공포문학의 1인자로 평가받으며 미시마 유키오 등의 극찬을 받았지만 그의 작품이 국내에 소개되는 것은 처음이다.', '우치다 햣켄 (지은이), 김소운 (옮긴이)', '글항아리', 'https://image.aladin.co.kr/product/34350/29/cover200/k592932991_2.jpg', '국내도서>소설/시/희곡>호러.공포소설>외국 호러.공포소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('세상 멋져 보이는 것들의 사회학 - 그럼에도 불구하고 던져야 할 질문', '“편리한데 불편하고, 쾌적한데 불쾌하고, 효율적인데 위험천만하다!” 피임약부터 수세식 변기, 아파트, 에어컨, 플랫폼 노동, 비행기까지. 혁신의 끝판왕들이 펼치는 아찔한 사회사.
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('네가 되어 줄게', '예민한 눈으로 개인과 사회를 응시해 온 작가 조남주. 그가 『귤의 맛』에 이은 두 번째 청소년소설 『네가 되어 줄게』를 출간했다. 4년 만이다. 이번 작품에선 ‘별스러운 일’이 몰고 온 엄마와 딸의 인생 2악장 조바꿈을 그렸다.', '조남주 (지은이)', '문학동네', 'https://image.aladin.co.kr/product/34125/29/cover200/k192931192_1.jpg', '국내도서>청소년>청소년 문학>청소년 소설', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('건너가는 자 - 익숙함에서 탁월함으로 얽매임에서 벗어남으로', '“익숙한 이곳에 머물러 있는가, 새로운 저곳으로 건너려 하는가?” 격변의 시대에 반야심경이 던지는 ‘인간다운 삶’이라는 화두. 철학자 최진석과 함께 읽는 삶의 지침서로서의 반야심경 인류의 고전에서 길어 올린 지혜의 정수.', '최진석 (지은이)', '쌤앤파커스', 'https://image.aladin.co.kr/product/33877/45/cover200/k192930604_1.jpg', '국내도서>인문학>교양 인문학', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('2025 심슨 보카 - 심우철 공무원 영어 기본서 시리즈', '2025년도부터 전면 개편되는 9급 공무원 영어 시험 출제 기조를 반영한 공무원 영어 어휘집이다. 실제 업무 수행에 필요한 실용적인 영어 능력을 검증하겠다는 인사혁신처의 발표에 따라 새로운 시험에 적합한 필수 어휘 및 실무 핵심 어휘를 엄선하였다.', '심우철 (지은이)', '심슨북스', 'https://image.aladin.co.kr/product/33777/81/cover200/k642930274_1.jpg', '국내도서>수험서/자격증>공무원 수험서>7/9급 공무원>영어>단어/문법/독해 외', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('착! 붙는 일본어 독학 첫걸음 - 히라가나부터 JLPT까지! 입에 착! 시험에 착!', '시사일본어학원의 회화 전문 강사들이 집필과 감수했다. N5~4레벨에서 익혀야 할 대부분의 문법과 문형을 적용하였고, 실생활에서 많이 쓰는 표현 위주로 예문을 제시하였다. 한국인, 일본인 강사의 인강으로, 문법 설명과 원어민 발음을 동시에 충족시켜 준다.', '일본어 공부기술연구소 (지은이)', '시사일본어사', 'https://image.aladin.co.kr/product/33605/56/cover200/8940293983_1.jpg', '국내도서>외국어>일본어>일본어 첫걸음', 1, 1);
INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('부의 인사이트 - 청담캔디언니가 들려주는 일과 사업, 인생에 관한 77가지 조언', '청담캔디언니가 평소 20대 아들에게 들려주었던 부와 성공에 관한 인사이트를 모은 책이다. 압구정동과 청담동에서 30여 년 살면서 가장 가까이에서 만나온 슈퍼리치들이 어떻게 부를 만들었는지, 무역에서 에어비앤비까지 다양한 사업에 도전하며 실전으로 익힌 성공 노하우와 끊임없이 도전할 수 있었던 최강의 마인드셋까지. 자본주의 사회에서 살아남는 생존 교육과 같은 가르침이다.', '함서경 (지은이)', '생각지도', 'https://image.aladin.co.kr/product/33492/74/cover200/k502938356_1.jpg', '국내도서>자기계발>성공>성공학', 1, 1);
