INSERT INTO book_tb (title, description, author, publisher, cover, category, total_stock, current_stock) VALUES ('책 읽고 글쓰기 - 서울대 나민애 교수의 몹시 친절한 서평 가이드', '서울대학교 기초교양 최고 인기 강의인 나민애 교수의 ‘서평 특강’이 책으로 출간되었다. 짧은 시간에 서평 쓰기의 틀을 익히고, 어렵지 않게 서평 쓰기에 도전하는, 글쓰기의 ‘기초 체력’을 키우는 것이 이 책의 확실한 역할이자 차별화된 포인트다.', '나민애 (지은이)', '서울문화사', 'https://image.aladin.co.kr/product/23596/29/cover200/k472638421_1.jpg', '국내도서>인문학>책읽기/글쓰기>글쓰기', 1, 1);
